  List<InfoCardModel> _genelKulturInfoCard = [
    InfoCardModel("Tarih-Osmanlı Devleti Siyasi Dönemler", [
      InfoCardItem("tarih_Info_1","","-Kuruluş Dönemi(1299-1453)\n-Yükselme Dönemi(1453-1579)\n-Duraklama Dönemi(1579-1699)\n-Gerileme Dönemi(1699-1792)\n-Dağılma Dönemi(1792-1918)",),
      InfoCardItem("tarih_Info_2","Kuruluş Dönemi","1453 İstanbul'un fethine kadar olan dönemdir.",),
      InfoCardItem("tarih_Info_3","Yükselme Dönemi","Sokullu Mehmet Paşa'nın ölümüne kadarki dönemdir.",),
      InfoCardItem("tarih_Info_4","Duraklama Dönemi","-Karlofça Antlaşmasına kadarki dönemdir.\n\nDuraklamanın Nedenleri\n-Merkezi yönetimin bozulması\n-Ordu ve donanmanın bozulması\n-Eğitimin bozulması\n-Maliyenin bozulması",),
      InfoCardItem("tarih_Info_5","Merkezi Yönetimin Bozulma Nedenleri","-Şehzadelerin Devlet başına geçmesinde sancak sistemi kullanılması.\n-Önemli makamlara gelecek kişilerin rüşvet ve iltimasla getirilmesi.\n-Kadınların yönetimde etkin olması",),
      InfoCardItem("tarih_Info_6","Ordu ve Donanmanın Bozulması Nedenleri","-Kapıkulu Ocaklarına askerin çok alınması.\n-Tımar sisteminin bozulması.\n-Yeniçerilerin askerlik dışındaki işlerle uğraşmaları.\n-Harp alanında Avrupa'yı takip edememesi",),
      InfoCardItem("tarih_Info_7","Eğitim Sisteminin Bozulması Nedenleri","-Beşik ulemalığı sisteminin ugulanması.\n-Medreselerden pozitif bilimin kaldırılması.\n-Medrese eğitimi görmeyenlerin iltimasla atanması\n-Medreselerin, Avrupa'daki yenilikleri takip edememesi",),
      InfoCardItem("tarih_Info_8","Maliyenin Bozulması Nedenleri","-Cülus bahşişinin arttırılması.\n-Vergilerin artmasıyla köylülerin üretimi bırakması\n-Savaşların uzaması\n-Gümrüklerden elde edilen gelirlerin azalması",),
      InfoCardItem("tarih_Info_9","Duraklamanın Dış Nedenleri","-Avrupadaki gelişmelerin takip edilememesi\n-Devletin doğal sınırlara ulaşması\n-Güçlü devletlerle sınır olunması",),

    ]),InfoCardModel("Tarih-Osmanlı Devleti Gerilme Dönemi", [
      InfoCardItem("tarih_Info_10","Siyasi Olaylar","-Osmanlı-Rus ilişkileri(Prut Antlaşması 1711)\n-Osmanlı Venedik Avusturya ilişkileri(Pasarofça Antlaşması 1718)\n-Osmanlı Rusya Avusturya Savaşları(Belgrad Antlaşması 1739)\n-Osmanlı Rus Avusturya Savaşları(Ziştovi-Yaş Antlaşması)\n-Osmanlı Fransa ilişkileri",),
      InfoCardItem("tarih_Info_11","Lale Devri-1","-1718 Pasarofça Antlaşması ile başlandı.\n-1730 Patrona Halil isyanı ile sona erdi.\n-Zevk, sefa dönemi\n-Padişah II. Ahmet\n-Sadrazam Damat İbrahim Paşa\n-Avrupada ilk kez geçici elçilikler kuruldu.",),
      InfoCardItem("tarih_Info_12","Lale Devri-2","-İbrahim Müreferrika ve Sait Efendi ile Türk Matbaasını kurdu.\n-İtfaiye örgütü(Tulumbacılar) kuruldu.\n-Çiçek aşısı geliştirildi.\n-Doğu Klasiklerinde eserler Türkçeye çevrildi.\n-Kağıt, çini fabrikası açıldı.\n-Askeri alanda yenilikler yapılmadı.",),
      InfoCardItem("tarih_Info_13","I. Mahmut","-1730-1754 tarihleri arasındadır.\n-Humbaracı Ocağı Batı tarzında teşkilatlandırılmıştır.\n-Humbaracı Ahmet Paşa Topçu Ocağını modernleştirdi.\n-Subay Yetiştirmek için mühendishane kurulmuştur.\n-Yabancı subaylarla ordu modernize edilmeye çalışıldı.",),
      InfoCardItem("tarih_Info_14","III. Mahmut","1756-1774 tarihleri arasını kapsar.\n-Maliye alanında ıslahatlar yapıldı.\n-Sürat Topçu Ocağı kuruldu.\n-Deniz Mühendishanesi kuruldu.\n-İç borçlanmaya gidildi.",),
      InfoCardItem("tarih_Info_15","I. Abdülhamit","-1774-1789 tarihleri arasını kapsar.\n-Sürat topçuları geliştirilerek sayısı arttırıldı.\n-Yeniçeri sayımı yapıldı.\n-Lağımcı ve Humbaracı ocakları geliştirildi.",),
      InfoCardItem("tarih_Info_16","III. Selim","-1789-1807 tarihleri arasını kapsar.\n-İlk kez köklü değişiklikler yapıldı.\n-Nizam-ı Cedit ordusu kuruldu.\n-İrado Cedit hazinesi oluşturuldu.\n-Kara Mühendishanesi kuruldu.\n-Avrapada daimi elçilikler açılmıştır.\n-Devlet Matbaası ilk kez kuruldu.\n-Kabakçı Mustafa Paşa isyanı ile tahttan indirildi.",),
      InfoCardItem("tarih_Info_17","Islahatların Genel Özellikleri-1","-Askeri alanda yapılmıştır.\n-Halk isteksizdir.\n-Padişah ve devlet adamları gerçekleştirmiştir.\n-Yüzeysel çözümler üretilmiştir.\n-İstenilen sonuca ulaşılmamıştır.\n-Batıdaki gelişmelerden faydalanılmamıştır.",),
      InfoCardItem("tarih_Info_18","Islahatların Faydalı Olmama Nedenleri","-Padişahların sık değişimi\n-Yenilik karşıtı yeniçeriler\n-Ulemanın tepkisi",),
    ]), InfoCardModel("Tarih-Dağılma Dönemi", [
      InfoCardItem("tarih_Info_19","II. Mahmut Dönemi-1","-1808-1839 tarihleri arasındadır.\n-Ayanlar ile II. Mahmut arasında Sened-i İttifak imzalandı.\n-Sekban-ı Cedid kuruldu.\n-Eşkinci Ocakları kuruldı.\n-Yeniçeri ocağı kaldırıldı(Vaka-i Hayriye)\n-Asakir-i Mansure-i Muhammediye ordusu kuruldu.",),
      InfoCardItem("tarih_Info_20","II. Mahmut Dönemi-2","-Takvim-i Vekayi adlı ilk resmi gazete çıkarıldı.\n-Tımar sistemi kaldırıldı.\n-Devlet memurları maaşa bağlandı.\n-Köy ve mahalle muhtarlıkları oluşturuldu.\n-Posta ve karantina hizmetleri açıldı.\n-Pasaport şartı getirildi.",),
      InfoCardItem("tarih_Info_21","II. Mahmut Dönemi-3","-İlköğretim İstanbul'da zorunlu hale getirildi.\n-Orta dereceli okullar açıldı.\n-Avrupa'ya ilk defa öğrenci gönderildi.\n-Divan kaldırıldı, Nazırlar(Bakanlıklar) kuruldu.",),
      InfoCardItem("tarih_Info_22","II. Mahmut Dönemi-4","-Kendi resmini devlet dairelerine astırdı.\n-Meclis-i Vala-yı Ahkam-ı Adliyye kuruldu.\n-Dar-ı Şura-yı Babıali(Danıştay) kuruldu.\n-Din ve mezhep özgürlüğü getirildi.",),
      InfoCardItem("tarih_Info_23","Sened-i İttifak-1","-1808 tarihinde imzalanmıştır.\n-Alemdar Mustafa Paşa hazırladı.\n-Ayanlar ve II. Mahmut imzaladı.",),
      InfoCardItem("tarih_Info_24","Sened-i İttifak-2","\nBuna göre;\n\n-Ayanlar padişahın emirlerine uyacak.\n-Ayanlar bulunduğu yerlerde vergi ve asker toplayacak.\n-Buna karşın Ayanlar devlet otoritesini tanıyarak yapılacak yeniliklere destek olacak. \n\n-Bununla birlikte padişahın sınırları ilk kez sınırlandırıldı.",),
