    InfoCardModel("Öğrenciyi Tanıma Teknikleri (Test Dışı Teknikler)", [
      InfoCardItem("eo_Info_220","A) Kendini Anlatma Teknikleri","Birey kendisi ile ilgili bilgileri doğrudan verir.",),
      InfoCardItem("eo_Info_221","B) Görüşme","Belli bir amaç için yüzyüze yapılan etkileşimdir.",),
      InfoCardItem("eo_Info_222","C) Anket","Bir konu hakkında görüş toplamak için oluşturulan soru formlarıdır.",),
      InfoCardItem("eo_Info_223","D) Problem Tarama Listeleri","Öğrencilerin sorunlarını tespit edip, çözmeyi amaçlayan bilgi toplama araçlarıdır.",),
      InfoCardItem("eo_Info_224","E) Otobiyografi","Öğrencilerin özgeçmişlerini rapor eder.",),
      InfoCardItem("eo_Info_225","F) İstek Listeleri","Öğrencilerin isteklerini belirlemek için sorulan sorulardır.",),
      InfoCardItem("eo_Info_226","G) Öğrenciyi Tanıma Formu","Öğrenciyi tanıma amacıyla kullanılan formlardır.",),
      InfoCardItem("eo_Info_227","H) Gözlemsel Teknikler","Gözlem, derecelendirme ölçekleri ve anekdot",),
      InfoCardItem("eo_Info_228","I) Gözlem","Doğal ortamlarında öğrencilerin gözlenme sürecidir.",),
      InfoCardItem("eo_Info_229","J) Anektod","Gözlem sonuçlarının kaydedildiği formlardır.",),
      InfoCardItem("eo_Info_230","J) Derecelendirme Ölçekleri","Öğrencilerin davranışlarının gözlem yapan kişi tarafından bir forma göre işaretlendiği ölçeklerdir.",),
