import '../../../model/info_card_model.dart';

class DevletMemurlariKanunuInfoViewModel {
  List<InfoCardModel> _devletMemurlariKanunuInfoCardList = [
    InfoCardModel("Devlet Memurları Kanunu-1", [
  InfoCardItem("657_Info_1","Kapsadıkları", "657 sayılı Devlet Memurları Kanunu,\n-Genel ve Katma Bütçeli Kurumlar,\n-İl Özel İdareleri,\n-Belediyeler,\n-İl Özel İdareleri ve Belediyelerin kurdukları birlikler ile bunlara bağlı döner sermayeli kuruluşlar,\n-Kanunlarla kurulan fonlarda,\n-Kefalet sandıklarında veya Beden Terbiyesi Bölge Müdürlüklerinde çalışan memurlarda uygulanır.(Madde 1)"),
  InfoCardItem("657_Info_2","Kapsamadıkları-1", "-Anayasa Mahkemesi üye ve yedek üyeleri ile raportörleri,\n-Hakimlik ve savcılık mesleklerinde veya bu mesleklerden sayılan görevlerde bulunanlar,\n-Danıştay ve Sayıştay meslek mansupları ve Sayıştay savcı ve yardımcıları,\n-Üniversitelerin öğretim üye ve yardımcıları,\n-Cumhurbaşkanlığı Senfoni Orkestrası üyeleri(Madde 1)"),
  InfoCardItem("657_Info_3","Kapsamadıkları-2", "-Genelkurmay Mehtaran Bölüğü Sanatkarları,\n-Devlet Tiyatrosu ile Devlet Opera va Balesi ve Belediye Opera ve tiyatroları ile şehir ve belediye konservatuvar ve orkestralarının sanatkar memurları,\n-Uzman memurları,\n-Spor-Toto Teşkilatında çalışan personel,\n-Subay, astsubay, uzman jandarma, uzman erbaş ve sözleşmeli erbaş ve erler ile Emniyet Teşkilatı çalışanlarını kapsamaz.(Madde 1)"),
  InfoCardItem("657_Info_4","657'nin Amacı", "-Devlet memurlarının hizmet şartlarını, niteliklerini, atanma ve yetiştirilmelerini, ilerleme ve yükselmelerini, ödev, hak, yüküm ve sorumluluklarını, aylıklarını ve ödeneklerini ve diğer özlük işlerini düzenlemektir.(Madde 2)"),
  InfoCardItem("657_Info_5","657'nin Temel İlkeleri", "-Sınıflandırma,\n-Kariyer,\n-Liyakat'tır.(Madde 3)"),
  InfoCardItem("657_Info_6","Sınıflandırma", "Devlet memurlarını görevlerinin gerektirdiği niteliklere ve mesleklere göre sınıflara ayırmadır.(Madde 3)"),
  InfoCardItem("657_Info_7","Kariyer", "Bulundukları sınıflar içerisinde en yüksek derecelere kadar yükselme imkanı verilmesidir.(Madde 3)"),
  InfoCardItem("657_Info_8","Liyakat", "Devlet memurlarını sınıflandırma ve kariyerde liyakat sistemine dayandırmak ve Devlet memurlarını güvenliğe sahip kılmaktır.(Madde 3)"),
  InfoCardItem("657_Info_9","İstihdam Şekilleri", "-Memurlar,\n-Sözleşmeli Personel,\n-Geçici Personel,\n-İşçiler(Madde 4)"),
  InfoCardItem("657_Info_10","Memurlar", "Kamu hizmetlerini sürekli ifa ile görevlendirilenler memur sayılırlar. Ayrıca genel a genel politika tespiti, araştırma, planlama, programlama, yönetim ve denetim gibi işlerde görevli ve yetkili olanlar da memur sayılır.(Madde 4)"),
  InfoCardItem("657_Info_11","Sözleşmeli Personel-1", "Mali yılla sınırlı sözleşmeli olarak çalıştırılan, kalkınma planı, yıllık program ve iş programlarında yer alan önemli projelerin hazırlanması, gerçekleştirilmesi, işletilmesi ve işlerliği için şart olan, zaruri ve istisnai hallerde özel bir meslek bilgisine ve ihtisasına ihtiyaç gösteren geçici işlerde kullanılacak, Cumhurbaşkanınca belirlenen esaslarla alınan işçi olmayan personellerdir.(Madde 4)"),
  InfoCardItem("657_Info_12","Sözleşmeli Personel-2", "Alınırken yapılacak sınavlar, izinleri, alacakları üst sınır ücret, iş sonu tazminat miktarı, ünvan ve nitelikleri, ceza, sözleşme fesih ve posizyon iptalleri Cumhurbaşkanı tarafından belirlenir. Sözleşmeyi tek taraflı fes ederlerse 1 yıl boyunca tekrar alınmazlar.(Madde 4)"),
  InfoCardItem("657_Info_13","İşçiler", "Mevsimlik işçi, kampanya işçisi, orman yangını ile mücadele personeli, memur, sözleşmeli personel ve geçici işçi dışında kalan personellerdir.(Madde 4)"),
  InfoCardItem("657_Info_14","", "Memurlar, Sözleşmeli personel, geçici personel ve işçi dışında  istihdam şekli yoktur.(Madde 5)"),
]),InfoCardModel("Devlet Memurları Kanunu-2", [
  InfoCardItem("657_Info_15","Ödev ve Sorumluluklar", "-Sadakat,\n-Tarafsızlık ve devlete bağlılık,\n-Davranış ve işbirliği,\n-Yurt Dışında Davranış,\n-Amirlerin Görevleri,\n-Devlet Memurlarının Görevleri,\n-Kişisel sorumluluk ve zarar,\n-Kişilerin uğradıkları zarar,\n-Mal Bildirimi,\n-Basına bilgi veya demeç verme,\n-Malzemelerin kullanımı"),
  InfoCardItem("657_Info_16","Sadakat", "'Asli Devlet Memurluğuna' atandıktan en geç 1 ay sonra yasalara bağlılık yemini edilir ve Yemin Belgesi özlük dosyasına konur.(Madde 6)"),
  InfoCardItem("657_Info_17","Tarafsızlık ve Devlete Bağlılık", "-Siyasi partiye üye olamazlar ve Devlet aleyhinde çalışan dernek ve gruplaşmaya katılamazlar. Görevlerini yaparken din, dil, ırk, cinsiyet ve siyasi düşünce gibi ayrım yapamazlar.(Madde 7)"),
  InfoCardItem("657_Info_18","Davranış ve İşbirliği", "Hizmet içinde ve dışında Devlet memurluğu görevine layık olduklarını davranışlarıyla göstermek zorundadır. İşbirliği içinde çalışması esastır.(Madde 8)"),
  InfoCardItem("657_Info_19","Yurt Dışında Davranış", "Yurt dışında görevli memurlar Devlet itibarını ve görev haysiyetini zedeleyemez.(Madde 9)"),
  InfoCardItem("657_Info_20","Amirlerin Görevleri", "-Görevleri eksiksiz yapmaktan ve yaptırmaktan sorumludurlar.\n-Maiyetindeki memurları yetiştirmekten, hal ve hareketlerini  takip ve kontrol etmekten sorumludurlar.\n-Adil davranırlar ve kanunlara, kararnamelere ve mevzuatlara bağlı kalırlar.\n-Hediye kabul edemez ve borç alamaz.(Madde 10)"),
  InfoCardItem("657_Info_21","Devlet Memurlarının Görevleri-1", "-Kanun ve mevzuattaki esaslara uyarlar.\n-Amirlerinin verdiği görevleri yerine getirirler.\n-Emir kanunlara, kararnamelere ve yönetmeliklere aykırı ise amire bildirilir. Yazılı olarak emir tekrarlanırsa yapılır ve sorumluluk emri verene aittir.(Madde 11)"),
  InfoCardItem("657_Info_22","Devlet Memurlarının Görevleri-2", "-Konusu suç teşkil eden emir hiçbir suretle yerine getirilemez; yerine getiren kimse sorumluluktan kurtulamaz.\n-Acele hallerde kamu düzeninin ve kamu güvenliğinin kornması için kanunla gösterilen istisnalar uygulanır.(Madde 11)"),
  InfoCardItem("657_Info_23","Kişisel Sorumluluk ve Zarar", "-Kasıt, kusur, ihmal veya tedbirsizlik ile zarara sebep olunmuşsa memur rayiç bedel üzerinden zararı öder.\n-Olay tarihinde en alt derecenin birinci kademesinde bulunan memurun brüt aylığının yarısını geçmeyen zararlar, kabul etmesi halinde disiplin amiri veya yetkili disiplin kurulu kararına göre ilgili memurca ödenir.(Madde 12)"),
  InfoCardItem("657_Info_24","Kişilerin Uğradıkları Zarar", "-Kişi zarara uğrarsa dava personel aleyhine değil kurum aleyhine açılır.\n-Devlet dairesine ait para şahıs tarafından zimmete geçirildiyse Hazine parayı hemen öder.\n-(Madde 13)"),
  InfoCardItem("657_Info_25","Mal Bildirimi", "-kendileriyle, eşlerine ve velayetleri altındaki çocuklarına ait taşınır ve taşınmaz malları, alacak ve borçları hakkında, özel kanunda yazılı hükümler uyarınca, mal bildirimi verirler.(Madde 14)"),
  InfoCardItem("657_Info_26","Basına Bilgi veya Demeç Verme", "-Kamu görevleri hakkında basında demeç verilmez.\n-Gerekli bilgi valiler ya da görevlendirecekleri tarafından verilebilir.\n-Askeri bilgiler Cumhurbaşkanlığı kararnamesi ve kanunların yetkilendirdiği kişiler harici verilemez.(Madde 15)"),
  InfoCardItem("657_Info_27","Malzemelerin kullanımı", "-Resmi araç-gereç ve belgeler yetki verilen mahaller dışına çıkartılamaz, kişisel işlerde kullanılamaz.\n-Görev icabı teslim edilen araç-gereçler görev sonunda iadesi gerekir. Bu durum mirasçılarına da şamildir.(Madde 16)"),
]),

    InfoCardModel("Devlet Memurları Kanunu-3", [
  InfoCardItem("657_Info_28","Genel Haklar", "-Uygulamayı isteme hakkı,\n-Güvenlik,\n-Emeklilik,\n-Çekilme,\n-Müracaat, şikayet ve dava açma,\n-Sendika kurma,\n-İzin,\n-Kovuşturma ve yargılama,\n-İftiralar karşı korunma"),
  InfoCardItem("657_Info_28","Uygulamayı İsteme Hakkı", "Memurun yürürlükte olan hükümlerin kendisi hakkında uygulanmasını istemesidir.(Madde 17)"),
  InfoCardItem("657_Info_30","Güvenlik", "Kanunda yazılı haller dışında memurluğa son verilemez, aylık ve başka hakları elinden alınamaz.(Madde 18)"),
  InfoCardItem("657_Info_31","Emeklilik", "Yazılı şartlar içinde emeklilik hakkı vardır.(Madde 19)"),
  InfoCardItem("657_Info_32","Çekilme", "Kanunda verilen esaslara göre memurluktan çekilinebilir.(Madde 20)"),
  InfoCardItem("657_Info_33","Müracaat, Şikayet ve Dava Açma", "-Memurların şahsi işleri için müracaat, amir veya kurum tarafından uygulanan idari işlerden dolayı şikayet etme ve dava açma hakları vardır.\n-Müracaat veya şikayet sözlü ya da yazılı yapılabilir.\n-En yakın amirden başlanarak silsile ile yapılır.\n-Şikayetlerde şikayet edilen amir atlanır.(Madde 21)"),
  InfoCardItem("657_Info_34","Sendika Kurma", "Anayasa ve özel kanunlara göre sendika ve üst kuruluşlar kurabilir ve üye olabilirler.(Madde 22)"),
  InfoCardItem("657_Info_35","İzin", "Kanunda verilen sürelere göre izin hakları vardır.(Madde 23)"),
  InfoCardItem("657_Info_36","Kovuşturma ve Yargılama", "Görevleri ile ilgili ya da görev sırasında işlenen suçlarla ilgili kovuşturma yapılması ve dava açılması özel hükümlere tabidir.(Madde 24)"),
  InfoCardItem("657_Info_37","İftiralara Karşı Korunma", "Memur hakkında yapılan ihbar ve şikayetlerin asılsız olduğu kanuni işlemle tespit edilirse, merkezde memurun en büyük amiri, illerde valiler isnatta bulunanlar hakkında kamu davası açılmasını Cumhuriyet Savcılığından isterler.(Madde 25)"),
]),InfoCardModel("Devlet Memurları Kanunu-4", [
  InfoCardItem("657_Info_38","Yasaklar", "-Toplu eylem ve hareketlerde bulunma,\n-Grev,\n-Ticaret ve diğer kazanç getirisi,\n-Hediye alma, menfaat sağlama,\n-Denetimindeki teşebbüsten menfaat sağlama,\n-Gizli bilgileri açıklama"),
  InfoCardItem("657_Info_39","Toplu Eylem ve Hareketlerde Bulunma", "Görevden kasıtlı birlikte çekilme, göreve gelmeme ve göreve gelip görevlerini isteyerek aksatmalarına ve yavaşlatmalarına sebep olacak davranışlarda bulunmaları yasaktır.(Madde 26)"),
  InfoCardItem("657_Info_40","Grev", "Greve karar verme, grevi tertipleme ve ilan etme ve greve/grev teşebbüsüne katılmaları yasaktır.(Madde 27)"),
  InfoCardItem("657_Info_41","Ticaret ve Diğer Kazanç Getirisi-1", "-Esnaf veya tacirliği gerektiren işler yapılamaz.\n-ticaret ve sanayi müesseselerinde görev alamaz, ticari mümessil veya ticari vekil veya kollektif şirketlerde ortak veya komandit şirkette komandite ortak olamazlar.\n-Mesleki/Meslek dışı faaliyetlerde bulunmak için ofis açamazlar, açılmış ofislerde çalışamazlar.(Madde 28)"),
  InfoCardItem("657_Info_42","Ticaret ve Diğer Kazanç Getirisi-2", "-Eşi, reşit olmayan veya mahcur çocukları bu faaliyetlerde bulunuyorsa 15 gün içinde kuruma bildirmek zorundadır.\n-Memurun üyesi olduğu yapı, kamu kurumu niteliğindeki kuruluşlar veya Cumhurbaşkanlığı kararnamesi ile kurulan yardım sandıklarının yönetim, denetim ve disiplin kurulları üyelikleri bu yasaklamaların dışındadır.(Madde 28)"),
  InfoCardItem("657_Info_43","Hediye ve Menfaat", "Doğrudan ya da aracı ile hediye istemesi, iş dışında dahi işini etkileyecek hediye almaları, iş sahibinden borç istemeleri ve almaları yasasktır.(Madde 29)"),
  InfoCardItem("657_Info_44","Denetimindeki Teşebbüsten Menfaat Sağlama", "Denetimi altındaki veya kurumu veya kendi görevi ile ilgili teşebbüsten her ne ad altında olursa olsun doğrudan veya dolaylı olarak menfaat sağlayamaz.(Madde 30)"),
  InfoCardItem("657_Info_45","Gizli Bilgileri Açıklama", "Görevinden ayrılmış dahi olsa yetkili bakanın yazılı izni olmadan gizli bilgiler açıklanamaz.(Madde 31)"),
]),InfoCardModel("Devlet Memurları Kanunu-5", [
  InfoCardItem("657_Info_46","Sınıflandırma", "-Genel idare hizmetleri sınıfı,\n-Teknik hizmetler sınıfı,\n-Sağlık hizmetleri ve yardımcı sağlık hizmetleri sınıfı,\n-Eğitim ve öğretim hizmetleri sınıfı,\n-Avukatlık hizmetleri sınıfı,\n-Din hizmetleri sınıfı,\n-Emniyet, jandarma, sahil güvenlik hizmetleri sınıfı,\n-Yardımcı hizmetler sınıfı,\n-Mülki İdare Amirliği hizmetleri sınıfı,\n-Milli İstihbarat Hizmetleri sınıfı"),
  InfoCardItem("657_Info_47","Genel İdare Hizmetleri Sınıfı", "Yönetim, icra, büro ve bezneri hizmetleri gören ve diğer sınıflara girmeyen memurları kapsar.(Madde 36)"),
  InfoCardItem("657_Info_48","Teknik Hizmetler Sınıfı", "Mühendis, mimar, fizikçi, kimyager, matematikçi hidrolog, jeofizikçi, istatistikçi, yöneylemci, çay eksperi, fen memuru, teknisyen vb bölümlerden mezun olup bu mesleklerini icra edenleri kapsar.(Madde 36)"),
  InfoCardItem("657_Info_49","Sağlık/Yardımcı Sağlık Hizmetleri Sınıfı", "Diş tabibi, eczacı, veteriner, fizikoterapist, sağlık mühendisi, sağlık idarecisi, sağlık savaş memuru, diş anestezi, röntgen teknisyeni gibi memurları kapsar.(Madde 36)"),
  InfoCardItem("657_Info_50","Eğitim ve Öğretim Hizmetleri Sınıfı", "Öğretmenleri kapsar.(Madde 36)"),
  InfoCardItem("657_Info_51","Avukatlık Hizmetleri Sınıfı", "Avukatlık ruhsatı olan, baroya kayıtlı ve kurumlarını yargı mercilerinde temsil yetkisine haiz memurları kapsar.(Madde 36)"),
  InfoCardItem("657_Info_52","Din Hizmetleri Sınıfı", "Çeşitli derecelerde din eğitimi görmüş olan ve dini görevini yapan memurları kapsar.(Madde 36)"),
  InfoCardItem("657_Info_53","Emniyet Hizmetleri Sınıfı", "Çarşı ve mahalle bekçisi, polis, komiser muavini, komiser, başkomiser emniyet müfettişi, polis müfettişi, emniyet amiri ve emniyet müdürü ve emniyet müdürü memurlarını kapsar.(Madde 36)"),
  InfoCardItem("657_Info_54","Jandarma Hizmetleri Sınıfı", "Jandarma Genel Komutanlığına bağlı subay, astsubay, uzman jandarma ile çarşı ve mahalle bekçileri kapsar.(Madde 36)"),
  InfoCardItem("657_Info_55","Sahil Güvenlik Hizmetleri Sınıfı", "Sahil Güvenlik Komutanlığına bağlı subay ve astsubayları kapsar.(Madde 36)"),
  InfoCardItem("657_Info_56","Yardımcı Hizmetler Sınıfı", "Kurumlardaki yazıları ve dosyaları dağıtma, toplama ve müracaat sahiplerini karşılama, temizlik, aydınlatma, ısıtma, ilaçlama yapma ve yaptırma ile görevli memurları kapsar. Temizlik, tesisat bakımı vb. işlemlerin ihale yoluyla gördürülmesi mümkündür.(Madde 36)"),
  InfoCardItem("657_Info_57","Mülki İdare Amirliği Hizmetleri Sınıfı", "Valiler, kaymakamlar ve bu sıfatı kazanmış İçişleri Bakanlığı merkez ve iller kurulunda çalışanları ve maiyet memurlarını kapsar.(Madde 36)"),
  InfoCardItem("657_Info_58","Milli İstihbarat Hizmetleri Sınıfı", "Milli İstihbarat Teşkilatında çalışan özel kanunlarla ve Cumhurbaşkanınca tespit edilen görevleri ifa edenleri kapsar.(Madde 36)"),
  InfoCardItem("657_Info_59","Derece Yükselme", "Azami yükselinebilecek derecenin dördüncü kademesinden aylık almaya hak kazanan ve son 8 yılda ceza almayanlar bir üst dereceye yükseltilir.(Madde 37)"),
  InfoCardItem("657_Info_60","Memuriyet Yaşı", "Genel olarak 18 yaşını tamamlıyanlar Devlet memuru olabilirler. Meslek veya sanat okulunu bitirenlerden 15 yaşını dolduranlar kazai rüşt şartı almak şartıyla atanabilir.(Madde 40)"),
  InfoCardItem("657_Info_61","Sınıflandırmada Öğrenim Unsuru", "Genellikle ortaokul mezunları memur olabilir. Ortaokul mezunu yoksa ilkokul mezunu da alınabilir. Alınacak mesleğin niteliğine göre kanunlara/kararnamelere dayanarak ilgili fakülte mezunu olmak, ingilizce bilmek gibi şartlar konabilir.(Madde 41)"),
  InfoCardItem("657_Info_62","Memurun Çalıştırılamaması", "Hiç bir memur sınıfının dışında ve sınıfının içindeki derecesinin altında bir derecenin görevinde çalıştırılamaz. (Madde 45)"),
]),InfoCardModel("Devlet Memurları Kanunu-6", [
  InfoCardItem("657_Info_63","Devlet Memurluğuna Alınma", "Bakanlıklar (Milli İstihbarat Teşkilatı Müsteşarlığı hariç) lüzumlu görülen boş kadroların sayılarını, sınıf ve derecelerini belirterek Devlet Personel Başkanlığına bildirirler.(Madde 46)"),
  InfoCardItem("657_Info_64","Duyurma", "Kadrolar, sınıf ve dereceleri, kurum ve yerleri, alınacak personel sayısı, genel ve özel şartları, sınav yerleri ve zamanlarını, son başvuru tarihinden en az 15 gün önce Resmi Gazete, tv, radyo ve yüksek tirajlı gazete gibi en az 1 yayın organıyla duyurulur.(Madde 47)"),
  InfoCardItem("657_Info_65","Genel Şartlar", "-Türk Vatandaşı olmak,\n-Yaş şartlarını taşımak,\n-Öğrenim şartlarını taşımak,\n-Kamu haklarından mahrum bulunmamak,\n-Akıl hastalığı bulunmamak,\n-Askerlikle ilgisi bulunamak, askerlik çağına gelmemiş olmak veya askerlik hizmetini yapmış/erteletmiş olmak.(Madde 48)"),
  InfoCardItem("657_Info_66","Özel Şartlar", "-Hizmet göreceği sınıf için diplama almış olmak,\n-Kurumların özel kanun veya diğer mevzuatında aranan şartları taşıyor olmak.(Madde 48)"),
  InfoCardItem("657_Info_67","Sınavlara Katılma", "Belirtilen şartlar sağlanarak başvuru yapılır. İlan edilen şartları taşımayanlar sınavlara katılamazlar ve bu durum başvuran kişiye yazı ile bildirilir.(Madde 49)"),
  InfoCardItem("657_Info_68","Sınav Şartı", "Devlet memuru olarak atanmak için ilgili sınavda başarılı olmak şarttır. Sınavsız alınacak alanlar Devlet Personel Başkanlığınca hazırlanacak genel yönetmelikle düzenlenir.(Madde 50)"),
  InfoCardItem("657_Info_69","Sınav Sonuçları", "Başarılı olanların isimleri başarı sıralaması ile ilan edilir ve yazı ile ilgililere bildirilir. Sonuçlar müteakip sınav tarihine kadar geçerlidir.(Madde 51)"),
  InfoCardItem("657_Info_70","Memur İhtiyaçlarını Karşılama", "Kurumlarda ihtiyaç duyulan memurlar sınav sonuçlarında başarılı olanlardan ihtiyaç miktarına kadar olan kısımdan karşılanır. Tekrar alım gerekirse yeni sınav yapılana kadar mevcut sınav başarı sırasında kalan kısımdan sırayla alınabilir.(Madde 52)"),
  InfoCardItem("657_Info_71","Engelli Personel", "-Her kurumda toplam personel sayısının %3'ü kadar engelli personel çalışmak zorundadır. Engelli kontenjanları için sınavlar normal sınavlardan ayrı zamanda engel gruplarının durumlarına göre yapılır.(Madde 53)"),
  InfoCardItem("657_Info_72","Adaylık", "Memurluğa atanan kişi aday memur olur. Aday memurluk bir yıldan az iki yıldan çok olamaz. Adaylık kalkmadan başka kurumlara nakli yapılamaz.(Madde 54)"),
  InfoCardItem("657_Info_73","Adayların Yetiştirilmesi", "Aynı kurumda temel eğitim ve hazırlayıcı eğitim yapılır. Eğitim süreleri, programları, değerlendirme esasları ve hangi kurumların sorumluluğunda yapılacağı Cumhurbaşkanlığı yönetmeliği ile yapılır.(Madde 55)"),
  InfoCardItem("657_Info_74","Adayların Görevine Son Verme", "Temel ve hazırlayıcı eğitimde başarısız olanlar ve adaylık süresince memuriyetle bağdaşmayacak hal ve hareketlerde bulunanlar atamaya yetkili amirin onayı ile memuriyet ile ilişikleri kesilir.(Madde 56)"),
  InfoCardItem("657_Info_75","Adaylık Süresi Sonunda Başarısızlık", "Adaylık süresince aylıktan kesme veya kademe ilerlemesi durdurma cezası alan kişiler disiplin amiri teklifi ve atamaya yetkili amir onayı ile memuriyetine son verilir. 3 yıl içinde Devlet memurluğuna alınmazlar.(Madde 57)"),
]),InfoCardModel("Devlet Memurları Kanunu-7", [
  InfoCardItem("657_Info_76","Asli Memurluğa Atanma", "Adaylık devresi içinde başarılı olan memurlar disiplin amirlerinin teklifi ve atamaya yetkili amirlerinin onayı ile onay tarihinden itibaren geçerli olmak üzere asli memurluğa atanırlar.(Madde 58)"),
  InfoCardItem("657_Info_77","İstisnai Memurluklar", "Cumhurbaşkanlığı ve TBMM memurlukları, Bakan Yardımcılıkları, Savunma Sanayi Müsteşarlığına ait Müsteşar, Müsteşar Yardımcılıkları ..... Valiliklere, Büyükelçiliklere, Dışişleri Bakanlığına Stratejik Araştırma Merkezi Başkanlığına, Anayasa Mahkemesi Basın Müşavirliğine .... gibi birçok alana memur atanabilir.(Madde 59)"),
  InfoCardItem("657_Info_78","İstisnai Memurluklarda Aranacak Şartlar", "48.maddede yer alan memur olurken gereken şartlar geçerlidir. Dışişleri Bakanlığı Hukuk Müşavirliğine atanabilmek için ise bir yabancı dili çok iyi bilmek ve özel yönetmeliğinde belirtilecek diğer nitelikleri taşımak şarttır.(Madde 60)"),
  InfoCardItem("657_Info_79","İstisnai Memurların Hakları", "657'nin atanma, sınavlar, kademe ilerlemesi ve derece yükselmesi dışındaki tüm hükümler uygulanır. Bakan müşaviri olarak memur olmayanlar da atanabilir. Görev süreleri Bakanın görev süresi ile sınırlıdır. Görev süresi tamamlanınca memuriyetle ilişikleri kesilir.(Madde 61)"),
  InfoCardItem("657_Info_80","Atama Yerine Gidiş-1", "-Aynı yerde göreve başlayanlar atama emri tebliğ gününde,\n-Başka yerde görev yapanlar tebliğ tarihi itibariyle 15 gün içerisinde göreve başlar.\n-Olağanüstü hallerde süre Cumhurbaşkanı Kararı ile kısaltılabilir.(Madde 62)"),
  InfoCardItem("657_Info_81","Atama Yerine Gidiş-2", "SÜRELER BELİRLENİRKEN; \n-Kanuni izinde veya görevde olanlar için iznin veya görevin bitimi,\n-Hesap devri zorunluluğu olan sayman ve sayman mutemetleri için yerlerine gelenin olması,\n-Eski görevlerine devamları kurumlarınca tebliğ edilenler için yerlerine memur gelmesi ile birlikte süreler başlatılır.(Madde 62)"),
  InfoCardItem("657_Info_82","İşe Başlamama", "Geçerli mazaret olmadan gerekli sürede işe başlamayanların atamaları iptal edilir ve 1 yıl süreyle Devlet memuru olarak istihdam edilmezler.\n-Belge ile ispatlı sebebi olanlar 2 ay içinde göreve başlamazsa atama yetkili makamlarca iptal edilir.\n-Başka yerden gelenler için 15 günlük süre dolarsa ek 10 gün daha verilir. Bu sürede göreve başlanmazsa memuriyetten çekişmiş sayılırlar.(Madde 63)"),
  InfoCardItem("657_Info_83","Kademe İlerlemesi-1", "-Derece içinde görevin önemi ve sorumluluğu artmadan aylıktaki artışa kademe ilerlemesi denir.\n-1 yıl aynı kademede olan ve bulunduğu derecede ilerleyeceği kademesi olanlar kademe ilerlemesi alır.\n-Zorunlu hizmet görevi yapanlar 2 yılda bir kademe ilerlemesi alır.\n-Yıllık izin çalışılmış sayılır.(Madde 64)"),
  InfoCardItem("657_Info_84","Kademe İlerlemesi-2", "-8 yıl ceza almayanlar kademe ilerlemesi alır.\n-Kademe ilerlemesini atamaya yetkili amir onaylar.\n-Kademe ilerlemesi alamayanlar kurumlarınca her ay toplu onay alınarak belirlenir.\n-Önce kademe ilerlemesi alıp sonra iptal edilirse iptal süresi kademe ilerlemesinin alındığı tarihtir.(Madde 64)"),
  InfoCardItem("657_Info_85","Derece Yükselmesi-1", "-Üst derecede boş kadronun olması,\n-Derecede 3 yıl, 3. kademede 1 yıl bulunmuş olmak,\n-Derece almak için gerekli nitelikleri taşımak gerekir.(Madde 68)"),
  InfoCardItem("657_Info_86","Derece Yükselmesi-2", "-1.derecede 5300 ek gösterge veya daha yukarı olanlardan en az 12 yıl,\n-1. ve 2. derecede 5300 ek göstergeden az olanlar için 10 yıl,\n-3. ve 4. derece için 8 yıl hizmeti olanlar süre kaydı aranmadan bulunacakları dereceden daha aşağı derecelere atamaları yapılabilir.\n-Yüksek öğrenim görme şartı aranır.\n-4 yıllıktan az öğrenim görenler için sürelere 2 yıl eklenir.(Madde 68)"),
  InfoCardItem("657_Info_87","Derece Yükselmesi-3", "-Sürelerin hesabında önceki kurumlardaki çalışma süreleri, Yasama Organı Üyeliğinde, belediye başkanlığında, belediye ve il genel meclisi üyeliğinde, asker ve asker öğretmenlikte, uluslararası firmalarda/serbest çalışmanın tamamı; müsteşarlık/müsteşar yardımcılığı, genel müdür ve başkan kadroları için tamamı diğerleri için 6 yılı geçmeden sürenin 3/4 ü dikkate alınır. (Madde 68)"),
  InfoCardItem("657_Info_88","Derece Yükselmesi-4", "-Bu bende göre atananlar ek gösterge dahil tüm haklardan yararlanır. Yıllık kademe ilerlemesi ve 3 yıllık derece ilerlemesini alırlar. Ancak kadro aylıkları başka göreve atanırlarsa kazanılmış hak sayılmaz. Daire başkanı vb kadrolarda hizmet süresi 5 yıl kabul edilir ve kazanılmış hak olarak ele alınır.(Madde 68)"),
  InfoCardItem("657_Info_89","İdari Görevlere Atanama", "Eğitim Öğretim, Sağlık ve Yardımcı Sağlık Hizmetleri sınıflarında olanlardan 2-4 üncü derece arasındakilerden 2 alt dereceye kadar olan kadrolara atanabilirler. Aylıkları yeni göreve göre ödenir. Ek gösterge, tazminat ve zam farklı ise fazla olan ödenir.(Madde 69)"),
  InfoCardItem("657_Info_90","Başka Sınıfa Geçme", "Sınıf değişikliği kurum kanunu ve Cumhurbaşkanlığı kararnamesi ile mümkündür. Mesleki sınıftan genel idare hizmetleri sınıfına görev ve ünvan eşitliği gözetmeden kazanılmış hak aylıkları ile atanabilirler. Eski sınıfında daha yüksek derecede olanlara dereceleri verilir ve bu süre tamamlanana kadar derece ilerlemesi verilmez.(Madde 71)"),
]),InfoCardModel("Devlet Memurları Kanunu-8", [
  InfoCardItem("657_Info_91","Yer Değiştirme-1", "-Aile birliğinin muhafaza edilmeesi esastır.\n-Atama olacak yerde eşin görevine uygun görev bulunmuyorsa eşinin görev süresince izin verilebilir. Maaşları kanuni kesintiler yapıldıktan sonra; \n-OHAL bölgesi ise %60'ı,\n-1.derece zorunlu hizmette olanlara %50'si,\n-2.derece zorunlu hizmette olanlara %25'i kurumlarınca ödenir. Diğer derecede olanlar ücretsiz izinli sayılır.(Madde 72)"),
  InfoCardItem("657_Info_92","Yer Değiştirme-2", "-Aile birliğinde belirtilen istisnalar 4 yılı geçemez.\n-Kademe ilerlemesi, emeklilik ve tüm hakları devam eder. Ancak ücretsiz izne ayrılanlar için emekliliğe esas süre sayılmaz. Sayılması için kesenek ve kurum karşılıklarının kişi tarafından Emekli Sandığına yatırılması gerekir.(Madde 72)"),
  InfoCardItem("657_Info_93","Yer Değiştirme-3", "-Engellilik durumuna bağlı yer değiştirmelerde gerekli düzenlemeler yapılır.\n-Memurların atanamayacakları yerler ve bu yerlerdeki görevler Devlet Personel Başkanlığınca hazırlanan yönetmelikle yapılır.\n-Kurumlar atanacak personeli için Devlet Personel Başkanlığının görüşü alınarak bir personel ve atama planı hazırlar.(Madde 72)"),
  InfoCardItem("657_Info_94","Karşılıklı Yer Değiştirme", "-Aynı kurumdaki farlı görev yerlerindeki aynı sınıftaki memurlar karşılıklı yer değştirme talebinde bulunabilirler.\n-Bu değişiklik atamaya yetkili amirin onayına bağlıdır.(Madde 73)"),
  InfoCardItem("657_Info_95","Memurların Nakli", "-Memurun isteği ve kadro dereceleri arasındaki farkın  3 dereceden çok olmaması şartıyla nakil gerçekleşebilir.\n-Aşağı dereceye atananlar eski  derecelerine atanablir. Aşağı dereceye atananlar kesenek ve farkları Emekli Sandığına yatırarak emeklilikte eski derecelerinde sayılabilir.(Madde 74)"),
  InfoCardItem("657_Info_96","Memurların Kurumlarınca Görev ve Yer Değişiklikleri", "-Kurumlar eşit veya üst derecelere aynı veya başka yerlerdeki kadrolara naklen atayabilir.\n-Memur isteği ile 3 derece alta kadar nakil yapılabilir.\n-Alt dereceye atananlar kesenek ve farkları Emekli Sandığına yatırarak emeklilikte eski derecelerinde sayılabilirler.(Madde 76)"),
  InfoCardItem("657_Info_97","Yabancı Memleket/Uluslararası Kuruluşta Görev", "-Kurum muvafakati ile görev alacak memurlar 3 yılda bir alınacak ilgili Bakan onayıyla yabancı kuruluş resmi kurumlarında 10 yıla, uluslararası kuruluşlarda 21 yıla kadar aylıksız izin verilir. \n-Emeklilik hakları ve memur kayıtları saklı kalır.\n-Geri dönmek isterse boş kadro varsa döner ve derece kadameleri alır.(Madde 77)"),
  InfoCardItem("657_Info_98","Staj İçin Yurt Dışı", "-Asli memurluğunu almış memurlar kendilerini geliştirmek, staj yapmak amacıyla yurt dışına gidebilirler.\n-Kurumlarınca açılan seçme veya yarışma sınavında başarılı olanlara,\n-Dış burslara dayandırılarak gönderilenlere 2 yıla kadar izin verilir.\n-Bu izin en çok 1 kat artabilir.(Madde 78)"),
  InfoCardItem("657_Info_99","Hak ve Yükümlülükleri-1", "-Emeklilik, kademe ilerlemesi ve tüm hakları devam eder.\n-Aylıklarının kanuni kesintilerden sonra net tutarın %60'ını alırlar.\n-İzin bitiminde yol süresi hariç 15 gün içinde göreve başlarlar.\n-Kurumlarınca aylıksız izin verilenler kesintilerini öderse emeklilik haklarından faydalanırlar.(Madde 79)"),
  InfoCardItem("657_Info_100","Hak ve Yükümlülükleri-2", "-Çalışma esasları Dışişleri Bakanlığınca belirlenen, staj yapan, gittikleri ülkede sürekli görevde bulunan ve 9. derece 1. kademeden aylık alan memurlara yurtdışı aylığı tutarı, diğerlerine bu tutarın 2/3'ü ödenir.\n-Şahsi burs sağlayanlar ve bu sebeple aylıksız izin alanların bursları ücretten az ise fark kurumlarınca ödenir.\n-(Madde 79)"),
  InfoCardItem("657_Info_101","Hak ve Yükümlülükleri-3", "-Süre bitiminde göreve başlamayanlar çekilmiş sayılır.\n-Aylık ve yol giderlerini 2 kat olarak geri ödemek zorundadırlar.\n-Görevlerine başlayıp da mecburi hizmeti bitirmeden ayrılanlar veya ceza ile memuriyetten çıkarılanlar mecburi hizmetlerinin eksik kalan kısmı ile orantılı miktarı 2 kat ödemek zorundadır.(Madde 79)"),
  InfoCardItem("657_Info_102","Silah Altına Alınma", "Hazarda ve seferde muvazzaflık hizmeti dışında silah altına alınan Devletmemurları, silah altında bulundukları sürece izinli sayılırlar(Madde 81)"),
  InfoCardItem("657_Info_103","Askerlik ve Terhis-1", "-Terhis tarihinden sonra 30 gün içinde göreve başlamak zorundadırlar.\n-Kurumları başvuru tarihinden itibaren 30 gün içinde göreve başlatmak zorundadır.\n-Askerlikte geçen süre ayrıldıkları sıradaki derece kademe ilerlemesi yapılmak suretiyle değerlendirilir.\n-Askerlik öncesi ve askerlik süresi toplamı 3 yılı geçerse derece kademe ilerlemesi alınır.(Madde 83)"),
  InfoCardItem("657_Info_104","Askerlik ve Terhis-2", "-Askerlik sonrası Devlet memurluğuna atananlar adaylık esaslarına tabi olurlar.\n-Asalet tasdik olunduktan sonra kademe ilerlemeleri ve derece yükseltmeleri sınav veya seçmeye tabi tutularak değerlendirilir.(Madde 84)"),
  InfoCardItem("657_Info_105","Savaşta Derece Kademe", "-Talim veya manevra maksadiyle veya savaş nedeniyle silah altına alınanların kademe ilerlemeleri devam eder.\n-Bu süre içinde derece yükseltmek gerekirse terhis sonrası seçme veya sınav yoluyla dereceleri yükseltilir.(Madde 85)"),
  InfoCardItem("657_Info_106","Vekalet ve Aylık-1", "-Memurun bir sebeple görevden olmaması durumunda geçici olarak kurum içinden veya diğer kurumlardan veya açıktan vekil atanabilir.\n-Memur tarafından vekaleten yürütülen görevde aylıksız vekalet asıldır.(Madde 86)"),
  InfoCardItem("657_Info_107","Vekalet ve Aylık-2", "-İlkokul öğretmenliği(yaz tatili hariç), tabiplik, diş tabipliği, eczacılık, mühendislik, mimarlık, veterinerlik, vaizlik, Kur'an kursu öğreticiliği, imamlık vb boş kadrolara Maliye Bakanlığı izni ile açıktan vekil atanabilir.(Madde 86)"),
  InfoCardItem("657_Info_108","Vekalet ve Aylık-3", "-Kurum içinden vekaleti 3 ayı geçen memurlara vekalet aylığı ödenir.\n-Kurum dışından veya açıktan atananlarla ilkokul öğretmenliği ve veznedar olarak atananlara atanma tarihi itibariyle vekalet aylığı ödenir.\n-Gerekli görülürse memurlara bu görevler ücretsiz olarak gördürülebilir.\n-Saymanlık görevine görev tarihi itibariyle aylık ödenir. (Madde 86)"),
]),InfoCardModel("Devlet Memurları Kanunu-9", [
  InfoCardItem("657_Info_109","İkinci Görev Yasağı-1", "-Bu kanuna tabi kurumlarda,\n-Sermayesinin tamamı devlet tarafından verilen iktisadi kurumlarda,\n-Sermayesinin yarıdan fazlası devletin olan bankalarda,\n-Özel kanunlarla veya Cumhurbaşkanlığı kararnamesi ile kurulan banka ve kuruluşlarda,\n-Sayılan kurumların yarıdan fazla sermayesi olan kuruluşlarda ikinci görev verilemez, para ödenemez ve yarar sağlanamaz.(Madde 87)"),
  InfoCardItem("657_Info_110","İkinci Görev Yasağı-2", "-Ancak özel kanunlarla yapılması ve ücret alınmasında sakınca olmayan durumlar için memurların hakları saklıdır.(Madde 87)"),
  InfoCardItem("657_Info_111","İkinci Görevler-1", "-Özel kanunlara ve Cumhurbaşkanlığı kararnamesine dayandırılarak memurlara gördürülmesi öngörülen sürekli hizmetler,\n-Tabiplik, diş tabipliği, eczacılık, kimyagerlik, veterinerlik, avukatlık, adli tıp uzmanlığı,\n-Görev yerlerindeki il-ilçe belediyelerindeki mühendislik ve mimarlik işleri ikinci görev olarak verilebilir.(Madde 88)"),
  InfoCardItem("657_Info_112","İkinci Görevler-2", "-Tabiplere; sağlık müdürlüğü/müdür yardımcılığı, halk sağlığı müdürlüğü/müdür yardımcılığı, şube müdürlükleri,\n-Diş hekimi ve veterinerlere baştabiplik ve yardımcılığı,\n-Eczacılara baştabip yardımcılığı,\n-Öğretmenlere okul ve enstitü müdürlüğü, başyardımcılığı ve yardımcılığı görevleri ikinci görev olarak verilebilir.(Madde 88)"),
  InfoCardItem("657_Info_113","Ders Görevi", "-Üniversite ve akademiler dahil olmak üzere her derecedeki eğitim kurumunda gerekli eğitmen bulunamaması durumunda öğretmenlere, öğretim üyelerine, memurlara veya açıktan atanacaklara ücreti ile ek ders verilebilir.\n-Ders saatlerinin sayısı, görev alacakların nitelikleri ve diğer hususlar Cumhurbaşkanlığı kararı ile tespit edilir.(Madde 86)"),
  InfoCardItem("657_Info_114","Birleşmeyecek Görevler", "-Vekalet görevi, ikinci görev veya ders görevlerinden ancak birisi verilebilir.\n-Öğretmenlerin idari görevi bu hükmün dışındadır.\n-Memurda  birden çok ücretli vekalet görevi veya ikinci görev bulunamaz.(Madde 90)"),
  InfoCardItem("657_Info_115","Kadroları Kalkan Memurlar-1", "-En geç 6 ay içinde kurumlarında uygun kadroya atanırlar.\n-Uygun kadro yoksa aynı süre içinde atanmak üzere Devlet Personel Başkanlığına bildirilirler.\n-Atama gerçekleşene kadar uygun işlerde çalıştırılırlar.\n-Atanmaya kadar mali hakları ve sosyal yardımları devam eder.(Madde 91)"),
  InfoCardItem("657_Info_116","Kadroları Kalkan Memurlar-2", "-Eski kadro ile yeni kadro arasında mali hak farklılığı olması durumunda aradaki fark kesintisiz şekilde tazminat olarak ödenir.\n-Aylık değil dönemsel yapılan ödemelerde çalıştığı süre kadar aya bölünür ve en son aldığı tutara ek ödeme ilave edilir.\n-Fazla çalışma ücreti, ek ders ve nöbet ücreti aylık tutara ilave edilmez.(Madde 91)"),
  InfoCardItem("657_Info_117","Kadroları Kalkan Memurlar-3", "-Kurum değişikliği yapacak personel Devlet Personel Teşkilatınca tespit edilen kadroya, ilgili Bakanlıkca teklif edilir.\n-Bu işlemin süresi 45 günü geçemez.\n-Teklif edildikten sonra en geç 30 gün içerisinde atama işlemi yapılmak zorundadır.\n-Ünvanı müdür ve daha üst olanlar ve danışma işlevleri olan kadrolar Araştırmacı kadrolarına diğerleri uygun kadrolara atanırlar.(Madde 91)"),
  InfoCardItem("657_Info_118","Memurluktan Çekilenlerin Atamaları", "-2.defayı geçmemek şartıyla ayrıldıkları sınıfta boş kadro olursa ve nitelikleri taşımak şartıyla aynı derece ve kademede başlayabilirler.\n-Yasama görevi veya bakanlıkta geçen süre yılda bir kademe iki yılda bir derece ilerlemesi olarak hesaplanır.\n-657'ye tabi olmayan memurlar uygun nitelikler taşınırsa uygun sınıftaki kadrolara atanabilir.(Madde 92)"),
  InfoCardItem("657_Info_119","Emeklilerin Yeniden Hizmete Alınması", "Yeniden göreve atanamayacak olanlar hariç emekli olanlar kurumlarında boş kadro olmak şartıyla yeniden memurluğa atanabilirler.(Madde 93)"),
]),InfoCardModel("Devlet Memurları Kanunu-10", [
  InfoCardItem("657_Info_120","Memuriyetin Sona Ermesi Çekilme", "-Devlet memuru yazılı olarak müracaat ederek çekilme isteğinde bulunabilir.\n-Kurumca kabul edilen mazereti olmadan kesintisiz 10 görev terkinde çekilme talep edilmiş sayılır.\n-Yerine atanan olması gereken görevlerde 1 aya kadar görevde kalır.\n-Süre 1 ayı geçerse üstüne haber vererek görevi bırakabilir.\n-Olağanüsü hallerde 1 ay şartı aranmadan çekilebilir.(Madde 94)"),
  InfoCardItem("657_Info_121","Devir Teslim Yükümlülüğü", "-Görevinde devir teslim yükümlülüğü olanlar devir gerçekleşene kadar görevi bırakamaz.\n-Devir ve teslim için gerekli süre yönetmelikte belirtilir.(Madde 95)"),
  InfoCardItem("657_Info_122","Olağanüstü Hallerde Çekilme", "Olağanüstü hal, savaş, seferberlik, afet gibi durumlarda çekilme kabul edilmedikçe veya yerine atanacaklar görevi teslim almadıkça görevlerini bırakamazlar.(Madde 96)"),
  InfoCardItem("657_Info_123","Çekilen/Çekilmiş Sayılanın Yeniden Atanması", "Mali ve cezai sorumlulukları saklı kalmak üzere\n-Usule uygun görevden ayrılanlar 6 ay geçmeden,\n-Usule uymadan görevden ayrılanlar 1 yıl geçmeden,\n-Devir zorunluluklarına uymadan ayrılanlar 3 yıl geçmeden,\n-Olağanüstü hal durumuna uymayanlar hiçbir surette Devlet memurluğuna alınmazlar.(Madde 97)"),
  InfoCardItem("657_Info_124","Memurluğun Sona Ermesi", "-Memurluktan çıkarılması,\n-Memurluk şartlarını taşımadığı sonradan anlaşılması veya şartları kaybetmesi,\n-Memurluktan çekilmesi,\n-İstek, yaş haddi, malüllük vs sebeplerle emekliye ayrılması,\n-Ölümü hallerinde memuriyeti sona erer.(Madde 98)"),
]),InfoCardModel("Devlet Memurları Kanunu-11", [
  InfoCardItem("657_Info_125_1_hatayla_atlamisim","Çalışma Saatleri", "-Haftalık çalışma süresi genellikle 40 saattir.\n-Bu süre cumartesi ve pazar tatil olmak üzere düzenlenir.\n-Kararname ve yönetmelikle çalışma süreleri değiştirilebilir.\n-Yurtdışı kuruluşlarda hizmet gerekirse Cumhurbaşkanı hafta tatilini farklı günler olarak tespit edebilir.(Madde 99)"),
  InfoCardItem("657_Info_125","Günlük Çalışma Saatleri", "-Günlük başlama, bitiş ve öğle arası dinlenme süresi saatleri merkezde Cumhurbaşkanınca, illerde valilerce düzenlenir.\n-Engelliler için süreler merkezde üst yönetici, taşrada mülki amirce farklılaştırılabilir.(Madde 100)"),
  InfoCardItem("657_Info_126","24 Saat Görev Gerektiren Mesleklerde", "-Askerlik, polislik, doktorluk vb. mesleklerde çalışma saatleri ve şekilleri kurumlarca belirlenir.\n-Kadın memurlara tabip raporu ile hamileliğin 24 ncü haftasından önce, her halde 24 üncü haftadan sonra ve doğumdan sonraki 2 yılda gece nöbeti verilemez.\n-Engelli vatandaşlara isteği dışında gece nöbeti verişlemez.(Madde 101)"),
  InfoCardItem("657_Info_127","Yıllık İzin", "-Hizmet süresi 1-10 yıl(on yıl dahil) olanlar için 20 gün, 10 yıldan fazla olanlar için 30 gündür.\n-Zorunlu hallerde gidiş ve dönüş için en fazla 2 şer gün eklenebilir.(Madde 102)"),
  InfoCardItem("657_Info_128","Yıllık İznin Kullanılışı", "-Amirin onayı ile toptan veya parça olarak kullanılılabilir.\n-Üst üste 2 yılın izni bir arada verilebilir.\n-Cari yıl ile bir önceki yıl hariç geçmiş izinler düşer.\n-Öğretmenler tatillerde izinli sayılır.\n-Öğretmenlere hastalık ve mazaretler dışında yıllık izin verilmez.\n-Radyoaktif ışınla çalışanlara her yılek 1 ay sağlık izni verilir.(Madde 103)"),
  InfoCardItem("657_Info_129","Mazeret İzni-1", "-Kadın memura doğumdan önce ve sonra 8'er haftalık toplam 16 hafta izin verilir.\n-Çoğul gebelikte doğum öncesi izne 2 hafta eklenir.\n-Çalışabilir raporu olanlar doğuma 3 hafta kalana kadar çalışabilir ve kalan izinleri doğum sonrasına aktarılabilir.\n-Doğum beklenenden önce gerçekleşirse izinler doğum sonrasına eklenir.(Madde 104)"),
  InfoCardItem("657_Info_130","Mazeret İzni-2", "-Doğumda veya doğum sonrasında anne vefar ederse talep halinde izinleri memur babaya aktarılabilir.\n-3 yaşını doldurmamış çocuğu evlat edinenlere 8 hafta izin verilir.\n-Doğumda babalık izni 10 gündür.\n-Kendisinin veya çocuğunun evlenmesi, eşinin, çocuğunun, kendisinin ya da eşinin anne, baba ve kardeşinin ölümünde isteğe bağlı 7 gün izin verilir.(Madde 104)"),
  InfoCardItem("657_Info_131","Mazeret İzni-3", "-Merkezde atamaya yetkili amir, ilde vali, ilçede kaymakam ve yurt dışında diplomatik misyon şefi kurum amirinin muvafakati ile bütün veya parçalı olarak 10 gün izin verilir.\n-Öğretmenler hariç zorunlu hallerde bu izne 10 gün daha eklenir.\n-İkinci 10 günlük izin yıllık izinden düşülür.\n-Tüm izinlerde mali haklar ve sosyal yardımlar saklıdır.(Madde 104)"),
  InfoCardItem("657_Info_132","Mazeret İzni-4", "-Analık izni biten memura ilk 6 ay günde 3 saat, ikinci 6 ayda günde 1.5 saat süt izni verilir.\n-Süt izin saatlerinde memurun tercihi esastır.\n-%70 oranında engelli çocuğu olan memur eşlerden birine çocuğun hastalığı durumunda 10 gün izin verilir.\n-Çocuk evliyse ve eşi de en az %70 engelliyse bu izin yine verilir.(Madde 104)"),
  InfoCardItem("657_Info_133","Mazeret İzni-5", "-Analık izni sonrasında çocuk sağ ise süt izni verilmeden 1.doğumda 2 ay, 2.doğumda 4 ay, 3.doğumda 6 ay günlük çalışma süresinin yarısı kadar çalışabilir.\n-Çoğul doğumlarda 1'er sürelere ay ilave edilir.\n-Çocuğun engelli doğması/12 ay içinde engelli olduğu tespitinde süreler 12 ay yapılır.\n-Evlar edinmelerde de bu haklar saklıdır.(Madde 104)"),
  InfoCardItem("657_Info_134","Hastalık ve Refakat İzni-1", "-Kanser, verem, akıl hastalığı gibi sürekli tedavi gereken hallerde 18 ay, diğer hastalıklarda 12 aya kadar izin verilir.\n-İzin bitiminde işe başlayabilmesi için iyileştiğine dair raporun ibrazı zorunludur.\n-İzin bitiminde iyileşme olmazsa izin uzatılır, tekrar iyileşmezse emeklilik uygulanır.(Madde 105)"),
  InfoCardItem("657_Info_135","Hastalık ve Refakat İzni-2", "-İyileşme sağlandığı raporla tespit edilirse ve emeklilik gerçekleşmemiş olursa uygun kadrolara atanırlar.\n-Görev sırasında kazaya veya saldırıya uğrayan veya meslek hastalığına tutulan kişi iyileşene kadar izinli sayılır.\n-(Madde 105)"),
  InfoCardItem("657_Info_136","Hastalık ve Refakat İzni-3", "-Anne, baba, eş, çocuk veya kardeşlerinden biri ağır kaza geçirmesi veya uzun süreli tedavi gerektiren hastalık durumlarında refakat edilmezse hayati tehlikesi olacak ise 3 aya kadar izin verilir.\n-Gerekirse süre 1 kat daha arttırılabilir.(Madde 105)"),
  InfoCardItem("657_Info_137","Aylıksız İzin-1", "-Memurlara hakettikleri izin bitiminde durumlarını raporla belgelendirmeleri halinde 18 aya kadar aylıksız izin verilir.\n-Doğum yapan memura izinleri bitiminde, eşi doğum yapan memura ise doğum tarihinden itibaren istekleri üzerinde 24 aya kadar aylıksız izin verilir.(Madde 106)"),
  InfoCardItem("657_Info_138","Aylıksız İzin-2", "-3 yaşından küçük çocuğu evlat edinen memurlara da izinleri bitiminden itibaren 24 aylık izin verilir.\n-Eşlerden ikiside memur ise talepleri ile 24 aylık süre birbirini takip eder şekilde 2 eşe de sırayla verilebilir.\n-Yurt dışına görev/eğitim amaçlı giden memurların eşlerine aylıksız izin verilebilir.\n-Askerlik yapanlar aylıksız izinli sayılır.(Madde 106)"),
  InfoCardItem("657_Info_139","Aylıksız İzin-3", "-Beş hizmet yılını tamamlamış memurlara istekleri ile memuriyet boyunca en fazla 2 kez toplam 1 yıla kadar aylıksız izin verilebilir.\n-Olağanüstü hallerde ve afet durumlarında geçerli değildir.\n-Aylıksız izin süresi bitmeden özür ortadan kalkarsa 10 gün içinde dönmek zorunludur.\n-Dönmeyenler memuriyetten çekilmiş sayılır.(Madde 106)"),
]),InfoCardModel("Devlet Memurları Kanunu-12", [
  InfoCardItem("657_Info_140","Memur Bilgi Sistemi", "Her memur TC kimlik numarası esas alınarak kurumlarınca tutulan personel bilgi sistemine kaydolur.(Madde 109)"),
  InfoCardItem("657_Info_141","Özlük Dosyası-1", "-Her memurun özlük dosyası vardır.\n-Dosyada memurun meslek bilgileri, mal bildirimleri, varsa ödül ve ceza belgeleri ve raporları bulunur.\n-Başarı, yeterlilik, derece-kadame ilerlemesi ve emeklilk işlemleri özlük dosyası göz önünde bulundurularak yapılır.\n-Usül ve esaslar Devlet Personel Başkanlığınca belirlenir.(Madde 109)"),
  InfoCardItem("657_Info_142","Başarı ve Üstün Başarı Belgesi", "-Olağanüstü gayret ve çalışmaları olan, kamu kaynağına tasarruf ve fayda sağlayan çalışmalar yapanlara Bakanlar, valiler ve kaymakamlar başarı belgesi verebilir.\n-3 kez başarı belgesi alanlar üstün başarı belgesi alır.\n-Üstün başarı belgesi alanlara bakan veya vali tarafından en yüksek memur aylığının %200 üne kadar ödül verilebilr.(Madde 122)"),
  InfoCardItem("657_Info_143","Ödüllendirme Sayısı", "Mali yıl içerisinde ödüllendirilecek sayısı kurumun yılbaşındaki dolu kadro mevcudunun binde onundan çok olamaz.\n-Meb, Emniyet, Jandarma ve Sahil Güvenlik ve Gümrük Müsteşarlığında binde 20'den fazla olamaz.(Madde 122)"),
  InfoCardItem("657_Info_144","Disiplin Cezası", "Yurt içinde ve dışında kanun ve yönetmeliklerin emrettiği görevleri yerine getirmeyenler, yasaklı işleri yapanlar niteliğe göre ceza alır.(Madde 124)"),
  InfoCardItem("657_Info_145","Uyarma Cezası-1", "-Daha dikkatli davran anlamında yazılı bildirilen cezadır.\nVERİLDİĞİ DURUMLAR;\n-Verilen görevin tam ve zamanında yapılmaması,\n-Belge, araç ve gereçlerin özenli kullanılmaması,\n-İzinsiz geç gelmek, erken çıkmak, görev yerini terk etmek,\n-Kurum tasarruf tedbirlerine uymamak, (Madde 125)"),
  InfoCardItem("657_Info_146","Uyarma Cezası-2", "VERİLDİĞİ DURUMLAR DEVAMI\n-Usülsüz müracaat ve şikayet,\n-Yakışmayan tutum ve davranışlar,\n-Göreve ve iş sahiplerine kayıtsız ve ilgisiz kalmak,\n-Kılık kıyafete uymamak,\n-İş birliğine aykırı davranmak \nuyarı cezası almayı gerektiren durumlardır. (Madde 125)"),
  InfoCardItem("657_Info_147","Kınama Cezası-1", "-Görev ve davranışların kusurlu olduğunun yazılı olarak bildirilmesidir.\nVERİLDİĞİ DURUMLAR;\n-Verilen görevin tam ve zamanında yapılmaması,\n-Belge, araç ve gereçlerin özenli kullanılmaması,\n-Eşlerinin ve reşit olmayan çocuklarının sürekli gelirlerini bildirmemek,\n-Amire saygısızlık,\n-Hizmet dışında yanlış hal ve hareketler,(Madde 125)"),
  InfoCardItem("657_Info_148","Kınama Cezası-2", "-Devlete ait resmi araç-gereçleri özel işlerde kullanmak,\n-Bu eşyaları kaybetmek,\n-İş arkadaşlarına ve maiyetindeki personle kötü davranmak,\n-Söz veya hareketle sataşmak,\n-Ahlak dışı hareketler yapmak, yazı yazmak ve resim çizmek,\n-Emre itiraz,\n-Borcu kasten ödemeyerek davalık olmak,\n-Kurum düzenini bozmak,\n-Yetkisiz basına demeç verme kınama cezası gerektirir.(Madde 125)"),
  InfoCardItem("657_Info_149","Aylıktan Kesme-1", "-Brüt aylıktan 1/30-1/8 arası kesinti yapılmasıdır.\nVERİLDİĞİ DURUMLAR;\n-Kasıtlı olarak verilen emri tam ve zamanında yapmamak,\n-Resmi araç, gereç ve belgeleri korumamak,\n-Özürsüz 1 veya 2 gün işe gelmemek,\n-Resmi araç, gereç ve belgeleri kullanarak menfaat elde etmek,\n-Yalan ve yanlış beyanda bulunmak,\n-Amirlere saygısızlık(Madde 125)"),
  InfoCardItem("657_Info_150","Aylıktan Kesme-2", "\nVERİLDİĞİ DURUMLAR;\n-Görev yerindeki herhangi bir yerin izinsiz toplantı, tören ve benzeri amaçlar için kullanılmasına izin vermek,\n-Devlet memuruna yakışmayacak hareketlerde bulunmak aylıktan kesme cezsaı gerektirir.(Madde 125)"),
  InfoCardItem("657_Info_151","Kademe İlerlemesinin Durdurulması-1", "-Fiilin ağırlığına göre kademe ilerlemesinin 1-3 yıl durdurulmasıdır.\nVERİLDİĞİ DURUMLAR;\n-Göreve sarhoş gelmek, görev yerinde alkol içmek,\n-Özürsüz ve kesintisiz 3-9 gün gelmemek,\n-Görevi ile ilgili çıkar sağlamak,\n-Amirine ve maiyetindekilere küçük düşürücü davranmak,\n-Görev yerinde izinsiz toplantı, gösteri ve tören yapılmasına izin vermek(Madde 125)"),
  InfoCardItem("657_Info_152","Kademe İlerlemesinin Durdurulması-2", "VERİLDİĞİ DURUMLAR;\n-Gerçeğe aykırı belge ve rapor düzenlemek,\n-Ticaret yapmak, yasaklanan getirilerden sağlamak,\n-Görevde dil, din, ırk, siyasi görüş ayırmak,\n-Mal bildiriminde bulunmamak,\n-Açıklanması yasak bilgiler açıklamak,(Madde 125)"),
  InfoCardItem("657_Info_153","Kademe İlerlemesinin Durdurulması-3", "VERİLDİĞİ DURUMLAR;\n-Hakaret ve tehditte bulunmak,\n-Yurt dışında ödeme gücü üstünde mazeretsiz borçlanmak ve ödememek,\n-Kasten görevi ihmal etmek,\n-Verilen emri kasten yerine getirmemek,\n-Herhangi bir siyasi parti yararına veya zararına fiilen faaliyette bulunmak kademe ilerlemesi durdurma cezası gerektirir.(Madde 125)"),
  InfoCardItem("657_Info_154","Devlet Memurluğundan Çıkarma-1", "-Bir daha Devlet memurluğuna atanmamak üzere memurluktan çıkarmaktır.\nVERİLDİĞİ DURUMLAR;\n-İdeolojik veya siyasi amaçlarla huzur ve güveni bozmak, boykot, işgal, iş yavaşlatma ve grev gibi eylemlere katılmak, toplu göreve gelmemek ve bunu yapanları tahrik ve teşvik etmek veya yardımda bulunmak,\n-Siyasi partiye girmek(Madde 125)"),
  InfoCardItem("657_Info_155","Devlet Memurluğundan Çıkarma-2", "VERİLDİĞİ DURUMLAR;\n-Yasaklanmış yayını, afiş, broşür vs. basmak, çoğaltmak, kuruma asmak,\n-Özürsüz 1 yılda 20 gün gelmemek,\n-Savaş, olağanüstü hal ve afetlerde amirlerin verdiği görevi yapmamak,\n-Fiili tecavüzde bulunmak,\n-Yüz kızartıcı suç işlemek,\n-Yetkisiz gizli bilgi açıklamak,(Madde 125)"),
  InfoCardItem("657_Info_156","Devlet Memurluğundan Çıkarma-3", "VERİLDİĞİ DURUMLAR;\n-Siyasi ve ideolojik faaliyetlerden arananları kurumda gizlemek,\n-Yurt dışında Devletin itibarını düşürecek hareket ve eylemlerde bulunmak,\n-Atatürk Aleyhine İşlenen Suçlar Hakkındaki Kanuna aykırı fiiller işlemek,\n-Terör örgütüne yardım ve yataklık etmek ve örgüt propogandası yapmak memuriyetten çıkarma cezasını gerektirir.(Madde 125)"),
  InfoCardItem("657_Info_157","Genel Hükümler-1", "-Özlük dosyasından silinmeden aynı fiilden ceza alınırsa bir derece ağırı uygulanır.\n-Aynı derecede ceza gerektiren farklı fiilden kaynaklı cezalarda da bir derece ağırı verilir.\n-Ödül, başarı belgesi veya olumlu çalışmaları olanlara bir derece hafif ceza verilir.\n-Benzer ceza gerektiren eylemleri yapanlara aynı nevinden ceza verilebilir.(Madde 125)"),
  InfoCardItem("657_Info_158","Genel Hükümler-2", "-Öğrenim durumları itibariyle yükselebilecekleri kademenin sonuna gelen memurlar, kademe ilerlemesinin durdurulması cezası alırsa brüt aylıkları 1/4'ü-1/2'si kesilir.\n-Ceza tekrarında görevlerine son verilir.\n-Özel kanunların disiplin cezaları saklıdır.\n-Genel hükümler kapsamına giren fiillerde ayrıca ceza kovuşturması açılabilir.(Madde 125)"),
]),InfoCardModel("Devlet Memurları Kanunu-13", [
  InfoCardItem("657_Info_159","Cezayı Vermeye Yetkili Amir ve Kurul", "-Cezalar il disiplin kurulunun kararlarına dayanan hallerde valiler tarafından verilir.\n-Memurluktan çıkarma amir isteği ile kurumun yüksek disiplin kurulu tarafından verilir.\n-Disiplin kurulları cezayı onaylar veya reddeder.\n-Ret halinde atamaya yetkili amir 15 gün içinde başka ceza verebilir.(Madde 126)"),
  InfoCardItem("657_Info_160","Zamanaşımı", "-Uyarma, kınama, kademe ilerlemesi durdurma ve aylıktan kesme cezalarında 1 ay içinde disiplin soruşturmasına,\n-Memuriyetten çıkarma cezasında 6 ay içinde disiplin kovuşturmasına başlanmadığı takdirde disiplin cezası zamanaşımına uğrar.\n-Disiplin cezası gerektiren eyleme 2 yıl içinde ceza verilmezse zamanaşımına uğrar.(Madde 127)"),
  InfoCardItem("657_Info_161","Karar Süresi", "-Uyarma, kınama ve aylıktan kesme cezaları soruşturma tamamlandıktan 15 gün sonrasına kadar amir tarafından verilmelidir.\n-Kademe ilerlemesi durdurma cezası soruşturma dosyası 15 gün içinde disiplin kuruluna verilir.\n-Disiplin kurulu 30 gün içinde kararını bildirir.\n-Memurluktan çıkarma cezası 6 ay içinde karara bağlanır.(Madde 128)"),
  InfoCardItem("657_Info_162","Disiplin Kurulu ve Memur Hakları", "-Gerekli görüldüğü takdirde yüksek disiplin kurulu, ilgili memurun her türlü dosyasını inceleme hakkına sahiptir.\n-Ayrıca tanık dinleme ve dinletme hakkına da sahiptir.\n-Hakkında memurluktan çıkarma cezası istenen memur evrakları inceleme, tanık dinletme, sözlü veya yazılı savunma yapma hakkına sahiptir.(Madde 129)"),
  InfoCardItem("657_Info_163","Savunma Hakkı", "-Savunma alınmadan ceza verilemez.\n-7 günden az olmamak üzere verilen savunma süresinde savunma yapılmazsa haktan vazgeçmiş sayılır.(Madde 130)"),
  InfoCardItem("657_Info_164","Cezai Kovuşturma-Disiplin Kovuşturması", "-Aynı olaydan dolayı ceza mahkemesinde kovuşturmanın başlamış olması disiplin soruşturmasını geciktirmez.\n-Ceza kanununa göre ceza alması disiplin cezası almasına engel olmaz.\n-Mahkeme sonrası kesinleşmiş karar memurun bağlı olduğu bakanlık veya kurum veya kuruluşuna gönderilir.(Madde 131)"),
  InfoCardItem("657_Info_165","Uygulama-1", "-Disiplin cezası verildikten sonra hemen uygulanır.\n-Aylıktan kesme cezası bir sonraki aybaşında uygulanır.\n-Disiplin cezası üst disiplin amirine, memuriyetten çıkarma cezası Devlet Personel Başkanlığına ayrıca bildirilir.(Madde 132)"),
  InfoCardItem("657_Info_166","Uygulama-2", "Aylıktan kesme cezası ile tecziye edilenler 5 yıl, kademe ilerlemesinin durdurulması cezası ile tecziye edilenler 10 yıl boyunca daire başkanı kadrolarına, daire başkanı kadrosunun dengi ve daha üstü kadrolara, bölge ve il teşkilatlarının en üst yönetici kadrolarına, düzenleyici ve denetleyici kurumların başkanlık ve üyeliklerine, vali ve büyükelçi kadrolarına atanamazlar.(Madde 132)"),
  InfoCardItem("657_Info_167","Cezaların Silinmesi", "-Uyarma ve kınama cezasında 5 yıl sonra, diğer cezalarda 10 yıl sonra atamaya yetkili amirden cezaların özlük dosyasından silinmesi istenebilir.(Memuriyetten çıkarma hariç)\n-Bu istek kabul görürse özlük dosyasına işlenir.\n-Kademe ilerlemesi durdurulması cezasında  disiplin kurulu mütalalası alınarak işlemler uygulanır.(Madde 133)"),
  InfoCardItem("657_Info_168","Disiplin Kurulları/Amirleri", "-Kurum merkezinde Yüksek Disiplin Kurulu, Milli Eğitim müdürlüklerinde Disiplin Kurulu bulunur.\n-Bu kurulların usül ve esasları Cumhurbaşkanınca çıkarılacak yönetmelikle düzenlenir. (Madde 134)"),
  InfoCardItem("657_Info_169","İtiraz", "-Uyarma, kınama ve aylıktan kesme cezalarında Disiplin Kuruluna, kademe ilerlemesi durdurulması cezasında Yüksek Disiplin Kuruluna itiraz edilebilir.\n-İtiraz karardan sonra 7 gün içinde yapılır.\n-İtiraz yoksa ceza kesinleşir.\n-İtiraza 30 gün içinde cevap verilir.\n-İtiraz kabulünde disiplin amirleri cezayı hafifletir veya kaldırır.\n-Cezalarda idari yargı yolu açıktır.(Madde 135)"),
]),InfoCardModel("Devlet Memurları Kanunu-14", [
  InfoCardItem("657_Info_170","Görevden Uzaklaştırma", "-Memurun görev başında kalması sakıncalı olduğu durumlarda alınan tedbirdir.\n-Soruşturmaların herhangi bir aşamasında alınabilir.(Madde 137)"),
  InfoCardItem("657_Info_171","Yetkililer", "-Atamaya yetkili amir,\n-Bakanlık veya genel müdürlük müfettişleri,\n-İllerde valiler,\n-İlçelerde kaymakamlar,(İlçe idare şube başkanı için vali muvafakati şarttır)\n-Vali ve kaymakam tedbiri kuruma hemen bildirir.(Madde 138)"),
  InfoCardItem("657_Info_172","Amirin Sorumluluğu", "-Görevden uzaklaştırmayı izleyen 10 gün içinde soruşturma açılmalıdır.\n-Hemen soruşturmaya başlamayan, garez ve kin ile veya keyfi olarak görevden uzaklaştırma yapan amirler, hukuki, mali ve cezai sorumluluğa tabidir.(Madde 139)"),
  InfoCardItem("657_Info_173","Memurun Hak ve Sorumluluğu", "-Görevden uzaklaştırılan, gözaltına alınan veya tutuklanan memurun maaşının 2/3 ü ödenir.\n-Sosyal hak ve yardımları devam eder.\n-Memuriyetten çıkarma cezası verilmezse kesilen 1/3 maaş geri ödenir.\n-Derece ve kademe ilerlemesi hakları iade edilir.(Madde 141)"),
  InfoCardItem("657_Info_174","Tedbirin Kaldırılması", "-Soruşturma sonucunda memuriyetten çıkarma cezası veya cezai bir işlem uygulanmasına gerek kalmayan memur göreve iade edilir.\n-İade işlemi atamaya yetkili amir, vali veya kaymakamlarca yapılır.\n-Tedbiri kaldırmayan görevli hakkında hukuki/mali cezaya tabidir.(Madde 142)"),
  InfoCardItem("657_Info_175","Göreve Dönme", "-Memurluktan çıkarma harici ceza alanlar,\n-Yargılama men'i veya beraat kararı,\n-Hükümden önce gelen genel af,\n-Memurluğa engel olmayacak bir ceza ile cezası ertelenenler haklarındaki görevden uzaklaşma tedbiri kaldırılır.(Madde 143)"),
  InfoCardItem("657_Info_176","Amirin Takdiri", "Görevden uzaklaşma tedbirinde soruşturmaya konu fiilin göreve devam etmeye engel bir durumu olmadığı anlaşılırsa her zaman kaldırılabilir.(Madde 144)"),
  InfoCardItem("657_Info_177","Süre", "-Görevden uzaklaşma soruşturma sırasında en çok 3 ay devam edebilir.\n-Süre sonunda haklarında karar verilmeyen memurlar göreve döner.\n-Ceza kovuşturmasından dolayı görevden uzaklaştırılan memurun durumu yetkili amir tarafından 2 ayda bir kontrol edilerek göreve dönüp dönmemesine karar verilir ve yazılı olarak bildirilir.(Madde 145)"),
]),InfoCardModel("Devlet Memurları Kanunu-15", [
  InfoCardItem("657_Info_178","Mali Hükümler", "-Kanun, kararname, yönetmelik ve amirlerin tayin ettiği görevler için Kanunla saklanan haklar dışında ücret ödenmez ve yarar sağlanmaz.\n-Aylık, taban aylığı, kıdem aylığı, zam ve tazminatlar ile diğer ödeme brüt tutarı İş Kanunu gereği asgari ücretten az olamaz, olursa aradaki fark tazminat olarak ödenir.(Madde 146)"),
  InfoCardItem("657_Info_179","Zamlar", "-Çalışma şartları zor mesleklerde iş güçlüğü zammı,\n-Hayat ve sağlık tehlikesi için iş riski zammı,\n-Sayıştaya hesap veren saymana, vezneden sorumlu veznedara ve diğer görevlilere mali sorumluluk zammı,\n-Temin, görevde tutma ve istihdamında zorluk olanlara temininde güçlük zammı verilir.(Madde 152)"),
  InfoCardItem("657_Info_180","Tazminatlar", "-Özel Hizmet Tazminatı,\n-Eğitim Öğretim Tazminatı,\n-Din Hizmetleri Tazminatı,\n-Emniyet Hizmetleri Tazminatı,\n-Mülki İdare Amirliği Özel Hizmet Tazminatı,\n-Denetim Tazminatı,\n-Adalet Hizmetleri Tazminatı(Madde 152)"),
  InfoCardItem("657_Info_181","Hak Kazanma", "-Kurul raporlu hastalık izni,\n-Yatarak tedavi görülen hastalıklardaki tedavi süresince zam ve tazminatlar eksik ödenmez.\n-Doktor raporu 7 günü geçerse %25 eksik ödenir.\n-Sürekli görevle yurt dışına çıkanlara ödenmez.\n-Yurt içine dönerlerse ödenir.\n-Tazminatlar sadece damga vergisine tabidir.(Madde 152)"),
  InfoCardItem("657_Info_182","Adayların Aylıkları", "Girecekleri derecenin hak edecekleri kademe aylığını alırlar. Aday memurlara asaletleri tasdiklenene kadar kademe ilerlemesi uygulanmaz.(Madde 158)"),
  InfoCardItem("657_Info_183","Kademe İlerlemesinde Aylık", "Kademe ilerlemesinde memur bir ileri kademeye ait göstergeye tekabül eden aylığı alır(Madde 160)"),
  InfoCardItem("657_Info_184","Aylık Ödenme Zamanı", "-Memura aylığı peşin ödenir.\n-Emeklilik veya ölüm durumunda peşin ödenen aylık geri alınmaz.\n-(Madde 164)"),
  InfoCardItem("657_Info_185","Açıktan Atanmada Aylık", "-Göreve başladığı günden itibaren aylığa hak kazanır.\n-İlk aylık gün hesabı ile ay sonunda ödenir.(Madde 165)"),
  InfoCardItem("657_Info_186","Kademe İlerlemesinde Aylık", "İlerlemeye müstehak olduğu tarihi takip eden ay başından itibaren aynı derecenin bir ileri kademesine ait aylığa hak kazanır.(Madde 166)"),
  InfoCardItem("657_Info_187","Derece Değişikliğinde Aylık", "Derece yükselmesinde veya daha aşağı derecelere atamada takip eden ay başından itibaren kazandığı aylığı alır.(Madde 167)"),
  InfoCardItem("657_Info_188","Görev Yeri Değişikliğinde Aylık", "Görev yeri değişikliğini takip eden ay itibariyle aylık yeni kurumca düzenlenir. Eski kurumunda ödenen aylıklar ile herhangi bir hesaplaşma yapılmaz.(Madde 169)"),
  InfoCardItem("657_Info_190","İzin/Geçici Görevde Yer Değişikliği", "İzin veya geçici görev bitimine kadar memurun aylığı eski kurumu tarafında ödenir.(Madde 170)"),
  InfoCardItem("657_Info_191","Sayman Devir ve Aylık", "-Hesaplarını devir zorunlu devir süresi 7 gündür.\n-Devir aylık süresine rastlarsa eski kurumca ödenir.\n-Sayman mutemetleri için devir süresi 2 gündür.(Madde 171)"),
  InfoCardItem("657_Info_192","Vekalet Aylığı", "Yalnızca fiilen yapılan vekalet görevinin aylığı ödenir.(Madde 174)\n-Vekaleten atanan memur kadro derecesinin 1/3'ü açıktan atanan ise asgari ücretten az olmamak kaydıyla 2/3'ü verilir.\n-Vekalet aylığı ödenmesi için asilde aranan şartları taşımak zorunludur.(Madde 175)"),
  InfoCardItem("657_Info_193","Ders ve Konferans Ücretleri", "-Gündüz verilen dersler için 140, yarıyıl/yaz tatili ve hafta sonlarında 150 gösterge puan katsayısı çarpımı ile oluşan miktardan ödenir.\n-Özel eğitim, özel sınıf ve cezaevinde görevli öğretmenlere %25 zamlı ödenir.\n-DYK kapsamında %100 fazlası ödenir.\n-Konferans ücretleri her yıl Bütçe Kanununda gösterilir.(Madde 176)"),
  InfoCardItem("657_Info_194","Fazla Çalışma Ücreti", "-Salgın hastalık ve afet durumlarında,\n-İşçi çalıştıran kurumlarda mesai saatleri dışında işçiyle çalışma zorunluluğunda ücret ödenir.\n-Gerektiğinde ücret ödemeden fazla çalıştırılabilir.\n-Bu durumda her 8 saati için bir gün izin verilir.\n-Bu izinlerin en çok 10 günlük kısmı yıllık izinle birleştirilebilir.(Madde 178)"),
  InfoCardItem("657_Info_195","Seferde Aylık", "-Muvazzaflık dışında silah altına alınan memurların aylıkları yedek subay/astsubay/askeri memur için Milli Savunma Bakanlığı tarafından ödenir.\n-Kurumdan aldıkları maaş fazla ise farkı kurumları öder.\n-Rütbesiz memurların maaşları kurumları tarafından ödenir.(Madde 184)"),
]),InfoCardModel("Devlet Memurları Kanunu-16", [
  InfoCardItem("657_Info_196","Emeklilik Hakları", "Emeklilik ve malüllük hallerinde kendilerinin, ölümleri halinde dul ve yetimlerinin sahip oldukları haklardır.(Madde 187)"),
  InfoCardItem("657_Info_197","Hastalık ve Analık Sigortası-1", "-Hastalık, analık, görev kazası ve mesleki hastalık,\n-Eşler ve bakmakla yükümlü(ana, baba, çocuklar) olduklarındaki hastalık ve analık,\n-Emekli veya malüllük aylığı alanların veya yakınlarının hastalık ve analık,\n-Dul ve yetim aylığı alanların hastalık ve analık durumlarında sosyal sigorta yardımları sağlanır.(Madde 188)"),
  InfoCardItem("657_Info_198","Hastalık ve Analık Sigortası-2", "-Bu sigorta yardımları özel kanunlarla düzenlenir.\n-Bu sigortalardan tanınan hak ve yardımlar genel sigorta hak ve yardımlarından az olamaz.(Madde 188)"),
  InfoCardItem("657_Info_199","Yeniden İşe Alıştırma", "Malülen emekli olanlardan yeniden çalışabileceğine inanılanlar eski sınıflarında veya yeni sınıflarında işe başlayabilmek için alıştırmaya tabi tutulabilirler.(Madde 189)"),
  InfoCardItem("657_Info_200","Memurların Sosyal Tesisleri", "-Lüzum ve ihtiyaç görülen yerlerde çocuk bakımevi ve sosyal tesis kurulabilir.\n-Kuruluş ve işletme esasları Devlet Personel Başkanlığı ile Maliye ve Gümrük Bakanlığınca belirlenir.(Madde 191)"),
  InfoCardItem("657_Info_201","Memurlar İçin Konut Kredisi", "-Hizmet süresi 10 yılı veya fazla olanlara Toplu Konut Fonundan özel şartlarla ve öncelikle konut kredisi verilebilir.\n-Krediden faydalanma şartları ve ödeme şekli gibi hususlar Toplu Konut ve Kamu Ortaklığı İdaresi Başkanlığınca hazırlanacak yönetmelikle belirlenir.(Madde 192)"),
  InfoCardItem("657_Info_202","Memurlar İçin Konut", "Devlet memurlarının lüzum ve zaruret görülen yerlerle kiralık konut ihtiyaçları, Cumhurbaşkanınca onanacak programlar gereğince, genel ve katma bütçelere her yıl konulacak ödeneklerle tesis edilecek fondan karşılanır.(Madde 193)"),
  InfoCardItem("657_Info_203","Öğrenim Bursları ve Yurtları", "-Mahrumiyet çalışan memurların çocukları orta dereceli okul bulunmazsa pansiyon indiriminden yararlanarak yatılı okuyabilir.\n-İndirim %50 oaranındadır ve fark Devlet bütçesinden karşılanır.\n-Özel pansiyonlarda kalması istenirse aradaki ücret farkı aile tarafından karşılanır. (Madde 199)"),
  InfoCardItem("657_Info_204","Diğer Durumlar", "-Öğrenci sınıf tekrarı yaparsa aynı sınıf için ikinci sene indirimden faydalanamaz.(Madde 200)\n-Görev yeri değişen, ölen veya emekliye ayrılan memur çocuklarının indirimleri ders yılı sonuna kadar devam eder.\n-Kendi isteği ile atanan memur çocuklarının indirimi atandıkları tarihteki taksit dönemi sonuna kadar devam eder.(Madde 201)"),
  InfoCardItem("657_Info_205","Aile Yardımı", "-Evli memurlara verilir.\n-Çalışmayan eşler için 1500, çocukları için 250(0-6 yaş için 1 kat fazlası), ek gösterge katsayıları ile çarpımı kadardır.\n-Gösterge rakamlarını 3 kata kadar arttırma hakkı Cumhurbaşkanına aittir.\n-Dul memurlarda da aynı hükümler geçerlidir.\n-Üvey evlarlar için de geçerlidir.\n-Boşanmada yardımı alacak taraf mahkeme ile belirlenir.(Madde 202)"),
  InfoCardItem("657_Info_206","Aile Yardımı Ödeme Usülü", "-Her ay aylıkla birlikte verilir.\n-Karı-koca ikisi de memursa yalnız kocaya verilir.\n-Bu yardımlar hiçbir vergi kesintisine uğramaz ve borç için haczedilemez.(Madde 203)"),
  InfoCardItem("657_Info_207","Aile Yardımına Hak Kazanma", "Memur, eş için ödenen aile yardımı ödeneğine evlendiği; çocuk için ödenen yardıma da çocuğunun doğduğu tarihi takip eden ay başından itibaren hak kazanır.(Madde 204)"),
  InfoCardItem("657_Info_208","Aile Yardımı Hakkını Kaybetme", "-Eş için eşden boşanma veya eşin ölümünde,\n-Çocuk için çocuğun ölümü veya 206. maddedeki hükümler gerçekleşirse aile yardımı bir sonraki aylık alınırken verilmez. (Madde 205)"),
  InfoCardItem("657_Info_209","Aile Yardımı Verilmeyecek Haller", "-Evlenen çocuklar,\n-25 yaşını dolduran çocuklar(evlenmemiş kız çocuğu veya engelli çocuklar hariç),\n-Ticaret yapan, çalışan çocuklar(tatilde çalışan hariç),\n-Burs alan veya Devletçe okutulan çocuklar için yardım alınamaz.(Madde 206)"),
  InfoCardItem("657_Info_210","Ölüm Yardımı", "-Memur olmayan eşi veya aile yardımı alan çocuğu ölen memurlara ek göstergeler dahil en yüksek memur aylığında,\n-Memur ölürse bildirdiği kimseye,\n-Bildirdiği kimse yoksa eşine/çocuğuna,\n-Bunlar yoksa anne/babasına,\n-Bunlar yoksa kardeşlerine ek göstergeler dahil en yüksek memur aylığının 2 katı verilir.\n-Ölüm yardımı vergilerden muaftır ve haczedilemez.(Madde 208)"),
  InfoCardItem("657_Info_211","Cenaze Giderleri", "-Memurun ölümünde nakil dahil tüm masraflar kurumca ödenir.\n-Sürekli veya geçici görevle yurtdışında bulunanların eş, anne, baba ve çocuklarının cenaze masrafları da kurumca karşılanır.(Madde 210)"),
  InfoCardItem("657_Info_212","Giyecek Yardımı", "Devlet memurlarından hangilerinin ne şekilde giyecek yardımından faydalanacakları Maliye Bakanlığı ile Başbakanlık Devlet Personel Başkanlığının birlikte hazırlıyacakları bir yönetmelik ile tesbit olunur.(Madde 211)"),
  InfoCardItem("657_Info_213","Yiyecek Yardımı", "Devlet memurlarının hangi hallerde yiyecek yardımından ne şekilde faydalanacakları ve bu yardımın uygulanması ile ilgili esaslar Maliye Bakanlığı ile Başbakanlık Devlet Personel Başkanlığının birlikte hazırlıyacakları bir yönetmelik ile tesbit olunur. (Madde 212)"),
]),InfoCardModel("Devlet Memurları Kanunu-17", [
  InfoCardItem("657_Info_214","Hizmet İçi Eğitim", "Devlet Personel Başkanlığınca ilgili kurumlarla hazırlanan yönetmelikler dahilinde hizmet içi eğitimler yürütülür.(Madde 214)"),
  InfoCardItem("657_Info_215","Eğitim Birimi", "-Her kurumda eğitim faliyetlerini yürütmek, düzenlemek ve sonuçları değerlendirmek için Eğitim Birimi kurulur.\n-Birden fazla birim olan kurumlarda birimlerden biri Merkezi Eğitim Birimi adını alır.(Madde 215)"),
  InfoCardItem("657_Info_216","Eğitim Merkezi", "-Kendi eğitim ihtiyaçlarını karşılamak için kurumlar Eğitim Merkezi açabilir.\n-Cumhurbaşkanı kararı ile kurumlararası eğitim merkezlleri de açılabilir.\n-Kuruluş ve işleyişleri Devlet Personle Başkanlığı görüşü ile hazırlanacak yönetmelikle belirlenir.(Madde 216)"),
  InfoCardItem("657_Info_217","Eğitim Planı", "Devlet memurları eğitim planı Cumhurbaşkanı kararıyla yürürlüğe konur.(Madde 217)"),
  InfoCardItem("657_Info_218","Yurt Dışında Yetiştirme", "Devlet memurları, yabancı memleketlerin hizmetle ilgili kurumlarında veya yetiştirme - eğitim merkezlerinde de yetiştirilebilirler.(Madde 218)"),
  InfoCardItem("657_Info_219","Rapor Verme", "Eğitim çalışmalarının sonuçları her 6 aylık dönemin bitiminden en geç 1 ay sonra Devlet Personel Başkanlığına bir raporla bildirmek zorundandır.(Madde 219)"),
  InfoCardItem("657_Info_220","Koordinasyon ve Denetleme", "Devlet Personel Başkanlığı yurt içi ve yurt dışı eğitimlerin hazırlanmasında yol göstermekle ve koordine ve denetlemeden sorumludur.(Madde 220)"),
  InfoCardItem("657_Info_221","Memurluğa Eleman Yetiştirme", "-Kendi bünyelerinde mesleki eğitim ve öğretim yapılabilir.\n-Yurt içi ve yurt dışında öğrenim kurumlarında öğrenci okutabilir ve ihtisas yaptırabilirler.\n-(Madde 221)"),
  InfoCardItem("657_Info_222","Öğrenci Seçme", "Kurumlarca yurt içinde veya yurt dışında okutulacak öğrenciler yarışma sınavı ile seçilirler.(Madde 222)"),
  InfoCardItem("657_Info_223","Okutma Şartları", "Hangi alanlarda kurslar açılacağı, öğrencilerin okuyabileceği öğrenim dalları, öğrencilerin seçilme ve ayrılma usülleri, çalışmaların izlenip denetlenmesi ve öğrencinin geri çağrılması gibi usüller Devlet Planlama Teşkilatı, MEB ve Maliye Bakanlığı ile Devler Personel Başkanlığınca hazırlanan yönetmelikle yapılır.(Madde 223)"),
  InfoCardItem("657_Info_224","Masraflar", "Devlet hesabına okuma hakkını kaybedenlerden veya öğrenim kurumunu terk edenlerden kurumlarınca yapılmış masrafları faizi ile geri alınır.(Madde 223)"),
  InfoCardItem("657_Info_225","Mecburi Hizmet-1", "Öğretim kurumları ve dalları dışında Devlet tarafından okutulan öğrencilerin\n-Yurt içinde okutulanlar tatiller dahil öğrenim süresi kadar,\n-Yurt dışında okutulanlar tatiller dahil öğrenim sürelerinin iki katı kadar zorunlu hizmet yapar.(Madde 224)"),
  InfoCardItem("657_Info_226","Mecburi Hizmet-2", "3 ay veya daha fazla eğitim, staj veya bilgi arttırmak için yurt dışına gönderilen memurlar yurt dışında kaldıkları sürenin 2 katı kadar mecburi hizmetle yükümlüdür.\n-Mecburi hizmetle yükümlü olanlar yarışma sınavına tabi tutulmaksızın atanırlar.(Madde 224)"),
  InfoCardItem("657_Info_227","Mecburi Hizmet Yükümlülükleri-1", "-Mecburi hizmetle yükümlü olanlar en geç 2 ay içinde kurumlarına başvurmak zorundadur.\n-Başvuru tarihinden en geç 3 ay içinde atanamayanlardan yükümlülükleri kalkar.\n-Mecburi hizmet krediye dönüştürülmek istenirse süre istemin karara bağlandığı tarihten başlar.\n-Bu durumun oluşuna kendi sebep olan memur zararı tazmin eder.(Madde 225)"),
  InfoCardItem("657_Info_228","Mecburi Hizmet Yükümlülükleri-2", "-Başvuru yapmayan veya gerekli belgeleri tamamlamayanlar yol giderleri dahil tüm masrafları %50 faiziyle öder.\n-Atanıp mecburi hizmeti bitirmeyen veya memurluktan çıkarılanlar eksik kalan süreyi %50 faiziyle öder.\n-Cumhurbaşkanı kararıyla hizmetlerine gerek görülmeyenlerin mecburi hizmetleri krediye dönüştürülebilir.(Madde 225)"),
  InfoCardItem("657_Info_229","Mecburi Hizmet Yükümlülükleri-3", "-MEB tarafından okutulan yatılı öğrencilerin hizmetleri kurumlarda ihtiyaç yoksa kaldırılabilir.\n-Kurum ihtiyaçları bir aylık süre verilerek Resmi Gazetede ilan ile tespit olunur.\n-Askerlik süresi mecburi hizmetten sayılmaz.\n-Mecburi hizmette yasama organına seçilenlerin süreleri seçildikleri sürece ertelenir.(Madde 225)"),
  InfoCardItem("657_Info_230","Mecburi Hizmet Yükümlülükleri-3", "-(Madde 225)"),
  InfoCardItem("657_Info_231","Kamu Personeli Bilgi Sistemi", "-Devlet Personel Başkanlığı tüm personele ilişkin gerektiğinde kamu kurum ve kuruluşlarından bilgi ister.\n-Kamu kurum ve kuruluşları bu bilgileri vermekle yükümlüdür.\n-Atama, yer değiştirme, görevde yükselme vb hareketleri kurumlar Devlet Personel Başkanığına bildirmek zorundadır.(Madde 231)"),
]),
  ];

