class OgrenOgrenSurecInfoViewModel extends ChangeNotifier {
  List<InfoCardModel> get ogrenmeOgretmeSurec => _ogrenmeOgretmeSurec;

  List<InfoCardModel> _ogrenmeOgretmeSurec = [
    InfoCardModel("Öğrenme, Öğretme ve Eğitim Kavramları", [
      InfoCardItem("aa_eo_Info_2800","Öğrenme-1","-Yaşantı veya tekrar yoluyla bireylerde meydana gelene kalıcı davranış değişikliklerine öğrenme denir.\n-Öğrenmeler kalıcıdır.\n-Çevre önemli bir etkendir.\n-Öğrenmenin unutulması veya kaybolması beklenmez.\n-Olumlu yönde olması tercih edilse de olumsuz da olabilir.\n-Davranış değişikliği gerektirir.\n-Refleksif hareketler öğrenme kabul edilmez."),
      InfoCardItem("aa_eo_Info_281","Öğrenme-2","-Öğrenmenin kalıcı olması, gözlenebilir olması, yaşantı sonucu gerçekleşmesi ve değişimin yalnızca büyüme ve olgunlaşmaya bağlı olmaması gerekir."),
      InfoCardItem("aa_eo_Info_282","Psikoloji","-İnsan davranışlarını ve davranışlarının altında yatan sebepleri inceler.\n-Davranışlar bilinç dışı süreçlerde kayıtlı bazı olay ve olgular sebebiyle sergileniyor olabilir.\n-Bu olay ve olgular tespit edilerek bireyin olumlu yönde gelişmesi sağlanabilir."),
      InfoCardItem("aa_eo_Info_283","Öğrenme Psikolojisi","-Öğrenme ve psikolojide ortak unsur davranıştır.\n-Psikoloji biliminin öğrenme odaklı olması öğrenme psikolojisi kavramını ortaya çıkartır.\n-Öğrenmenin nasıl gerçekleştiğini, öğrenmeyi etkileyen faktörleri, öğrenmeler arası disiplinleri, öğrenmede bireysel farklılıkları ve öğrenmeyi oluşturan unsurları inceler."),
      InfoCardItem("aa_eo_Info_283_11","Öğretme","-Öğrenmenin gerçekleştirilmesi için öğrenciye rehberlik etme sürecine öğretme denir.\n-Süreç sonunda bireyden davranış değişikliği beklenir.\n-Bu davranış değişikliği süreci istendiktir.\n-Birey, davranışlarındaki değişimi kendi yaşantıları yoluyla gerçekleştirir."),
      InfoCardItem("aa_eo_Info_283_12","Öğretim","-Eğitim sürecinin okul içini kapsayan bölümüdür.\n-Okullarda yürütülen önceden planlanmış, sistematik ve uzmanlar tarafından verilen eğitim sürecidir.\n-Hedefler, süreç başlamadan belirlenmiştir.\n-Bu hedeflere ulaşmak için uygulanan programa süreç bitimine kadar sadık kalınır."),
      InfoCardItem("aa_eo_Info_283_13","Eğitim-1","-Bireyin davranışlarında kendi yaşantıları yoluyla, kasıtlı olarak istendik yönde davranış değişikliği meydana getirme sürecidir.\n-Bireyin kültürlenme süreci olarak da tanımlanır.\n-Eğitim birey doğduğu andan itibaren başlar; aile, okul ve çevre etkileşimiyle yaşam boyu devam eder."),
      InfoCardItem("aa_eo_Info_283_14","Eğitim-2","-Eğitim  ile birey bir yandan gelişimini sağlarken diğer yandan da yaşama hazırlanır.\n-Eğitim alan bireylerde, eğitim süreci sonucunda davranış değişikliği olması beklenir.\n-Bu davranış değişiklikleri istendik ve amaca yönelik olmalıdır. "),
    ]),InfoCardModel("Davranış Türleri", [
      InfoCardItem("aa_eo_Info_284","Davranış","-Dış dünyada gözlemlenebilen her türlü etkinliğe denir.\n\n-Davranış türleri;\n-Bilişsel Davranış,\n-Duyuşsal Davranış,\n-Psikomotor Davranış,\n-Öğrenilmiş Davranış,\n-İstendik Davranış,\n-İstenmedik Davranış,\n-Geçici Davranış,\n-Refleksif Davranış olabilir."),
      InfoCardItem("aa_eo_Info_285","Bilişsel davranış","-Zihinsel süreç gerektiren davranışlardır.\n-Eylemin yapılmasında sebep sonuç ilişkisi kurma, dikkat etme, hatırlama, yorum çıkartma ve hayal gücünü kullanma gibi zihinsel süreçler etkilidir."),
      InfoCardItem("aa_eo_Info_286","Duyuşsal davranış","-Duygular ön plandadır.\n-Sevinç, üzüntü, korku, heyecan gibi duyguların beyin süzgecinden geçerek davranışa dönüşmesidir."),
      InfoCardItem("aa_eo_Info_287","Psikomotor davranış","-El göz koordinasyonu gerektirir.\n-Yürüme, koşma, zıplama, kaldırma gibi fiziksel aktiviteler içerir."),
      InfoCardItem("aa_eo_Info_288","Öğrenilmiş davranış","-Davranış öğrenme ürünüdür.\n-Öğrenme ürünü olan davranışlar kalıcı olur."),
      InfoCardItem("aa_eo_Info_289","İstendik davranış","-Öğrenme planlı bir eğitim sürecinde gerçekleşir.\n-Gelişigüzel kültürlenme sonucu da öğrenme gerçekleşebilir."),
      InfoCardItem("aa_eo_Info_290","İstenmedik davranış","-Öğrenme hatalı eğitim sürecinde gerçekleşir.\n-Kötü kültürlenme de öğrenmeye sebep olmuş olabilir."),
      InfoCardItem("aa_eo_Info_291","Geçici davranış","-İlaç kullanımı ve alkol gibi durumlar sebebiyle ortaya çıkan davranışlardır.\n-Davranışlar kalıcı değil kısa sürelidir."),
      InfoCardItem("aa_eo_Info_292","Refleksif davranış","-Refleksif, içgüdüsel ve kontrol dışı ortaya çıkan davranışlardır."),

    ]),InfoCardModel("Öğrenmeyi Etkileyen Faktörler", [
      InfoCardItem("aa_eo_Info_293","Öğrenmeyi etkileyen faktörler","-Öğrenenle ilgili faktörler,\n-Öğrenme yöntemi ile ilgili faktörler,\n-Öğrenme malzemesi ile ilgili faktörler,"),
      InfoCardItem("aa_eo_Info_294","Öğrenenle ilgili faktörler","-Türe özgü hazır oluş,\n-Olgunlaşma,\n-Genel uyarılmışlık düzeyi,\n-Güdü,\n-Eski yaşantılar,\n-Dikkat"),
      InfoCardItem("aa_eo_Info_295","Türe özgü hazır oluş","-Bazı davranışların öğrenilebilmesi için gerekli biyolojik yeterlilikler bulunur.\n-Organizma bu donanıma sahip değilse davranış öğrenilemez."),
      InfoCardItem("aa_eo_Info_296","Olgunlaşma","-Bazı davranışların gerçekleştirilebilmesi için fiziksel ve zihinsel yeterliliğin olması gerekir.\n-Bunlar yaş ve zeka ile ilgili olabilir.\n-Ancak bu olgunlaşma sağlanırsa öğrenme gerçekleşir."),
      InfoCardItem("aa_eo_Info_297","Genel uyarılmışlık düzeyi","-Çevreden gelen uyarıcıların birey tarafından alınma düzeyidir.\n-Belirli bir düzeye kadar öğrenmeyi olumlu yönde etkiler.\n-Belirli bir düzey sonrası öğrenmeyi olumsuz etkiler.\n-Bir işe kendini verme düzeyidir.\n-Sorularda ortamın ısı-ışık ve gürültü durumu, öğrenenin yorgunluk, açlık-susuzluk ve hastalık durumu, öğrenenin yaşadığı korku, kaygı ve stres varsa cevap genel uyarılmışlık halidir."),
      InfoCardItem("aa_eo_Info_298","Güdülenme","-İhtiyaç sonucu ortaya çıkar.\n-Organizmayı davranışa sürükler.\n-İlgi, istek, ihtiyaç vs. gibi içsel nedenler varsa içsel güdüdür.\n-Beğenilmek, yüksek not almak, cezadan kaçmak vs. gibi dışsal nedenler varsa dışsal güdüdür.\n-Birincil güdü; doğuştan gelen, hayati önemi olan ve öğrenilmemiş güdülerdir. (uyku, sevgi, cinsellik, açlık-susuzluk)\n-İkincil güdüler; sosyal olarak öğrenilen ve sonradan kazanılan güdülerdir. (statü kazanma, not alma, uzman öğretmenlik sınavını kazanmak)"),
      InfoCardItem("aa_eo_Info_299","Eski yaşantılar-1","-Bireyin önceki öğrenmelerinin yeni öğrenmelerine olan etkisidir.\n-Öğrenmeyi olumlu veya olumsuz yönde etkileyebilir.\n-Öğrenme aşamasında olumlu veya olumsuz etkilenme varsa transfer(aktarma), öğrenmeyi unutturma varsa ket vurma vardır.\n-Yani toplama işlemini bilen birinin çıkarmayı kolay öğrenmesi transferdir.\n-Yeni okula tayini çıkan öğretmenin soranlara hala eski okulunun adını vermesi ket vurmadır. (unutma veya hatırlayamama olmalıdır.)\n-Detaylar sonraki kartta işlenecektir."),
      InfoCardItem("aa_eo_Info_299_11","Eski yaşantılar-2","Transfer Olumlu Transfer veya Olumsuz Transfer(Alışkanlık Çatışması) olabilir.\n-Olumlu Aktarma: Öğrenmelerin birbirini olumlu etkilemesidir. (Örn: İngilizce bilmesi sayesinde Almanca'yı kolay öğrenme olumlu aktarmadır).\n-Olumsuz Aktarma (Alışkanlık Çatışması): Önceki öğrenmenin sonraki öğrenmeyi zorlaştırması durumudur. (Örn: İngilizce bildiği için Almanca kelimelerin telaffuzunu tam yapamamak).\n-Ket vurma bir sonraki kartta işlenecektir. "),
      InfoCardItem("aa_eo_Info_299_22","Eski yaşantılar-3","-Öğrenme gerçekleşir, sonrasında unutma veya hatırlayamama varsa ket vurmadır.\n-Öğretmenin yeni bir ile tayini çıkmıştır. Okul isimlerini karıştırmaktadır. Eğer;\n\n-Eski okulunun ismini hatırlayıp yeni okulunun ismini unutursa ileriye ket vurma,\n-Yeni okulunun ismini hatırlayıp eski okulunun ismini unutursa geriye ket vurma olarak adlandırılır."),
      InfoCardItem("aa_eo_Info_299_22","Aktarma-Ket vurma","-Sorularda öğrenme süreci geçerse veya ima edilirse AKTARMA işaretlenecektir.\n-Unutma, unutturma, karıştırma vs. geçiyorsa KET VURMA işaretlenecektir."),
      InfoCardItem("aa_eo_Info_300","Dikkat","-Öğrenmeyi etkileyen en temel faktörlerdendir.\n-Birey kendi ihtiyaçlarından dolayı dikkatli ise istemlidir.\n-Dikkat çevresel güçlü uyarıcılar sebebiyle oluyorsa istemsiz dikkattir.\n-Aynı anda birçok farklı işe bakabiliyorsa bölünmüş dikkattir."),
      InfoCardItem("aa_eo_Info_301","Öğrenme yöntemi ile ilgili faktörler","-Konunun yapısı,\n-Zaman kullanımı,\n-Dönüt,\n-Etkin katılım"),
      InfoCardItem("aa_eo_Info_302","Konunun Yapısı","-Konunun nasıl öğrenileceğini kapsar.\n-Bütün halinde veya parçalara bölünerek çalışılabilir.\n-En etkili yöntem bütün-parça-bütün yöntemidir.\n-Bu yöntemde konu önce bütünüyle ele alınır.\n-Sonra parçalara bölünerek çalışılır.\n-Daha sonra tekrar bütün olarak ele alınır."),
      InfoCardItem("aa_eo_Info_303","Zaman kullanımı","Konunun toplu olarak veya zamansal aralıklarla çalışılması öğrenme yöntemi ile ilgilidir."),
      InfoCardItem("aa_eo_Info_304","Dönüt","-Öğrenmenin ne düzeyde gerçekleştiği hakkında öğrenene bilgi verme sürecidir.\n-Dönüt, öğrenene sık sık verilmelidir.\n-Dönüt hemen verilirse, öğrenmedeki aksaklıklar daha hızlı giderilebilir."),
      InfoCardItem("aa_eo_Info_305","Etkin katılım","-Öğrenenin öğrenilecek konu için süreçte tüm dikkatiyle var olmasıdır.\n-Katılım arttıkça öğrenme artar."),
    ]),
    InfoCardModel("Öğrenme Malzemesi İle İlgili Faktörler", [
    InfoCardItem("aa_eo_Info_306","Öğrenme malzemesi ile ilgili faktörler","-Algısal ayırt edilebilirlik,\n-Anlamsal çağrışım,\n-Kavramsal gruplandırma,\n-Çağrışımsal gruplandırma,\n-Telaffuz edilebilirlik"),
    InfoCardItem("aa_eo_Info_307","Algısal ayırt edilebilirlik","-Uyarıcının o an çevrede var olan tüm uyarıcılardan daha güçlü olması durumudur.\n-Bu durumda odak noktası güçlü uyarıcıdadır.\n-Öğrenmeyi direkt olarak etkiler."),
    InfoCardItem("aa_eo_Info_308","Anlamsal çağrışım","-Öğrenme sırasında geçmiş öğrenmelerle bağ kurmaktır.\n-Yani yeni öğrenmeler önceki öğrenmelerden esinlenilerek gerçekleştirilir."),
    InfoCardItem("aa_eo_Info_309","Kavramsal gruplandırma","-Konuların birbiri ile ilişkilerinin haritalama ile ortaya çıkarılmasıdır.\n-Hiyerarşik düzende ilişkilendirme yapılır.\n-Kavram haritası mantığı kullanılır."),
    InfoCardItem("aa_eo_Info_310","Çağrışımsal gruplandırma","-İçeriğin deneyimlerle ve bilgilerle ilişkilendirilmesidir.\n-Çağrışım sayısı ne kadar artarsa, hatırlama düzeyi o kadar artar."),
    InfoCardItem("aa_eo_Info_311","Telaffuz edilebilirlik","-İçeriğin dilsel gelişime uygun olarak verilmesidir.\n-Konu uygun ve anlaşılır örneklerle açıklanır."),
]),InfoCardModel("Öğrenme Kuramlar", [
      InfoCardItem("aa_ogrenogretsurec_info_1", "Öğrenme Öğretme Kuramları", "-Davranışçı Kuramlar,\n-Bilişsel Kuramlar,\n-Duyuşsal Kuramlar,\n-Nörofizyolojik Kuramlar"),
      InfoCardItem("aa_ogrenogretsurec_info_2", "Davranışçı Kuramlar-1", "-Öğrenme davranış ile uyarıcı arasındaki etkileşim ile meydana gelir.\n-Yani uyarıcı ve davranış arasında bağ kurulur.\n-Davranış değişikliği pekiştirme yolu ile elde edilir.\n-Öğrenmede çevresel etmenler etkilidir.\n-Bu yüzden çevre değiştikçe öğrenme değişir."),
      InfoCardItem("aa_ogrenogretsurec_info_3", "Davranışçı Kuramlar-2", "-Pragmatist, yani faydaya dayalıdır.\n-Önceki öğrenmeler sonraki öğrenmeleri etkiler.\n-Determinizm(nedensellik) temel ilkedir.\n-Yani nedenler değiştikçe sonuçlar değişir.\n-Davranış doğru ise pekiştirme gerekir.\n-Dolayısıyla pekiştirilen davranış öğrenilir."),
      InfoCardItem("aa_ogrenogretsurec_info_4", "Davranışçı Kuramlar-3", "-Öğrenme yalnızca gözlenebilen ve ölçülebilen davranışları konu edinir.\n-Öğrenme tekrar ve yaparak yaşayarak öğrenmeye dayalıdır.\n-Öğrenme sonucunda yanlış davranış kazanılmışsa bu koşullanmanın yanlış olmasından kaynaklanmış demektir.\n-İçgüdü gözlenemediği için kabul edilmezken dışsal faktörler öğrenmede kabul edilir."),
      InfoCardItem("aa_ogrenogretsurec_info_5", "Davranışçı Kuramlar-4", "-Klasik koşullanma,\n-Edimsel koşullanma\n-Watson ve Bitişiklik kuramı"),
       ]),
    InfoCardModel("Klasik Koşullanma", [
      InfoCardItem("aa_ogrenogretsurec_info_6","Klasik Koşullanma", "-Pavlov'un köpek deneyi olarak bilinir.\n-Zil sesinden sonra et verilen köpeğin, tekrar yoluyla zil sesini duyduğunda et geleceğini düşünerek salya akıtması klasik koşullanma örneğidir.\n-Bu deneyde zil ve et salya tepkisine sebep olur."),
      InfoCardItem("aa_ogrenogretsurec_info_7","Deney üzerinden temel kavramlar-1", "-Koşulsuz Uyarıcı: Doğuştan var olan ve tüm canlılarda olan uyarıcıdır. (Et tüm köpekler için doğal uyarıcıdır.)\n-Koşulsuz tepki: Tüm canlıların aynı uyarıcıya verdikleri ortak tepkidir. (Et gören köpeğin salyası akar.)\n-Nötr uyarıcı: Canlı için henüz anlam ifade etmeyen uyarıcı. (Zil sesini duyan köpekte öğrenme yoksa sese tepki vermez.)"),
      InfoCardItem("aa_ogrenogretsurec_info_8","Deney üzerinden temel kavramlar-2", "-Pekiştireç: Koşulsuz uyarıcının görevidir. (Yani et, köpeğin doğru davranışını pekiştirmek için kullanılır.)\n-Birincil Pekiştireç: Koşulsuz tepkiyi oluşturur. (Et, köpekte doğal tepki oluşturur ve birincil pekiştireçtir.)\n-İkincil Pekiştireç: Koşullu tepkiyi oluşturur. (Zil, zil sonunda salyayı oluşturur.)"),
      InfoCardItem("aa_ogrenogretsurec_info_9","Deney üzerinden temel kavramlar-3", "-Bitişiklik: Nötr uyarıcı (zil) ile koşulsuz uyarıcının (et) kısa süre aralıklarıyla verilmesidir. Bu davranışı oluşturmak için gereklidir.\n\n-Başlangıçta zil nötr uyarıcı, salya koşulsuz tepki iken, deney sonunda zil koşullu uyarıcıya dönüşürken salya tepkisi koşullu tepkiye dönüşmüştür."),
      InfoCardItem("aa_ogrenogretsurec_info_10", "Koşullanma Örnekleri", "-Bir uyaranın koşullu olması için daha önce deneyimlenmiş olması gerekir. Yani doktor ile ilgili kötü anısı olan kişinin doktor görünce tedirgin olması koşullu uyaran koşullu tepkidir. Bazı örnekler;\n\n-Kan görünce bayılma, limon görünce ağzın sulanması, Köpek sesi duyunca korkma, ani fren sesi duyunca ürperme koşullu uyarıcılara verilen koşullu tepki örnekleridir."),
      InfoCardItem("aa_ogrenogretsurec_info_11", "Örneklerle Koşullanma İlkeleri-1", "-Bitişiklik: Koşullu-koşulsuz uyarıcının peş peşe verilmesi. (Zil çalıp et verme)\n-Habercilik: Uyarandan sonra yeni uyaran geleceği veya uyaranın kesileceğinin haber verilmesidir. (Zilin etin habercisi olması)\n-Pekiştirme: Davranışın ödüllendirilmesidir. (Zil çalınca et verilmesi salya davranışını pekiştirir)"),
      InfoCardItem("aa_ogrenogretsurec_info_12", "Örneklerle Koşullanma İlkeleri-2", "-Alışma: Sürekli karşılaşılan durumun önemini yitirmesidir. (Zil sesinden sonra 3 parça et verilirken, et parça sayısı 1 e düşerse salya azalır.)\n-Sönme: Koşulsuz uyarıcı yoksa davranışın bitmesidir. (Zil sesinden sonra et verilmezse davranış söner)\n-Kendiliğinden Geri gelme: Sönen davranışın tekrar gerçekleşmesidir. (Zil sesine tepki vermeyen köpeğin bir süre sonra tepki vermesi)"),
      InfoCardItem("aa_ogrenogretsurec_info_13", "Örneklerle Koşullanma İlkeleri-3", "-Ayırt etme: Sadece belirli bir uyarıcıya belirli tepkinin verilmesidir. (Zil sesine salya akıtan köpeğin düdük sesine akıtmaması)\n-Genelleme: Tepkinin benzer uyarıcılara da verilmesidir. (Zil sesinde salyası akan köpeğin düdük sesinde de salyasının akması)"),
      InfoCardItem("aa_ogrenogretsurec_info_14", "Örneklerle Koşullanma İlkeleri-4", "-Geçici koşullanma: Aynı aralıklarla verilen uyarıcıya geçici koşullanma oluşmasıdır. (30 dakika ara ile et verilen köpeğin et verilmese de salyasının akması)\n-Üst düzey koşullanma: Tepki verilen uyarıcının yanına yeni uyarıcılar eklenmesiyle, yeni eklenen uyarıcılara da tepki verilmesidir.\n-Genelleme, benzer uyarıcılara (zil-düdük) aynı tepkinin verilmesi iken, üst düzey koşullanma alakasız uyarıcılara (zil-kapı) da tepki eklenmesidir."),
         ]),
    InfoCardModel("Koşullanma İlkeleri", [
      InfoCardItem("aa_ogrenogretsurec_info_15", "Garcia Etkisi", "-Bir olayın arada zaman farkı olan başka bir olaya bağlanması sonucunda yaşanan zihinsel ilişki sürecidir.\n-Örneğin ellerinin üzerinde kızarıklık oluşan bir kişinin sebebini 3 gün önce gittiği kır gezisine bağlamasıdır. Bu sebeple tekrar kır gezisine gitmez."),
      InfoCardItem("aa_ogrenogretsurec_info_16", "Duyusal Ön Şartlanma", "-Bir uyarıcıya koşullanan organizmanın, koşullanmadan önce bu uyarıcı ile ilişkilendirdiği şeylere de uyarıcıya verdiği tepkiyi vermesidir.\n-Döner yedikten sonra midesi bulanan ve hastanede tedavi gören kişinin, olaydan sonra dönerin yanındaki ayranı gördüğünde de midesinin bulanmasıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_17", "Öğrenilmiş Çaresizlik", "-Artık sonucun değişmeyeceğine inanılması sürecidir.\n-Doğruluk payı yoktur.\n-Araba kullanmakta zorlanan kişinin, ne kadar çalışırsam çalışayım ben asla araba kullanamam düşüncesine kapılmasıdır.\n-Öğrenilmiş çaresizlik sonucun ne olduğuna bakmadan kabul edilmesi sürecidir."),
      InfoCardItem("aa_ogrenogretsurec_info_18", "Kendini Gerçekleştiren Kehanet", "-Beklediği ile karşılaşılması durumudur.\n-Araba kullanamayacağına inanan bir kişinin, çalışmaktan uzak durarak araba kullanmayı öğrenememesi durumunda, ben zaten öğrenemeyeceğimi biliyordum algısına kapılmasıdır.\n-Olumsuz düşüncenin onayı alınmış olur."),
      InfoCardItem("aa_ogrenogretsurec_info_19", "Davranışı Kaldırma Süreci", "Öğrenilen bir davranışın ortadan kaldırılması için aşağıdaki yöntemler kullanılır.\n-Karşıt koşullanma\n-Sistematik duyarsızlaşma\n-Sönme\n-İtici uyarıcıya koşullanma"),
      InfoCardItem("aa_ogrenogretsurec_info_20", "Karşıt koşullanma", "-Uyarıcıya verilen tepkinin tersine çevrilmesi sürecidir.\n-Tepki değişmesi bulunur.\n-Kadayıf yedikten sonra midesi bulanan bir kişi tekrar kadayıf yemek istememektedir. Ancak annesinin yaptığı kadayıfı yemiş ve çok beğenmiştir. Kadayıfa olan olumsuz duyguları değişerek sevmeye başlamıştır."),
      InfoCardItem("aa_ogrenogretsurec_info_21", "Sistematik duyarsızlaşma", "-Uyaran ile çok fazla karşılaşılması sonucunda tepki verilmemeye başlanmasıdır.\n-Topluluk önünde konuşamayan öğrencinin, küçük gruplardan başlanarak alıştırılması sürecidir.\n-Bu durumda aşamalılık bulunmaktadır.\n-Davranışın değişmesi için zamana ihtiyaç duyulur."),
      InfoCardItem("aa_ogrenogretsurec_info_22", "Sönme", "-Davranış pekiştirilmeyerek veya görmezden gelinerek ortadan kalkması beklenir.\n-Gereksiz yere sürekli parmak kaldıran öğrenciye öğretmenin söz hakkı vermemesi ile parmak kaldırma davranışının ortadan kalkması sürecidir.\n-Alışma ve sönme karıştırılabilir.\n-Temel fark alışmada uyarıcı şiddeti sabitlenir, sönmede, sönme öncesi şiddet artar."),
      InfoCardItem("aa_ogrenogretsurec_info_23", "İtici uyarıcıya koşullanma", "-Amaç uyarıcının ilgi çekiciliğini azaltmaktır.\n-İstenen uyarıcıya istenmeyen uyarıcı eşlik eder.\n-Organizma durumdan öncesinde haberdar değildir.\n-Örneğin asitli içecek içmemesi gereken bir çocuğun içeceğine karabiber konularak içmesinin engellenmesi."),
      ]),
      InfoCardModel("Edimsel Koşullanma", [
      InfoCardItem("aa_ogrenogretsurec_info_24", "Edimsel Koşullanma", "-Skinner tarafından ortaya konulmuştur.\n-Ödüle götüren veya cezadan kurtaran tepkinin öğrenilmesidir.\n-Skinner davranış türlerini tepkisel ve edimsel olmak üzere ikiye ayırır.\n-Ancak Skinner tepkisel davranışlar yerine edimsel davranışlar üzerinde durmuştur.\n-Şimdi tepkisel ve edimsel davranışlara daha yakından bakalım."),
      InfoCardItem("aa_ogrenogretsurec_info_25", "Tepkisel Davranışlar", "-Organizmanın doğasında olan refleksif davranışlardır.\n-Önce kokusunu duyup sonra yediği yemeği çok beğenen bir kişinin, yemeğin kokusunu aldığında ağzının sulanması örnektir.\n-Davranışlar koşulsuz uyarıcı ile ortaya çıkar.\n-Ortaya çıkan davranışlar da koşulsuz tepkilerdir."),
      InfoCardItem("aa_ogrenogretsurec_info_26", "Edimsel Davranışlar", "-Sonuçları kontrol edilebilen davranışlardır.\n-Davranışın devamlılığı organizmanın süreçten ve sonuçtan memnuniyet duyması ile ilişkilendirilir.\n-Örneğin tatile gittiğimiz bir yerden memnun kalırsak, aynı yere tekrar tatile gitmek bizi mutlu eder.\n-Derste doğru cevap veren öğrenciye artı verilmesi öğrencinin derste sorulara cevap vermesini pekiştirir."),
      InfoCardItem("aa_ogrenogretsurec_info_27", "Skinner'ın Deneyi-1", "-Aç bırakılan bir fare, düzenek hazırlanmış bir kaba bırakılır.\n-Dokulduğunda peynir düşüren bir manivela, elektrik şoku ve istenildiğinde yakılabilen bir ışık bulunur.\n-Fare tesadüfen manivelaya dokunur ve peynir düşer.\n-Benzer davranışların tekrarında manivela ile peynir arasında bağlantı kurmayı öğrenir.\n-Bu işlem hemen gerçekleşmemiş aşama aşama öğrenilmiştir."),
      InfoCardItem("aa_ogrenogretsurec_info_28", "Skinner'ın Deneyi-2", "-Skinner aynı fareye başka bir deney daha yapmıştır.\n-Öncesinde butona bastığında peynir alan fareye bu kez butona bastığında elektrik şoku verilmiştir.\n-Artık ceza ile sonuçlanan butona basma davranışı ortadan kalkmıştır."),
    ]),
    InfoCardModel("Edimsel Koşullanma Temel Kavramlar-1", [
      InfoCardItem("aa_ogrenogretsurec_info_29", "Temel Kavramlar-1", "Edimsel koşullanmaya ilişkin temel kavramlar;\n-Alışma,\n-Sönme,\n-Kendiliğinden geri gelme,\n-Ayırt etme,\n-Genelleme\n-Kademeli yaklaşma,\n-Batıl Davranış..."),
      InfoCardItem("aa_ogrenogretsurec_info_30", "Temel Kavramlar-2", "-Premack ilkesi,\n-Yoksunluk,\n-Alışkanlık kazanma,\n-Programlanmış öğrenme,\n-Zincirleme,\n-Ayırt edici uyarıcı"),
      InfoCardItem("aa_ogrenogretsurec_info_31", "Alışma", "-Pekiştireç sürekli kullanılırsa etkisi azalır.\n-Bu süreçten sonra pekiştirmenin devam etmesi bir anlam ifade etmemektedir.\n-Ödevlerini yaptıktan sonra bilgisayar oyunu oynamasına müsade edilen bir çocuğun, aynı oyunu oynamaktan sıkılması sebebiyle oyun ödülüne olumlu tepki vermemesidir."),
      InfoCardItem("aa_ogrenogretsurec_info_32", "Sönme", "-Pekiştirilmeyen davranış söner.\n-Bu durumun aynısı klasik koşullanmada da mevcuttu.\n-Aradaki fark davranışın pekiştirilmemesini ani artışın takip etmesidir.\n-Bu durum sönme patlaması olarak adlandırılır.\n-Telefon istediği için ağlayan çocuğa telefon verilmezse ağlaması şiddetlenir. Yine verilmezse ağlama davranışı söner."),
      InfoCardItem("aa_ogrenogretsurec_info_33", "Kendiliğinden geri gelme", "-Davranışlar pekiştirmemeye veya alışmaya bağlı olarak sönebilir.\n-Sönen bir tepki bir süre sonra yeniden ortaya çıkabilir.\n-Bu duruma kendiliğinden geri gelme denir.\n-Okula top getirdiği için ceza alan çocuk bir süre okula top getirmemiştir. Ancak sonra getirmeye devam etmiştir."),
      InfoCardItem("aa_ogrenogretsurec_info_34", "Ayırt etme", "-Tepki verilecek durumun seçilmesidir.\n-Yani bir uyarıcıya verilen tepki başka bir uyarıcıya verilmemektedir.\n-Sınıfta espri yapan öğrenciye bir öğretmeni gülerek karşılık vermiş diğer aynı espriye kaşlarını çatarak karşılık vermiştir. Bu öğrenci devamında sadece gülen öğretmenine espri yapmaya devam etmiştir."),
      InfoCardItem("aa_ogrenogretsurec_info_35", "Genelleme", "-Bir tepkinin uyarıcıya benzeyen diğer uyarıcılar için de verilmesidir.\n-Ses tonu babasına benzeyen öğretmenini babasına benzetmesi genellemedir.\n-Bazen öğrenmeler de genellenebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_36", "Kademeli yaklaşma", "-İstenilen davranışa ulaşmak için küçük adımlardan başlanır.\n-Süreç içinde davranış pekiştirilerek kazandırılır.\n-Uzun atlama öğretilmek istenen öğrenci kısa mesafelerden başlanarak çalıştırılır. Her doğru atlaması pekiştirilir ve atlama uzuluğu arttırılır. Böylece süreç sonunda uzun atlamada istenilen mesafe yakalanır. Temel nokta her seferinde pekiştirmektir."),
      ]),
    InfoCardModel("Edimsel Koşullanma Temel Kavramlar-2", [
        InfoCardItem("aa_ogrenogretsurec_info_37", "Batıl Davranış", "-İlişkisi olmayan bir durumun, ilişkisinin varlığına organizmanın kendini inandırmasıdır.\n-Sınava abisinin kalemiyle giren bir öğrenci yüksek not alır. Aldığı yüksek notu kaleme bağlar. Diğer sınavlarda da bu kalemi kullanır."),
      InfoCardItem("aa_ogrenogretsurec_info_38", "Premack ilkesi", "-Babaanne kuralı olarak geçer.\n-Çok istenilen davranışın az istenilen ile birlikte gerçekleştirilmesidir.\n-Bilgisayarda oyun oynamak isteyen bir öğrencinin ödevlerini yapması koşuluyla bilgisayar oyunu oynamasına izin verilmesi örnek olarak gösterilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_39", "Yoksunluk", "-İstenilen davranış sergilenmeden önce pekiştirecin ortadan kaldırılması performansın arttırılmasına sebep olur.\n-Öğrenci bilgisayar oyunu oynamak ister. Ancak ödevleri bitmediği için ailesi müsade etmez. Süreç sonunda bilgisayar oynayacağını bilen çocuk ödevlerini bitirmek için daha fazla gayret gösterir."),
      InfoCardItem("aa_ogrenogretsurec_info_40", "Alışkanlık kazanma", "-Organizma için rutin hale gelen etkinliklerdir.\n-Öğrencinin her bildiği soruya artı verilir. Bu başlangıçta ideal bir yöntemdir. Ancak süreç ilerledikçe, farklı pekiştireçler ile desteklenmezse öğrencide alışkanlık kazanma olur ve pekiştireç anlamını kaybeder."),
      InfoCardItem("aa_ogrenogretsurec_info_41", "Programlanmış öğrenme", "-Adım adım gerçekleşen öğrenme sürecidir.\n-Basit öğrenmeler ile başlayan süreç daha karmaşık öğrenmeler ile devam eder.\n-Bilinenden bilinmeyene metodu kullanılır.\n-Bu metotlar sayesinde öğrenci öğrenme sürecinde aktiftir.\n-Geri bildirim önemlidir.\n-Doğru davranış hemen pekiştirilmelidir.\n-Öğrenmelerde bireysel hız farklılıklara sebep olur."),
      InfoCardItem("aa_ogrenogretsurec_info_42", "Zincirleme", "-Aşamalılık söz konusudur.\n-Davranış bitmeden diğer davranışa geçilmez.\n-Davranış tamamlandıktan sonra pekiştireç gelir.\n-Aşamalar sıra ile yapılır ve atlama yapılmaz.\n-Araba kullanmak, çay demlemek örnek olarak verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_43", "Ayırt edici uyarıcı", "-Davranışın sürekli belirli bir davranış veya ortamda iken pekiştirilmesidir.\n-Önemli olan uyarıcıdır.\n-Bir uyarıcı farklı durumları tetikleyebilir.\n-Pavlov'un deneyindeki zile benzer.\n-İki farklı derste espri yapan öğrenciye bir öğretmeni gülmüş, diğeri gülmemiştir. Gülen öğretmenin dersinde öğrenci espri yapmaya devam eder. Gülen öğretmen ayırt edici uyarıcıdır."),
    ]),
    InfoCardModel("Pekiştireç Türleri-1", [
      InfoCardItem("aa_ogrenogretsurec_info_44", "Pekiştireç", "-Davranışın hemen arkasından verilen, organizma için haz kaynağı olan ve davranışın devamlılığını sağlayan uyarıcılardır.\n-Yapılan işlem pekiştirmedir.\n-Örneğin soruyu bilen öğrenciye artı vermek bir pekiştirmedir. Artı ise pekiştireçtir.\n-Dereceye giren öğrencilere kupa vermek bir pekiştirme iken kupa pekiştireçtir.\n-Pekiştirme ve pekiştireç olumlu ve olumsuz olmak üzere 2 çeşittir."),
      InfoCardItem("aa_ogrenogretsurec_info_45", "Olumlu Pekiştireç", "-Organizmaya haz veren, verildiğinde davranışın devamlılığını sağlayan pekiştireçtir.\n-Kendi içerisinde 2'ye ayrılır.\n\nBunlar;\n-Birincil(koşulsuz) olumlu pekiştireç,\n-İkincil(koşullu) olumlu pekiştireçtir."),
      InfoCardItem("aa_ogrenogretsurec_info_46", "Birincil(koşulsuz) olumlu pekiştireç", "-Yaşamsal duygulara hitap eden, yiyecek, içecek ve sevgi gibi biyolojik gereksinimleri harekete geçiren, insanı esasen fiziki yönden güdüleyen pekiştireçlerdir.\n-Pekiştirme doğal yolla gerçekleşir.\n-Örneğin 100 tam puan alan öğrenciye tost ısmarlama."),
      InfoCardItem("aa_ogrenogretsurec_info_47", "İkincil(koşullu) olumlu pekiştireç", "-Nötr(organizma için anlamı olmayan) bir uyarıcının, birincil (koşulsuz) pekiştireç ile ilişkilenmesiyle pekiştirme özelliği kazanmasına denir.\n-Örnek: Defteri düzenli olan öğrenciye yıldız vermek"),
      InfoCardItem("aa_ogrenogretsurec_info_48", "Olumsuz Pekiştireç-1", "-Ortamdan çekilen uyarıcı sayesinde istenilen davranışın yapılması sağlanır.\n-Organizmanın kaçtığı bir uyarıcı ortamdan çekilir.\n-Ödevlerini bitiren öğrencinin patlamış topunun yerine sağlam olanının alınması örnektir.\n-Bu örnekte öğrenci için olumsuz durum patlak toptu. Bu ortadan kaldırılarak yeni top ile değiştirildi. Ödev bitirme davranışına olumlu katkı sağlayacaktır."),
      InfoCardItem("aa_ogrenogretsurec_info_49", "Olumsuz Pekiştireç-2", "-Kendi içerisinde 2'ye ayrılır.\n\nBunlar;\n-Birincil(koşulsuz) olumsuz pekiştireç,\n-İkincil(koşullu) olumsuz pekiştireçtir."),
      InfoCardItem("aa_ogrenogretsurec_info_50", "Birincil(koşulsuz) olumsuz pekiştireç", "-Organizmaya direkt olarak zarar verme durumu olan uyarıcılardır.\n-Yaşamı tehdit eder.\n-Elektrik şoku verilen hayvanlar zarar görme korkusu ile doğru davranışı sergilemiştir.\n-Yaz tatilinde ailesi ile eğlenceli vakit geçiren Erdal, babasının verdiği görevi yapmazsa abisi tarafından soğuk suyla ıslatılmıştır. Soğuk sudan korunmak için Erdal istenilen davranışı gerçekleştirir."),
      InfoCardItem("aa_ogrenogretsurec_info_51", "İkincil(koşullu) olumsuz pekiştireçtir", "-Organizmaya zarar vermez.\n-Ancak organizma tarafından yine istenmez.\n-Ayrıca başlangıçta nötr olduğu halde birincil olumsuz pekiştireç ile pekiştireç özelliği kazanır.\n-Sınıfta sürekli konuşan öğrencinin sözlü notunun düşürülmesi doğru davranışı gösterme olasılığını arttırır."),
    ]), InfoCardModel("Pekiştireç Türleri-2", [
      InfoCardItem("aa_ogrenogretsurec_info_52", "Sosyal Pekiştireç", "-Sosyal ortamlarda organizmayı mutlu eden, haz duyuran, motivasyonunu arttıran ve davranış sıklığını arttıran pekiştireçtir.\n-Temel noktası sosyal ortamı kapsamasıdır.\n-Toplum içerisinde bir davranışından dolayı övülen bir çocuk davranışı sürdürür.\n-Onaylama, saygı duyma vs. örnekler verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_53", "Sembolik Pekiştireç", "-Organizmanın sergilediği davranış sembolik değerler ile ödüllendirilir.\n-Puan, marka, gülen yüz kartı vs. gibi sembolik değerler kullanılır.\n-Öğrencilerden ödev yapanlara yıldız verilmesi semboliktir.\n-Ayrıca not verme, alkışlama, futboldaki sarı kart vs. gibi örnekler verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_54", "Nesne Pekiştireç", "-Organizmanın sergilediği olumlu davranışın nesneler ile ödüllendirilmesidir.\n-Sınavda sınıfın en yüksek puanını alan öğrencinin tablet ile ödüllendirilmesi örnektir."),
      InfoCardItem("aa_ogrenogretsurec_info_55", "Faaliyet Pekiştireç", "-Organizmanın sergilediği olumlu davranışın sevdiği faaliyetler ile ödüllendirilmesidir.\n-Yıl sonu başarı puanı istenilen düzeyde olan öğrencinin yaz futbol okuluna yazdırılması örnek olarak verilebilir.\n-İstenilen davranışı gösteren çocuğa istediği oyuncak kostümünü almak da bu kapsamda değerlendirilir."),
      InfoCardItem("aa_ogrenogretsurec_info_56", "Pekiştirme Aralıkları", "-Sürekli pekiştirme,\n-Sabit zaman aralıklı pekiştirme\n-Değişken zaman aralıklı pekiştirme\n-Sabit oranlı pekiştirme\n-Değişken oranlı pekiştirme"),
      InfoCardItem("aa_ogrenogretsurec_info_57", "Sürekli pekiştirme", "-İstenilen davranış her gerçekleştiğinde pekiştireç verilir.\n-İlk öğrenmelerde tercih edilir.\n-En basit pekiştirme yöntemidir.\n-Düzenli ders çalışma alışkanlığı kazandırılmak istenen bir çocuğun, her ders çalışmasında ödüllendirilmesi örnek olarak verilebilir.\n-Sönmeye karşı direnci düşüktür."),
      InfoCardItem("aa_ogrenogretsurec_info_58", "Sabit zaman aralıklı pekiştirme", "-Pekiştirme belirlenmiş aralıklar ile yapılır.\n-Her 40 dakikada bir eksi almayan öğrencilerin ödüllendirilmesi.\n-Dereceye giren öğrencilerin her hafta Cuma günü ödüllendirilmesi."),
      InfoCardItem("aa_ogrenogretsurec_info_59", "Değişken zaman aralıklı pekiştirme", "-Organizma pekiştirmenin ne zaman geleceğini bilmez.\n-Davranış devam ederken aniden ödül verilir.\n-Bu durum davranışta sürekliliği sağlamada etkilidir.\n-Öğretmenin zamanı bildirmeden ön sınav yapması, öğrencilerin defrelerinin zaman bildirilmeden kontrol edilmesi örnek olarak verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_60", "Sabit oranlı pekiştirme", "-Pekiştirecin verilme durumunu belirleyen davranışın sayısıdır.\n-İstenilen davranış belli sayıda gösterildikten sonra pekiştireç verilir.\n-Öğrencinin verdiği 5 doğru cevap için kantinden çikolata alması, girilen ders sayısı kadar ek ücret alınması örnek olarak verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_61", "Değişken oranlı pekiştirme", "-Pekiştirecin verilme durumunda davranışın kaç kez tekrarlanacağı belirtilmemiştir.\n-Sönme direnci en yüksek olan pekiştireçtir.\n-Yere atılan çöpleri alanlara ödül veren öğretmenin, bazen 1 kez atana bazen 3 kez atana ödül vermesi örnektir.\n-Pekiştirecin kaç davranıştan sonra geleceği belli olmadığı için organizmayı sürekliliğe iter."),
    ]),InfoCardModel("Ceza Türleri", [
      InfoCardItem("aa_ogrenogretsurec_info_62", "Ceza-1", "-Organizmanın, istenmeyen davranışı yapmasına engel olmak için organizmaya uygulanan yaptırım olarak tanımlanır.\n-Amaç organizmanın olumsuz davranıştan uzaklaşmasını sağlamaktır.\n-Davranıştan sonra ceza verilmesi esastır.\n-Aksi taktirde organizma neden cezalandırıldığını bilemeyeceği için davranışın ortadan kaldırılmasına katkısı olmayacaktır."),
      InfoCardItem("aa_ogrenogretsurec_info_63", "Ceza-2", "-Ceza alınma olasılılğı olduğu sürece organizma istenmeyen davranışı göstermeyecektir.\n-Ancak ceza ortadan kalktığında organizma aynı davranışı gösterme eğilimi gösterecektir.\n-Ceza istenmeyen davranışı ortadan kaldırırken başka istenmeyen davranışların ortaya çıkmasına sebep olabilir.\n-Ceza, organizmada saldırganlığa neden olabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_64", "Ceza/Olumsuz Pekiştirme-1", "-En çok karıştırılan kavramlardandır.\n-İstenmeyen davranış sonucunda organizmaya ceza verilir.\n-Olumsuz pekiştirmede ise istenilen davranış ortaya çıktığında istenmeyen durum ortamdan çekilir.\n-Beden eğitimi dersinde kavga eden öğrencinin topunu almak ceza iken kavga etmeyen çocuğun inmiş topunu alıp şişmiş top vermek olumsuz pekiştireçtir. "),
      InfoCardItem("aa_ogrenogretsurec_info_65", "Ceza/Olumsuz Pekiştirme-2", "-Davranış sonucunda ceza verilirse, davranışın ortaya çıkma olasılığı azalır.\n-Davranış sonucunda olumsuz pekiştirme yapılırsa davranışın ortaya çıkma olasılığı artar.\n-Ceza olumsuz pekiştireçleri ortama getirirken olumsuz pekiştirmede olumsuz pekiştireçler ortadan kaldırılır."),
      InfoCardItem("aa_ogrenogretsurec_info_66", "Ceza Türleri", "İki tür ceza bulunur. Bunlar;\n-1. Tür Ceza,\n-2. Tür Cezadır."),
      InfoCardItem("aa_ogrenogretsurec_info_67", "1. Tür Ceza", "-İstenmeyen uyarıcı ortama sokulur.\n-Ortama istenmeyen uyarıcı girdiği anda davranış durur.\n-Ortama giren uyarıcı olumsuz pekiştireçtir.\n-Susmayan sınıfa öğretmenin sert bakış atması, kemer takmayan sürücünün çevirmede ceza yemesi örnek olarak verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_68", "2. Tür Ceza", "-İstenen uyarıcı ortamdan çekilir.\n-Ortamdan istenilen uyarıcı çekilerek istenilen davranış gerçekleştirilir.\n-Ortamdan çekilen uyarıcı olumlu pekiştireçtir.\n-Ödevini yapmayan öğrencinin bilgisayar oynayamaması, notları düşük olan öğrencinin yıl sonu gezisine katılamaması örnek olarak verilebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_69", "Ceza Uygulanırken-1", "-Cezanın davranışı hızlı ama geçici söndüreceği unutulmamalıdır.\n-Cezalandırma uygulanacaksa davranış sonrası gecikme olmamalıdır.\n-Aynı davranışa ceza vermekte kararlı davranılmalıdır.\n-Ceza aniden verilmemeli, öncesinde bildirilmelidir.\n-Kişiliği hedefleyen cezalar verilmemelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_70", "Ceza Uygulanırken-2", "-Başkalarına ders verme, korkutma, söz geçirme veya ispatlama gibi amaçlarla ceza verilmemelidir.\n-Davranış gerçekleşmeden veya gerçekleşmesinin üzerinden uzun süre geçtikten sonra ceza verilmemelidir.\n-Ceza uygulanan kişi için anlamsız olmamalıdır.\n-Fiziksel olarak zarar verecek türden cezalar verilmemelidir."),
    ]),
    InfoCardModel("Watson Bitişiklik Kuramı", [
    InfoCardItem("aa_ogrenogretsurec_info_71", "Watson", "-Davranışçı kuramın en bilindik temsilcilerinden birisi Watson'dur.\n-Watson'un savunduğu görüşe göre insanlar doğmazlar, yaratılırlar.\n-Yani bebeklerin koşullanma yoluyla öğretmen, polis, müzisyen vs. olabileceğini savunur.\n-Öğrenme sürecinde, öğretmenin görevinin öğrenme çevresinin kontrolü olduğunu belirtir."),
    InfoCardItem("aa_ogrenogretsurec_info_72", "Watson Bitişiklik Kuramı-1", "-Öğrenmenin ön koşulunun bitişiklik olduğunu söyler.\n-Organizmanın uyarıcıya verdiği tepki neticesinde bitişiklik oluşur.\n-Bitişiklik oluştuktan sonra ne zaman aynı uyarıcıyı görse, aynı tepki ortaya çıkar.\n-Davranışın çevresel etmenler ile gerçekleştiğini savunur.\n-Davranışta kalıtsal özelliklerin etkisinin olmadığını savunur."),
    InfoCardItem("aa_ogrenogretsurec_info_73", "Watson Bitişiklik Kuramı-2", "-Davranış kazandırmada öncelikle nötr uyarıcı verilmelidir.\n-Daha sonra koşulsuz uyarıcı verilmeli ve ikisi arasındaki süre kısa olmalıdır.(bitişiklik)\n-Öğrenme için bitişikliğin yanında tekrar da önemli yer kaplamaktadır.\n-Bu ifadeler Pavlov'un ifadeleri ile büyük ölçüde benzerlik göstermektedir.\n-Buradan hareketle Pavlov'un fikirlerini benimsediği söylenebilir."),
    InfoCardItem("aa_ogrenogretsurec_info_74", "Korku Koşullanması", "-Daha önce tepkisiz kalınan bir uyarıcıya korku tepkisi geliştirilmesidir.\n-Duygusal öğrenmelerin klasik koşullanmada var olduğunun ispat edilmiştir.\n-Korku koşullanmasının tespit edilmesi için, bir deney gerçekleştirilmiştir.\n-Bu deyin nasıl yapıldığına göz atalım."),
    InfoCardItem("aa_ogrenogretsurec_info_75", "Tavşan Deneyi-1", "-Öğrencisi Rayner ile gerçekleştirmiştir.\n-Korkunun doğal tepki mi yoksa öğrenilebilir bir deneyim mi olduğunu anlamak için yapılmıştır.\n-8 aylık bebek olan Albert'a maske, tavşan, fare vb. ilk kez gördüğü maddeler gösterilir.\n-Amaç koşulsuz(doğal) tepkinin varlığını ölçmektir.\n-Albert her şeye güler, hiçbir nesneden korkmaz."),
    InfoCardItem("aa_ogrenogretsurec_info_76", "Tavşan Deneyi-2", "-Albert boş bir odaya bırakılarak yanına fare gönderilir.\n-Albert fareden korkmaz aksine oynamak isterç\n-Daha sonra fareye her dokunduğunda rahatsız edici sesler çıkartılır.\n-Seslerden korkan Albert ağlar.\n-Bu işlem birkaç kez yapıldıktan sonra Albert fareye dokunmaktan korkar hale gelir."),
    InfoCardItem("aa_ogrenogretsurec_info_77", "Tavşan Deneyi-3", "-Fareye her dokunduğunda rahatsız edici sesler çıkartmaya devam edilir.\n-Albert her ses çıkartılmasında ağlamaya devam başlar.\n-Bu işlem birkaç gün devam ettikten sonra odaya beyaz tavşan bırakılır.\n-Albert, fareye benzeyen tavşanı veya benzer objeleri görünce de ağlamaya başlar.\n-Böylece korkuların öğrenilebildiği ispatlanır."),
    InfoCardItem("aa_ogrenogretsurec_info_78", "Korkunun Giderilmesi", "-Korku sonradan öğrenilebiliyorsa, korkulan bir durumdan korkmama da öğrenilebilir.\n-Korkunun giderilmesi işlemi aşama aşama gerçekleştirilir.\n-Önce uzağa tavşan konmuş sonra yavaş yavaş yaklaştırılmıştır.\n-Süreç sonunda Albert tavşandan korkma davranışını tekrar göstermemiştir."),
    InfoCardItem("aa_ogrenogretsurec_info_79", "En Son En Sık İlkesi", "-Bir uyarıcıya verilecek tepkinin, uyarıcıya karşı yapılan en son ve en sık tepki olduğunu savunur.\n-Öğrenme için pekiştirmeye ihtiyaç duyulmadığını belirtmiştir.\n-Öğrenmenin güçlü olmasının temel koşulunun sık verilen koşullu ve koşulsuz uyarıcılar olduğu savunulur."),
    ]),
    InfoCardModel("Guthrie Bitişiklik Kuramı", [
     InfoCardItem("aa_ogrenogretsurec_info_80", "Guthrie-1", "-Watson gibi öğrenmenin gerçekleşmesi için pekiştirecin zorunluğu olmadığını savunmuştur.\n-Öğrenmenin gerçekleşmesi için uyarıcı ile tepkinin bitişik olması yeterli olacaktır.\n-Uyarıcı ile tepkinin bitişikliğinin kurulması önemlidir.\n-Uyarıcı-tepki bitişikliği sağlandığında tepkinin ortaya çıkması için uyarıcının ortaya çıkması yeterli olacaktır."),
     InfoCardItem("aa_ogrenogretsurec_info_81", "Guthrie-2", "-Öğrenmede pekiştirme, ödül ve tekrarın önemi yoktur.\n-Öğrenmenin gerçekleşmesi için bir kez bağ kurulması yeterlidir.\n-Bu görüşü ile Watson'un sıklık ilkesi ile ters düşmüştür.\n-Öğrenme tek seferde gerçekleşirken, beceriyi geliştirmek için alıştırma gerekir.\n-Örneğin bisiklete binmeyi öğrenmek için bir kez bitişiklik kurmak gerekir. Beceri kazanmak için alıştırma yapmak gerekir. "),
     InfoCardItem("aa_ogrenogretsurec_info_82", "Eş Zamanlı Koşullanma-1", "-Öğrenmenin temeli eş zamanlı koşullanmadır.\n-Uyarıcı bir tepkiyi ortaya çıkarabildiği anda bitişiklik kurulmuş demektir. Bu sayede tekrara gerek kalmadan öğrenme gerçekleşmiş olur.\n-Yani tepki ile uyarıcı bir kez tanıştıktan sonra birbirlerini asla unutmazlar.\n-Eğer bu tanışma, yani bitişiklik, sağlanmamışsa öğrenme gerçekleşmiş denilemez."),
     InfoCardItem("aa_ogrenogretsurec_info_83", "Eş Zamanlı Koşullanma-2", "-Öğrenme gerçekleşirken tekrara gerek yok iken, beceri geliştirme sürecinde tekrar gerekir.\n-Yani bilgisayar kullanmayı öğrenmek için bir kez uyarıcı-tepki bitişikliği yeterlidir. Ancak bilgisayar kullanma becerisinin oluşması için tekrar/alıştırma gerekir.\n-Organizmanın uyarıcıya verdiği son tepki, bir sonraki karşılaşmada tekrar gösterilir."),
     InfoCardItem("aa_ogrenogretsurec_info_84", "Ceza-1", "-Guthrie södürülmek istenen davranışlarda ceza vermenin etkili bir yöntem olmadığını savunmuştur.\n-Ancak ceza verilecekse duygulara hitap etmemesi gerektiğini söyler.\n-Ayrıca öğrenmede olduğu gibi cezada da bitişikliğin önemine vurgu yapar.\n-Sınavda kopya çeken öğrenciye verilecek ceza, kopya çekme anında olmalıdır.\n-Davranışın üzerinden vakit geçerse ceza anlamsızlaşır."),
     InfoCardItem("aa_ogrenogretsurec_info_85", "Ceza-2", "-Cezanın etkili olduğunu anlamak için, istenmeyen davranışın tersi bir davranış geliştirilmiş olunmalıdır.\n-Örneğin sınıfta top oynayan öğrencilere verilen ceza etkili olursa bu davranışın yerine bahçedeki sahada top oynama davranışı geliştirilmiş olmalıdır.\n-Uyarıcı-tepki bitişikliğinin bir kez sağlanması öğrenmede etkili ise cezada da etkilidir görüşünü savunur."),
     InfoCardItem("aa_ogrenogretsurec_info_86", "Davranış Söndürme", "İstenmeyen davranışın ortadan kaldırılması için cezaya çok sıcak bakmayan Guthrie, bazı yöntemleri kullanarak istenmeyen davranışın ortadan kaldırılabileceği görüşünü savunmuştur.\n\nBu yöntemler;\n-Bıktırma(Yorma) Yöntemi,\n-Eşik(Alıştırma) Yöntemi\n-Zıt(Karşıt) Tepki Yöntemi"),
     InfoCardItem("aa_ogrenogretsurec_info_87", "Bıktırma(Yorma) Yöntemi", "-İstenmeyen davranışın organizmada bıkkınlık oluşana kadar yaptırılmasıdır.\n-Arabanın kornasına sürekli basarak çevreyi rahatsız eden çocuğu boş bir araziye götürerek sıkılana kadar kornaya basmasını sağlamak bu yönteme örnektir.\n-Organizmanın kendine zarar verme durumu olan istenmeyen davranışlarda bu yöntem kullanılamaz."),
     InfoCardItem("aa_ogrenogretsurec_info_88", "Eşik(Alıştırma) Yöntemi", "-İstenmeyen tepkiye sebep olan uyarıcı belirlenir.\n-Uyarıcı ile istenmeyen tepki arasındaki bağlantının kopması için uyarıcı küçük parçalara bölünür.\n-En küçük birimden başlayarak ve yavaş yavaş parçalar büyütülerek uyarıcı organizma ile karşılaştırılır.\n-Bu sayede tepkinin kaybolması sağlanır.\n-Kediden korkan çocuğu adım adım kedinin yanına yaklaştırmak."),
     InfoCardItem("aa_ogrenogretsurec_info_89", "Zıt(Karşıt) Tepki Yöntemi", "-İki uyarıcı bulunur.\n-Bir tanesi istenmedik davranışı ortaya çıkaran uyarıcı, diğeri ise istendik davranışı ortaya çıkarma potansiyeli olan uyarıcıdır.\n-Kediden yanına yaklaşmaktan korkan bir çocuğun çok sevdiği annesinin yanında kedinin yanına yaklaşması örnektir."),
    ]),
    InfoCardModel("Piaget’in Bilişsel Gelişim Kuramı (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_90", "Bilişsel Öğrenme Kuramları-1", "-Bilişsel alan kuramcıları davranışçı görüşleri kabul etmekle birlikte, birey davranışlarının çok daha karmaşık, gözlenemeyen zihinsel süreçlere dayalı olduğunu savunmuşlardır.\n-Bilişsel alan kuramcılarına göre davranışçı yaklaşımlar öğrenmeyi kısmen açıklayabilmektedir.\n-Öğrenme süreçleri “uyarıcı-davranış” kalıbından çok daha karmaşık bir yapıya sahiptir."),
       InfoCardItem("aa_ogrenogretsurec_info_91", "Bilişsel Öğrenme Kuramları-2", "-Bilişsel yaklaşım bireyin çevresi hakkındaki bilgileriyle ve bu bilgilerin onun davranışlarını etkileme yollarıyla ilgilenir.\n-Bu yaklaşımda ön bilgi, bilgi işleme süreci ve bilginin kodlanması gibi gözlenemeyen zihinsel etkinliklerle ilgili çalışmalar yürütmektedir.\n-Bilişsel alan kuramcılarından Piaget, bireyin bilişsel gelişimini gelişim dönemlerine göre açıklamaya çalışmıştır."),
       InfoCardItem("aa_ogrenogretsurec_info_92", "Piaget’in Bilişsel Gelişim Kuramı", "Piaget’in Kuramındaki Temel Kavramlar\n-\n-Zeka\n-Şema\n-Adaptasyon\n-Özümleme\n-Uyma\n-Dengeleme\n-Örgütleme"),
       InfoCardItem("aa_ogrenogretsurec_info_93", "Zeka", "-Zeka organizmanın çevreye uyum sağlama yeteneğidir.\n-Birey çevresine uyum sağladığı sürece zekidir.\n-Piaget, zeka sayısal olarak ölçülemediği için zeka testlerine karşı çıkmıştır. "),
       InfoCardItem("aa_ogrenogretsurec_info_94", "Şema", "-Şemalar doğuştan getirilen nesne, olay ve olguların yerleştirildiği en temel zihinsel çerçevelerdir.\n-Şemaların sayıları çevreyle etkileşim sonucunda artar, bu etkileşim ile şemalar gelişir ve değişirler.\n-İlk bilişsel şemalar reflekslerdir. Şema öğrenmenin en temel yapıtaşıdır. "),
       InfoCardItem("aa_ogrenogretsurec_info_95", "Adaptasyon", "-Bireyin içinde yaşadığı ortama uyum sağlamasıdır.\n-Adaptasyon birbirini tamamlayan ve izleyen iki süreçten (Özümleme ve Uyma) oluşur."),
       InfoCardItem("aa_ogrenogretsurec_info_96", "Özümleme", "-Bilginin içselleştirilme sürecidir. Yeni karşılaşılan bir durumun bilgi, deneyim ve var olan şemalara göre yeniden düzenlenmesidir.\n-Bir çocuğun ilk defa gördüğü kediye daha önce gördüğü köpek demesi örnek olarak verilebilir. "),
       InfoCardItem("aa_ogrenogretsurec_info_97", "Uyma", "-Önceki şemaların yetersiz olması durumunda elde edilen yeni bilgi ve deneyimlerle yeni şemaların oluşmasıdır. Eğitim ve öğretim Piaget’e göre uymadır.\n-Çocuğun kedi için yeni bir şema açması ve onu köpek şemasından ayırması uymaya örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_98", "Dengeleme", "-Elde edilen yeni bilgiler ilk olarak önceki şemalarla karşılaştırılır ve değerlendirilir.\n-Bu aşamada özümleme yapılır.\n-Eğer bu değerlendirme yeni bilgiyi karşılamadıysa bilişsel denge bozulur. Denge uyma aracılığıyla yeniden kurulmaya çalışılır.\n-Denge bulununcaya kadar özümleme ve uyma işlemleri aktif olarak kullanılır.\n-Dengeleme düşüncenin bir aşamasından diğerine geçmesini sağlar.\n-Bireyin çevreye uyum sağlama isteği kapsamında dengeleme gerçekleşmektedir."),
       InfoCardItem("aa_ogrenogretsurec_info_99", "Örgütleme", "-Piaget’e göre birey zihindeki bilgi parçalarını sürekli olarak ilişkilendirmeye ve bütünleştirmeye çalışır.\n-Bu yapıya örgütleme adı verilir.\n-Örgütleme ile birey üst düzey ve dengeli zihinsel yapılar kurabilir."),
       InfoCardItem("aa_ogrenogretsurec_info_100", "Örnek", "-Bir çocuk için balon şeması, yuvarlak olan ve uçan tüm nesneleri içerir.\n-İlk kez yumurta gören çocuk onu balon şemasına yerleştirir (özümleme).\n-Uçması için onu havaya atınca yumurtanın uçmadığını ve kırıldığını görünce bilişsel dengesi bozulur.\n-Yeni bir yumurta şeması oluşturarak bilişsel dengeyi tekrar kurar (Uyma)."),
       InfoCardItem("aa_ogrenogretsurec_info_101", "Örnek Devamı", "-Tüm bu süreç dengeleme olarak adlandırılır.\n-Yeni bilgileri var olan eski bilgilerle bütünleştirmesi ya da ilişkilendirmesi ise örgütlemedir.\n-Yumurtanın düşmesi ile yer çekimi bilgilerini ilişkilendirmesi örnek olarak verilebilir."),
    ]),
    InfoCardModel("Piaget’in Bilişsel Gelişim Dönemleri", [
       InfoCardItem("aa_ogrenogretsurec_info_100", "Tanım", "-Piaget’in bilişsel gelişim kuramında dört bilişsel gelişim döneminden bahsedilmektedir. Bunlar;\n\n-Duyusal Motor Dönem (0-2 yaş)\n-İşlem Öncesi Dönem (2-7 yaş)\n-Somut İşlemler Dönemi (7-11 yaş)\n-Soyut İşlemler Dönemi (11 yaş sonrası)"),
       InfoCardItem("aa_ogrenogretsurec_info_101", "Duyusal Motor Dönem (0-2 yaş)-1", "Anahtar kelimeler\n\n-Refleksler,\n-Döngüsel tekrar tepkileri,\n-Dil gelişimi,\n-Nesne sürekliliği,\n-Ses bulaşması,\n-Sihirli nedensellik,\n-Ertelenmiş taklit. \nBu kavramların tamamı açıklanacaktır."),
       InfoCardItem("aa_ogrenogretsurec_info_102", "Duyusal Motor Dönem (0-2 yaş)-2", "-Bebekler bu dönemin başlarında sadece refleks tepkiler verirler ancak zamanla çevrelerinin de etkisi ile daha karmaşık duyusal motor tepkiler verdikleri bir süreç yaşarlar.\n-Yani refleks davranışlardan amaçlı davranışlara geçerler.\n-Kısa gibi gözükmesine rağmen bu dönemde bebekte çok büyük değişim ve gelişimler yaşanır.\n-Bu süreçte bebek tam bir muhtaçlık ve bağımlılık halinden konuşan, yürüyen bir birey haline dönüşür. "),
       InfoCardItem("aa_ogrenogretsurec_info_103", "Duyusal Motor Dönem (0-2 yaş)-3", "-Bu dönemde zekanın göstergesi hareketlerdir.\n-Sadece dokunma davranışını sergileyen bebek tutma, asılma, emme gibi davranışlar sergilemeye başlar.\n-Zamanla bilişsel bir sistem geliştirirler.\n-Sürekli etrafını keşfetme çabasında olan bebek bu dönemde kendini dış dünyadan ayırt etmeye başlar."),
       InfoCardItem("aa_ogrenogretsurec_info_104", "Duyusal Motor Dönem (0-2 yaş)-4", "-Bu keşifte duyusal ve hareketsel yeteneklerini kullanır.\n-Akıl yürütme ve mantıksal işlemler pek görülemez.\n-Duyusal motor dönemin önemli özelliklerinden biri de nesne kalıcılığıdır.\n-Birkaç aylık bir bebek nesne saklandığı zaman o nesnenin yok olduğunu ve artık gerçekten var olmadığını düşünür. "),
       InfoCardItem("aa_ogrenogretsurec_info_105", "Duyusal Motor Dönem (0-2 yaş)-5", "-Bir yaşına doğru bebek nesne gözünün önünden kaldırılsa bile var olduğunu bilir.\n-İki yaşına doğru ise sebep-sonuç ilişkisi kurabilen çocuk nesnenin varlığından emindir ve onu aramaya başlar.\n-Piaget’e göre işlem kavramı bir sonraki gelişim döneminden itibaren ele alınmaya başlar. "),
       InfoCardItem("aa_ogrenogretsurec_info_106", "Sonraki Kart", "Duyusal Motor Dönemin evreleri bir sonraki kartta detaylı şekilde işlenecektir."),
    ]),
    InfoCardModel("Duyusal Motor Dönemin Evreleri", [
       InfoCardItem("aa_ogrenogretsurec_info_107", "Evreler", "-Duyusal Motor Dönem 6 evreden oluşmaktadır.\n-1.Evre (0-1 Ay)\n-2.Evre (1-4 Ay)\n-3.Evre (4-10 Ay)\n-4.Evre (10-12 Ay)\n-5.Evre (12-18 Ay)\n-6.Evre (18-24 Ay)"),
       InfoCardItem("aa_ogrenogretsurec_info_108", "1.Evre (0-1 Ay)", "-Bu dönemde bebekler emme reflekslerini kullanılır.\n-Bu refleksleri farklı hareketlere evirmeye çalışırlar.\n-Bu davranışlar bebeğin gelişiminin temelini oluşturur."),
       InfoCardItem("aa_ogrenogretsurec_info_109", "2.Evre (1-4 Ay)", "-Bebek kendi vücudunu tanımaya yönelik davranışlar, merak işaretleri ve farklı nesnelere ilgi davranışları sergilerler.\n-İlk döngüsel tekrar tepkileri vermeye başlarlar.\n-Elini sürekli indirip kaldırması, sürekli parmağını emmesi, tekrarlı olarak parmaklarını açıp-kapaması, ses bulaşması, bulduğu şeyi ağzına götürmesi gibi…\n-Ses bulaşması basit bir taklit ve öğrenme faaliyetidir.\n-Ağlama sesi duyan bebeğin kısa bir süre sonra ağlamaya başlaması örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_110", "3.Evre (4-10 Ay)", "-Bebek ilgisini artık kendisinden çevreye kaydırmıştır.\n-Doğadan ayrışma adı verilen kendisinden bağımsız bir dünya olduğunu fark etmeye başlar.\n-Bebek gözlem ya da keşif yoluyla elde ettiği bir hareketi tekrarlar.\n-Buna ikincil döngüsel tekrar tepkisi adı verilir.\n-İlk döngüsel tekrar tepkileri sadece hareketi tekrarlamaya yönelikken ikincil olanlarda ilgi, merak ve hoşa giden durumun tekrarlanması isteği vardır. "),
       InfoCardItem("aa_ogrenogretsurec_info_111", "3.Evre (4-10 Ay)", "-Beşiğinin üzerine asılmış hareketli oyuncaklara ayak ya da eliyle vurarak sürekli hareket ettirmesi gibi.\n-Davranışlar arasında neden sonuç ilişkisi kuramayan bebek için sihirli bir nedensellik söz konusudur.\n-Davula vurunca önemli olan ses çıkmasıdır.\n-Sesin nasıl çıktığıyla ilgilenmez ve önemsemez. "),
       InfoCardItem("aa_ogrenogretsurec_info_112", "4.Evre (10-12 Ay)", "-Bebek ikincil döngüsel tepkileri birbiriyle ilişkilendirerek ilk amaçlı davranışlarını-zeka belirtisi olarak-sergilemeye başlar.\n-Zihinde canlandırma adı verilen, bir davranışta bulunmadan önce onu zihninde canlandırmaya başlayabilir.\n-Yine bu evreden itibaren bir davranışın yakın ya da uzak bir gelecekte taklit ve tekrar edilmesi (ertelenmiş taklit) görülebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_113", "4.Evre (10-12 Ay)", "-Ertelenmiş taklit davranışı ömür boyu devam eder.\n-Saklanan bir şeyi bulma amacıyla bir nesnenin arkasına hareket etmesi örnek olarak verilebilir.\n-Elindeki tahta kaşığı sürekli tabağa vurup çıkan sesten hoşlanıp davranışı tekrar ettirebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_114", "5.Evre (12-18 Ay)", "-Bu dönemde bebek neden-sonuç ilişkileri içerisinde üçüncül döngüsel tepkiler göstermeye başlar.\n-Bebek etkin olmasını engelleyen bir durumla karşılaştığında onu ortadan kaldırmaya çalışır.\n-Örneğin oyuncağını almak için bazı nesneleri kullanabilir.\n-Hoşuna giden bir sözü ya da şarkıyı sürekli tekrar edebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_115", "6.Evre (18-24 Ay)", "-Çocuk bu evrede sadece keşfeden değil zihinsel süreçleri ve bağlantıları kullanarak yeni durumlar türetendir.\n-Artık problemlerini sadece fiziksel değil zihinsel süreçler yardımıyla çözme davranışını göstermeye başlamıştır.\n-Sembolik düşünceye geçiş bu evrede olur.\n-Zihninde temsili bir ortam yaratma belirtileri ve temel problem çözme basamaklarını kafasında kurgular gibidir."),
       InfoCardItem("aa_ogrenogretsurec_info_116", "Genel Açıklama", "İlk deneme yanılma tecrübeleri de bu dönemde başlar. 4 aylık bir bebek oyuncağına ulaşmaya çalışır ancak ulaşamazsa ondan vazgeçer. Ancak 11 aylık bir bebek oyuncağına ulaşabilmek için her yolu dener. "),

    ]), InfoCardModel("İşlem Öncesi Dönem (2-6 yaş)", [
       InfoCardItem("aa_ogrenogretsurec_info_117", "İşlem Öncesi Dönem (2-6 yaş)-1", "Anahtar kavramlar;\n-Nesne ya da olayları sembolleştirme,\n-Tek yönlü sınıflandırmalar,\n-Ben merkezcilik,\n-Sihirli düşünce, monolog,\n-Kişi sürekliliği,\n-Sembolik ve paralel oyun,\n-Animizm,\n-İşaretsel işlev"),
       InfoCardItem("aa_ogrenogretsurec_info_118", "İşlem Öncesi Dönem (2-6 yaş)-2", "-Bu dönemde çocuk önceki gelişim dönemindeki harekete bağlılıktan uzaklaşarak basit düzeyde sembol kullanımına başlar.\n-Nesneleri zihninde canlandırarak bir tahta parçasını uçak ya da bir havyan (zihinsel sembol) gibi düşünebilir. "),
       InfoCardItem("aa_ogrenogretsurec_info_119", "İşlem Öncesi Dönem (2-6 yaş)-3", "-Piaget işlem kavramını mantıksal düşünme olarak ifade etmektedir.\n-Ancak bu dönemde henüz yetişkinlerdeki gibi bir mantık gelişmediği için bu döneme işlem öncesi dönem adı verilir.\n-İşlem öncesi dönem ikiye ayrılır. \n\nBunlar;\n-Sembolik İşlemler Dönemi(3-4 Yaş),\n-Sezgisel İşlemler Dönemi(5-6 Yaş)'dir."),
       InfoCardItem("aa_ogrenogretsurec_info_120", "Sembolik İşlemler Dönemi (3-4 Yaş)", "-Bu dönemde dil gelişimi hız kazanır.\n-Hayal gücü dil gelişimine bağlı olarak bir hayli gelişmiştir.\n-Sihirli düşünce, sembolik oyun, animizm, benmerkezcilik, monolog, kişilerin sürekliliği, paralel oyun ve işaretsel işlev davranışları görülebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_121", "Sihirli Düşünce", "-Yüksek hayal gücü zayıf mantıksal düşünce sebebiyle ortaya çıkar.\n-Bir çocuğun çizgi filmlerdeki durumlara inanması sihirli düşünce ile açıklanabilir.\n-Zeynep’in iyi bir çocuk olursa şirinleri görebileceğine, ıspanak yiyen Ali’nin temel reis gibi güçleneceğine inanması örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_122", "Sembolik Oyun", "-Çocuğun bir nesneyi başka bir nesne ya da kişi yerine koyarak oynadığı oyundur. Öyleymiş gibi yapmaktır.\n-Bir tahta parçasını uçak gibi düşünerek onunla oyun oynanması örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_123", "Animizm", "-Çocuklar cansız varlıklara canlıymış gibi davranırlar.\n-Oyuncak ayısıyla konuşur, ona yemek yedirir, yere düştüğünde canının yandığını düşünür."),
       InfoCardItem("aa_ogrenogretsurec_info_124", "Benmerkezcilik", "-Olayları sadece kendi bakış açısından değerlendirirler.\n-Çocuk kendisinin duyduğu, gördüğü, bildiği her şeyi diğer herkesin bildiğini ve her şeyin kendisi için var olduğunu zanneder.\n-Benmerkezciliğin bir çeşidi de ergenlikte görülecektir."),
       InfoCardItem("aa_ogrenogretsurec_info_125", "Monolog", "-Çocuğun tek başına kaldığında kendi kendine konuşması ve mırıldanmasıdır.\n-Çocuk sergilediği her davranışı birde sözel olarak dile getirmektedir."),
       InfoCardItem("aa_ogrenogretsurec_info_126", "Kişilerin Sürekliliği", "-Çocukların bir kişinin dış görünüşü değişiklik ya da benzerlik gösterse bile onun aynı kişi ya da farklı kişi olduğunu bilmesidir.\n-Her sakallı dedesi değildir.\n-Bıyığını kesse bile babasını tanır."),
       InfoCardItem("aa_ogrenogretsurec_info_127", "Paralel Oyun", "-Bir gruptaki çocuklar beraber oyun oynuyormuş gibi gözükseler de aslında hepsi kendi bireysel oyunlarını oynamaktadırlar.\n-Bireyler arasında etkileşim yok gibi görünse de birbirlerinin hareket ve davranışlarını kopyalayabilirler."),
       InfoCardItem("aa_ogrenogretsurec_info_128", "İşaretsel İşlev", "-Çocuğun yanında olmayan ya da o an göremediği nesneleri hatırlayıp kullanabilmesidir.\n-Resim çizerken yanında olmasa da portakalı çizebilmesi örnek olarak verilebilir."),
    ]),
    InfoCardModel("Sezgisel İşlemler Dönemi (5-6 Yaş)", [
       InfoCardItem("aa_ogrenogretsurec_info_129", "Sezgisel İşlemler Dönemi (5-6 Yaş)-1", "-Bu dönemde henüz mantıksal düşünme tam gelişmemiştir.\n-Ancak mantıklı düşünme döneminin temelleri atılmaya başlanmıştır.\n-Bir çocuk kibritle oynamaması gerektiğini bilir ancak kibritle oynamasının doğuracağı sonuçları henüz kestiremez. "),
       InfoCardItem("aa_ogrenogretsurec_info_130", "Sezgisel İşlemler Dönemi (5-6 Yaş)-2", "-Bu dönemde sezgiler ön plana çıkmaktadır.\n-Merkeziyetçilik, tersine çevirme, korunumu kazanma, sınıf kapsama özelliği, özelden özele akıl yürütme, tek yönlü sınıflama, özdeşlik, yapaycılık davranışları görülebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_131", "Merkeziyetçilik", "-Çocuğun iki durumu bir araya getirememesi ya da olayların tek bir yönüne odaklanmasıdır.\n-Yatmadan önce annesi masal okurken çocuğun aynı anda sütünü içmemesi örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_132", "Tersine Çevirme", "-Çocuklar sadece tek yönlü işlemleri yapabilirken tersine işlemleri yapamazlar (A=B iken B=A’yı kavrayamaz).\n-Çocuklar birden fazla olay, durum, özellik üzerine dikkatlerini toplayamazlar."),
       InfoCardItem("aa_ogrenogretsurec_info_133", "Korunumu Kazanma", "-Bir ekmek ortadan ikiye bölündüğünde iki parçaya ayrıldığı için bunu bütünden (bir ekmekten) daha fazla olduğunu düşünürler. "),
       InfoCardItem("aa_ogrenogretsurec_info_134", "Sınıf Kapsama Özelliği", "Yediği çileğin bir meyve olduğunu kabul eden çocuğun başka bir meyveyi meyve olarak kabul edememesi henüz sınıf kapsama özelliğini kazanamadığının göstergesidir."),
       InfoCardItem("aa_ogrenogretsurec_info_135", "Özelden Özele Akıl Yürütme", "-Bir durumu tek nesne ile eşleştirmek diğer nesneleri görememektir.\n-Her gün kahvaltıda ballı ekmek yiyen bir çocuğun ballı ekmeğini yemediğinde kahvaltı etmediğini söylemesi örnek olarak verilebilir. "),
       InfoCardItem("aa_ogrenogretsurec_info_136", "Tek Yönlü Sınıflama", "-Tek bir niteliğe göre gruplama yapabilmektir.\n-Oyuncakları şekillerine ya da sadece renklerine göre sınıflandırılabilmesi örnek olarak verilebilir. "),
       InfoCardItem("aa_ogrenogretsurec_info_137", "Özdeşlik", "-Bir şey ne ise odur.\n-Suyun konduğu kabın şeklini alsa da su yine de sudur.\n-Çocuk bu durumu kavrayabiliyorsa özdeşlik bilincini kazanmış demektir."),
       InfoCardItem("aa_ogrenogretsurec_info_138", "Yapaycılık", "-Doğa olaylarının kaynağının doğal olmayan bir güç olduğunu düşünür.\n-Yağmur yağdığında kuşların ağladığını düşünmesi gibi."),
    ]),
    InfoCardModel("Somut İşlemler Dönemi (7-11 yaş)", [
       InfoCardItem("aa_ogrenogretsurec_info_139", "Somut İşlemler Dönemi (7-11 yaş)-1", "Bireyin bilişsel gelişimine paralel olarak mantıksal düşünme, korunum kazanma, üst düzey sınıflama, somut yollarla problem çözme, empati, tümevarımsal düşünme görülmeye başlar."),
       InfoCardItem("aa_ogrenogretsurec_info_140", "Somut İşlemler Dönemi (7-11 yaş)-2", "Bu dönemde çocuğun bilişsel gelişiminde büyük çaplı değişimler yaşanır.\n\nBu değişimler;\n-Odaktan uzaklaşma (dağılma),\n-Tersine çevirebilirlik,\n-Sınıfa dahil olma,\n-Tümevarımsal mantık,\n-Korunum davranışları görülebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_141", "Odaktan Uzaklaşma", "Nesne ya da olaylara tek yönüyle değil daha fazla yönüyle odaklanabilirler. "),
       InfoCardItem("aa_ogrenogretsurec_info_142", "Tersine Çevirebilirlik", "Geriye doğru düşünebilme durumudur. A=B ise B=A, ya da A>B ise B<A’dır."),
       InfoCardItem("aa_ogrenogretsurec_info_143", "Sınıfa Dahil Olma", "-Alt kategorilerinde üst kategoriler içerisinde olduğunu bilmektir.\n-Yediği çileğin bir meyve olduğunu kabul eden çocuğun başka bir meyveyi de meyve olarak kabul edebilmesi örnek olarak verilebilir.\n-Sınıfa dahil olma özelliği, sınıf kapsama özelliğinin tersi olarak düşünülebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_144", "Tümevarımsal Mantık", "Parçadan bütüne düşünerek akıl yürütmedir. Bu dönem içerisinde kazanılan bir özelliktir. "),
       InfoCardItem("aa_ogrenogretsurec_info_145", "Korunum", "-Korunum, bir şeyin özü değişmedikten sonra diğer özellikleri değişse bile, o şeyin hala aynı şey ve miktarda olduğunu bilmektir.\n-Bir gofretin üçe bölünse bile miktarının artmayacağını bilmesi örnek olarak verilebilir."),
    ]),
    InfoCardModel("Soyut İşlemler Dönemi (11 yaş sonrası)", [
      InfoCardItem("aa_ogrenogretsurec_info_146", "Soyut İşlemler Dönemi (11 yaş sonrası)-1", "Bireyin bilişsel gelişimine paralel olarak soyut düşünme, bilimsel yöntemle problem çözme, değer ve inanç sistemini yapılandırma, fikir dünyasıyla aktif olarak ilgilenme, tümevarımsal ve tümdengelimsel düşünme görülmeye başlar."),
      InfoCardItem("aa_ogrenogretsurec_info_147", "Soyut İşlemler Dönemi (11 yaş sonrası)-2", "Bu dönemde;\n\n-Tümdengelim,\n-Hipotetik düşünme,\n-Birleştirici düşünme,\n-Ergen benmerkezciliği,\n-Hayali seyirciler,\n-Kişisel efsane,\n-Omnipotent düşünce,\n-Göreli kavramlar gibi davranışlar görülebilir. "),
      InfoCardItem("aa_ogrenogretsurec_info_148", "Tümdengelim", "Bütünden parçaya düşünerek akıl yürütmedir. Bilimsel düşüncenin temelini oluşturur. "),
      InfoCardItem("aa_ogrenogretsurec_info_149", "Hipotetik düşünme", "Bireyin bir düşünceyi için de bulunduğu koşulları da göz önünde bulundurarak uygun savlar ile savunabilme yeteneğidir. "),
      InfoCardItem("aa_ogrenogretsurec_info_150", "Birleştirici düşünme", "Birey karmaşık bir probleme ait değişkenleri ele alıp tek tek test eder sonrasında bu çözümleri birleştirerek problemi çözer."),
      InfoCardItem("aa_ogrenogretsurec_info_151", "Ergen benmerkezciliği-1", "-Gencin ergenlik dönemindeki söylediklerinin, düşündüklerinin en doğru olduğunu ve dünya kendi etrafında dönüyor gibi hissetmesidir.\n-Kendi zihninde oluşturduğu dünya ile karşı karşıya gelen her duruma tahammül gösteremez. "),
      InfoCardItem("aa_ogrenogretsurec_info_152", "Ergen benmerkezciliği-2", "Ergen benmerkezciliğinde öne çıkan üç kavram vardır. Bunlar;\n-Hayali seyirciler,\n-Kişisel efsane\n-Omnipotent düşünce'dir."),
      InfoCardItem("aa_ogrenogretsurec_info_153", "Hayali seyirciler", "Gencin etrafındaki herkesin kendisini izlediğine ve ilgilendiğine inanmasıdır. "),
      InfoCardItem("aa_ogrenogretsurec_info_154", "Kişisel efsane", "Gencin kendisinin çok özel olduğuna ve varlığının çok özel bir sebebinin olduğuna inanmasıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_155", "Omnipotent düşünce", "Gencin gücünün her şeye yetebileceğine inanması durumudur. Bu durumda ergen genç yaşamı çok basite indirger."),
      InfoCardItem("aa_ogrenogretsurec_info_156", "Göreli kavramlar", "Birey bir olaya farklı bakış açılarıyla yaklaşabilir. Olayın farklı koşullarda farklı anlamları olabileceğini kavrar."),
    ]),

   InfoCardModel("Yapılandırmacı Kuram (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_157", "Yapılandırmacılık-1", "Yapılandırmacılık kuramının doğuşunda Bruner, Vygotsky, Barlett ve Dewey’in görüşleri etkili olmuştur.\n-Ancak kuramın geliştirilip tanımlanmasında en büyük etkiyi Piaget’in birey gelişimi üzerine yaptığı çalışmalar göstermiştir."),
       InfoCardItem("aa_ogrenogretsurec_info_158", "Yapılandırmacılık-2", "Yapılandırmacılık davranışçı kuramlardan farklı olarak, bireyin nasıl öğrendiğini ele almaktadır.\n-Bireyin zihinsel yapılandırması sonucu gerçekleşen biliş temelli bir öğrenme yaklaşımıdır.\n-Bireyin nasıl anladığını ve öğrendiğini açıklayan bir anlayış ortaya koymaktadır.\n-Yapılandırmacı yaklaşımın temel hareket noktası pragmatizmdir."),
       InfoCardItem("aa_ogrenogretsurec_info_159", "Pragmatizm-1", "Pragmatizm yarar, çıkar anlamına gelmektedir.\n-Pragmatizme göre bilginin doğruluğu yararlı olduğu kadardır.\n-Gerçekler ve doğrular insanların bakış açısından, kanaatlerinden ve eylemlerinden bağımsız olamaz.\n-Pragmatizm doğruluk ve gerçekliği yalnızca insan eylemlerinin sonuçları ve faydalılıklarına göre değerlendirir."),
       InfoCardItem("aa_ogrenogretsurec_info_160", "Pragmatizm-2", "Bu yüzden gerçeklik, doğruluk ve insan eylemlerinin sonuçları, sağladığı başarı ve yararlarla eşdeğerdir.\n-Pragmatizme göre toplumların ve devletlerin ilerlemesi için, faydasız olan tüm kavramlardan uzak durulmalı ve ilerlemeci bir anlayışla geleceğe yatırım yapmalıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_161", "Yapılandırmacılık-3", "Yapılandırmacılığın özünde, öğrencinin bilgiyi kendi süzgeçlerinden geçirerek yapılandırması ve uygulamaya koyması yer almaktadır.\n-Geleneksel yaklaşım içinde öğrencinin pasif olarak görülmesine karşı çıkar ve öğrencinin aktif bir birey olarak öğrendiğini öne sürer.\n-Bu kurama göre her bireyin kişisel bir bilgi birikimi ve tecrübesi vardır.\n-Birey yeni öğrendiğini var olan bilgisinin üzerine ekleyerek yeni bir bilgi geliştirir."),
       InfoCardItem("aa_ogrenogretsurec_info_162", "Yapılandırmacılık-4", "-Bu yaklaşıma göre eğitim sisteminde ezberden öte yeni çıkarımlar ve fikir yürütmeler bulunmalıdır.\n-Program esnekliğine, öğrenci odaklılığına ve bilgide öznelliğe vurgu yapmaktadır.\n-Öğrencilerin öğrenme süreçlerini inceleyerek bu süreçleri daha verimli bir yere getirmeyi hedeflemektedir."),
       InfoCardItem("aa_ogrenogretsurec_info_163", "Yapılandırmacılık-5", "Yapılandırmacı öğrenme kuramı öğrencilerin sorunlara yeni ve farklı çözümler bulmalarını, düşüncelerini rahatça dile getirebilmelerini, başkalarıyla işbirliği yapabilmelerini, düşünme şekillerini gözden geçirmelerini ve en iyi çözümü sunabilmeleri için öğrencilerin desteklenmesini önemser.\n-Öğretmenin iyi olması öğrencinin etkin öğrenmesi için yeterli olmayabilir."),
       InfoCardItem("aa_ogrenogretsurec_info_164", "Yapılandırmacılık-6", "-Öğretmenin görevi öğrenmenin merkezinde yer alan öğrenciye rehberlik etmek, öğrencinin bilgiyi özümsenmesini ve içselleştirmesini sağlamaktır.\n-Öğrenen kişi yeni bir bilgi ile karşılaştığında önceden oluşturduğu bilgi ve tecrübelerini kullanır ve algıladığı bilgiyi daha iyi açıklamak için yeni kurallar oluşturur."),
       InfoCardItem("aa_ogrenogretsurec_info_165", "Yapılandırmacı Öğrenme Kuramının Temel İlkeleri-1", "Yapılandırmacı öğrenme kuramının temel ilkeleri şunlardır;\n-Öğretmen öğretmez, deneyimler yaşatır ve rehberlik eder. Öğrenci kendi deneyimleriyle öğrenir.\n-Amaç öğrenmeyi öğretmektir. Öğrenci bilgi edinme yollarına hakim olmalıdır.\n-Öğrenci pasif değil aktiftir ve kendi öğrenmesinden sorumludur."),
       InfoCardItem("aa_ogrenogretsurec_info_166", "Yapılandırmacı Öğrenme Kuramının Temel İlkeleri-2", "-Hayat durağan olmayan bir süreçtir ve her şey değişmektedir. Eğitim süreci öğrencilerin her an değişen ilgi ve ihtiyaçlarına göre düzenlenmelidir. Değişen bir gerçekte, mutlak ve değişmez bir doğru söz konusu olamaz.\n-Yapılandırmacı öğrenmede disiplinler arası ve tümleşik bir öğrenme söz konusudur."),
       InfoCardItem("aa_ogrenogretsurec_info_167", "Yapılandırmacı Öğrenme Kuramının Temel İlkeleri-3", "-Her birey diğerinden farklı ve eşsizdir. Bu yüzden öğrenme şekilleri, bakış açıları ve değerlendirme biçimleri de farklı olmaktadır.\n-Öğrenme sadece okulun sınırları içinde kalmamalıdır çünkü yaşamda öğrenilecek çok şey vardır. (Yaşam Boyu Öğrenme)\n-Öğrenme sosyal bir süreçtir (sosyal etkileşim) ve diğer insanlarla kurulan işbirliği sonucu ortaya çıkar."),
       InfoCardItem("aa_ogrenogretsurec_info_168", "Yapılandırmacı Öğrenme Kuramının Temel İlkeleri-4", "-Öğrenmelerin değerlendirilmesinde sonuç değil süreç dikkate alınmalıdır.\n-Yapılandırmacı öğrenme ortamları bireysel bağımsızlığa, iş birliğine, üretkenliğe, etkin katılıma ve araştırıcılığa dayanmalıdır.\n-Öğretim programları öğrencilerin katılımı ve yönlendirmesi ile geliştirilmedir. Eğitim programları ve etkinliklerde esnek yaklaşım söz konusudur."),
    ]),

    InfoCardModel("Yapılandırmacı Öğrenme Yaklaşımının Çeşitleri (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_169", "Yapılandırmacı Öğrenme Yaklaşımının Çeşitleri", "-Bilişsel Yapılandırmacılık\n-Radikal Yapılandırmacılık\n-Sosyal Yapılandırmacılık\n-Kültürel Yapılandırmacılık\n-Eleştirel Yapılandırmacılık"),
       InfoCardItem("aa_ogrenogretsurec_info_170", "Bilişsel Yapılandırmacılık-1 (Jean Piaget)", "Bilişsel yapılandırmacılık bilgi işleme süreciyle ilişkilidir ve biliş süreçleri üzerine temellendirilmiştir.\n-Birey bilgiyi edilgen olarak değil bilişsel yapılarının üstüne etkin bir biçimde işleyerek yapılandırır."),
       InfoCardItem("aa_ogrenogretsurec_info_171", "Bilişsel Yapılandırmacılık-2", "-Bilgi edinme ve anlam oluşturma, bireyin öz eylemidir ve içsel güdülenme bu süreçte çok önemlidir.\n-Eğitim öğretim sürecinde öğretmen öğrenciye rehberlik eden, yeni kaynaklar sunan, bilginin içselleştirilme sürecini kolaylaştıran bir rehber rolündedir.\n-Piaget’e göre öğrenme problem çözme işidir."),
       InfoCardItem("aa_ogrenogretsurec_info_172", "Bilişsel Yapılandırmacılık-3", "Problem çözme ile bilgi elde edinme yolu doğuştan gelmez ve taklit ile öğrenilemez. Birey tarafından zamanla etkin bir şekilde yapılandırılır.\n-Yani düşünce etkinlikten doğar, etkinlik birey tarafından içselleştirilir ve düşünce gelişir."),
       InfoCardItem("aa_ogrenogretsurec_info_173", "Bilişsel Yapılandırmacılık-4", "-Gelişen zihin, dengelenme, özümleme ve uyma süreçlerini tekrarlar.\n-Bu süreç beş temel aşamada açıklanır:\n-Önceki Bilgilerin Harekete Geçirilmesi\n-Yeni Bilginin Kazanılması\n-Bilginin Anlaşılması\n-Bilginin Uygulanması\n-Bilginin Farkında Olunması"),
       InfoCardItem("aa_ogrenogretsurec_info_174", "Bilişsel Yapılandırmacılık Süreçleri-1", "Önceki Bilgilerin Harekete Geçirilmesi: Öğrenmede bilgiler birbiri üzerine inşa edilerek kümülatif olarak oluşur.\n-Yeni Bilginin Kazanılması: Öğrenen tarafından bilginin bütün ve parça ilişkilerinin tam anlamıyla kavranmasıdır.\n-Bilginin Anlaşılması: Özümseme ve uyma süreçleridir. Yeni bilgi eski olan ile çelişiyorsa ya da eksikse yeni bir düzenleme yapılır (uyma). Yeni bilgi eski olan ile uyuşuyorsa bilgi anlaşılmış demektir (özümleme)."),
       InfoCardItem("aa_ogrenogretsurec_info_175", "Bilişsel Yapılandırmacılık Süreçleri-2", "-Bilginin Uygulanması: Öğrenilen bilgi ile problemlerin çözümlenme sürecidir.\n-Bilginin Farkında Olunması: Bireyin öğrendiği bilgi ile problemi çözerken kendisini çözüme ulaştıran işlemlerin ve etkinliklerin bilincinde olmasıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_176", "Sosyal Yapılandırmacılık-1 (Lev Vygotsky)", "Sosyal yapılandırmacılık radikal ve bilişsel yapılandırmacılığın aksine bireyin öğrenme sürecinde sosyal çevrenin ve dilin etkili olduğunu savunur.\n-Öğrenmede bireyin tek başına gerçekleştirdiği sürecin yanı sıra sosyal etkileşim ve dil gelişiminin de önemli olduğunu vurgular."),
       InfoCardItem("aa_ogrenogretsurec_info_177", "Sosyal Yapılandırmacılık-2", "-Birey sosyal çevresi ile etkileşim kurarak tek başına öğrendiğinden daha fazlasını öğrenir.\n-Öğrenme ve gelişim sosyal bir etkinliktir ve diğer bireylerden etkilenir.\n-Öğrenme sürecinde sadece içsel değil dışsal güdüde önem arz eder.\n-Sosyal yapılandırmacılığa göre öğretmenin görevi, öğrencilerin iş birliğine dayalı olarak çalışmalarını ve etkileşimlerini desteklemektir."),
       InfoCardItem("aa_ogrenogretsurec_info_178", "Radikal Yapılandırmacılık-1 (Ernst Von Glaserfeld)", "Bireyler bilgileri kendi yaşantıları ve değerlerine göre yapılandırdığı için mutlak ve gerçek doğrudan söz edilemez.\n-Bilgi mutlak değil, değişken ve fonksiyoneldir.\n-Radikal yapılandırmacılık gerçek ve doğru gibi kavramların değişimler geçirmesi gerektiğini savunur."),
       InfoCardItem("aa_ogrenogretsurec_info_179", "Radikal Yapılandırmacılık-2", "-Birey kendi dünyasında ihtiyaçlarına göre bilgi, kavram, değer ya da anlamlarını oluşturur.\n-Radikal yapılandırmacılığa göre mutlak bilgiden söz edilemediği için var olan bilgi ve gerçek arasında yeni ve daha somut ilişkiler kurmak önemlidir.\n-Radikal yapılandırmacılık sürecinde dil önemli bir yere sahiptir. Bilgi sübjektiftir yani bilene özgüdür."),
       InfoCardItem("aa_ogrenogretsurec_info_180", "Kültürel Yapılandırmacılık", "Kültürel yapılandırmacılığa göre öğrenme süreci dilden, dinden, geleneklerden ve biyolojik özelliklerden etkilenir.\n-Birey bilişsel yapı düzenlemeleri yaparken toplumsal, kültürel ve yaşamındaki simgeleri kullanılır."),
       InfoCardItem("aa_ogrenogretsurec_info_181", "Eleştirel Yapılandırmacılık", "Eleştirel yapılandırmacılık birey tarafından bilgi yapılarının inşa edildiği sürece , sosyal ve kültürel çevreye, eleştirel boyutun eklenmesidir.\n-Eleştirel yapılandırmacılık bireyin bilişsel yapıları inşa etme gerekçelerini sorgulamalarıdır."),
    ]),

    InfoCardModel("Yapılandırmacılığın Eğitim Uygulamaları (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_182", "Yapılandırmacı Öğrenme Faaliyetlerinin Aşamaları", "Yapılandırmacı öğrenme faaliyetlerinin beş aşaması vardır.\n1-Dikkat Çekmek\n2-Keşfetmek\n3-Açıklamak\n4-Bilgiyi Anlamlandırmak (Derinleşme)\n5-Değerlendirme"),
       InfoCardItem("aa_ogrenogretsurec_info_183", "Dikkat Çekmek", "Öğrenme faaliyetlerine başlarken önce öğrencilerin dikkatlerini çekmek gerekir.\n-Soru sormak, bir problemi tanımlamak, ilginç bir olayı anlatmak gibi yöntemler kullanılabilir."),
       InfoCardItem("aa_ogrenogretsurec_info_184", "Keşfetmek", "Öğrenci öğrenme sürecinde aktif olmalı, öğrenme materyalleri ile doğrudan etkileşime girmeli ve sonuca kendisi ulaşıp, keşifleri kendisi yapmalıdır.\n-Bu süreçte öğretmen sadece rehberlik yaparak yönlendirici bir rol üstlenmelidir."),
       InfoCardItem("aa_ogrenogretsurec_info_185", "Açıklamak", "Öğrenme süreçlerinde öğretmen öğrencilerin anlama düzeylerini ve olası yanlış anlamaların olabileceğini göz önünde bulundurarak zaman zaman açıklamalar yapmalıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_186", "Bilgiyi Anlamlandırmak (Derinleşme)", "Öğrenciler öğrendikleri kavramları genişletip diğer kavramlarla ilişkiler kurarlar.\n-Bilgilerini gerçek yaşamda kullanarak onları daha anlamlı hale getirirler."),
       InfoCardItem("aa_ogrenogretsurec_info_187", "Değerlendirme", "Yapılandırmacı kurama göre öğrenme anlık bir olaydan ziyade bir süreçtir.\n-Bu sebeple öğrenmelerin değerlendirmesinde süreç esas alınmalıdır.\n-Sonuca odaklanıp yapılan tek bir sınav uygulamasıyla öğrenme değerlendirilmemelidir.\n-Değerlendirmede, öğrenci merkezli bir anlayış kabul edilmeli ve öğrenciler değerlendirme sürecine mutlaka katılmalıdırlar."),
       InfoCardItem("aa_ogrenogretsurec_info_188", "Yapılandırmacı Eğitim Ortamının Özellikleri-1", "-Öğrenme aktiviteleri temel ve önemli konular etrafında yoğunlaşır.\n-Önceden öğrenilmiş bilgiler çok önemlidir. Bu yüzden öğrencilerin hazırbulunuşluk düzeyleri kontrol edilmeden öğrenme süreci başlatılmaz.\n-Planlama sürecini öğrenciler ve öğretmenler birlikte yürütürler.\n-Ölçme değerlendirme işlemleri sonuca göre değil çok boyutlu olarak süreç dikkate alınarak yapılır.\n-Öğrenme süreci öğrencileri yeni keşiflere götürür."),
       InfoCardItem("aa_ogrenogretsurec_info_189", "Yapılandırmacı Eğitim Ortamının Özellikleri-2", "-Öğrenciler öğrenmeyi öğrenirler ve kendi öğrenme stratejilerini düzenleyebilirler.\n-Bu yaklaşımın doğası gereği zaman zaman belirsizlikler ve karmaşa yaşanabilir.\n-Eğitim ortamı bireyin kendisini özgürce ifade etmesine ve gelişimine olanak sağlamalıdır.\n-Öğrencilerim önceden öğrendiklerinin yeterliliği mutlaka sınanmalıdır.\n-Zengin bir öğrenme ortamı oluşturulmalıdır.\n-Öğrenme sürecinde sosyal etkileşim sağlanmalıdır"),
       InfoCardItem("aa_ogrenogretsurec_info_190", "Yapılandırmacı Öğretmen Tutum ve Davranışları-1", "Yapılandırma öğrenme kuramını benimsemiş öğretmenlerin sergilediği tutum ve davranışlar:\n-Öğrencilerinin öne sürdükleri fikirleri onları eleştirmeden desteklerler.\n-Öğrencilere sordukları sorulara cevap alabilmek için onlara yeterli süre tanırlar."),
       InfoCardItem("aa_ogrenogretsurec_info_191", "Yapılandırmacı Öğretmen Tutum ve Davranışları-2", "-Öğrencilerin doğal meraklarını geliştirmek ve canlı tutmak için öğretim stratejilerinde, yöntem ve tekniklerinde değişiklik yaparlar.\n-Öğrencilerin istekleri dikkate alarak öğrenme sürecinde değişikliğe giderler.\n-Öğrencilerin cevaplarını geliştirerek, konuya ilişkin örnekler vererek ve ilaveler yaparak öğrenmelerin doğru ve kalıcı olmasını sağlarlar."),
       InfoCardItem("aa_ogrenogretsurec_info_192", "Yapılandırmacı Öğretmen Tutum ve Davranışları-3", "-Temel ders kaynakların yanı sıra, öğrenci etkileşimini sağlayan diğer kaynak ve materyalleri de kullanırlar.\n-Öğrencilere ödev verirken üst bilişsel kavramlara (sınıflandırma, analiz, tahmin ve yaratıcılık gibi) yer verirler."),
       InfoCardItem("aa_ogrenogretsurec_info_193", "Yapılandırmacı Öğretmen Tutum ve Davranışları-4", "-Çeşitli kavramları açıklamadan önce, öğrencilerin o kavramlar hakkındaki düşüncelerini ve görüşlerini ortaya çıkarmak için çaba sarf ederler.\n-Öğrencilerin sosyal etkileşime ve iletişime girmelerini özendirirler.\n-Öğrencilerin birbirlerine açık uçlu sorular yöneltmelerini sağlayarak onların araştırma yapmalarını desteklerler."),
    ]),

    InfoCardModel("Beyin Temelli Öğrenme Kuramı (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_194", "Beyin Temelli Öğrenme Kuramı-1", "Beyin üzerine yapılan araştırmalar öğrenme süreçleri ile ilgili farklı boyutlar ortaya koymuştur.\n-Araştırmaların sonuçları, öğrenme süreçleri ile ilgili yeni yaklaşımlar sergilenmesinin gerekliliğini vurgulamaktadır. \n-Beyin temelli öğrenme (Nöro-Fizyolojik) öğrenciyi merkeze alarak öğrenmelerin daha kalıcı ve etkin olmasını savunan bir yaklaşımıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_195", "Beyin Temelli Öğrenme Kuramı-2", "-Beyin temelli öğrenme kuramı insan beyninin yapısına ve işlevine dayanan bir öğrenme kuramıdır. \n-Beyin temelli öğrenme kuramını daha iyi anlamak için öncelikle beynin yapısını ve işleyişini anlamak gerekmektedir"),
       InfoCardItem("aa_ogrenogretsurec_info_196", "Beynin Yapısı-1", "Beyin birçok işlevi eş zamanlı olarak yerine getirebilen karmaşık bir organdır. \n-Sinir sisteminin en önemli kısmı ve merkezi konumundadır. \n-Beyin üç katmanlı beyin zarı ile örtülmüş bir vaziyette kafatası içerisinde yer almaktadır. \n-Renk olarak yer yer gri, pembe ve beyaz olarak görülmektedir. \n-Gri rengin sebebi nöron olarak adlandırılan sinir hücreleridir. \n-Pembemsi beyaz rengin kaynağı ise sinir bağlarıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_197", "Beynin Yapısı-2", "-Yaklaşık 100 milyar beyin hücresinden 10-15 milyarı nöron olarak adlandırılan düşünmeyi, öğrenmeyi sağlayan sinir hücreleridir. \n-Geri kalanlar ise beslenme, temizlik gibi farklı görevleri olan yardımcı hücrelerdir. \n-Beyin vücudun sadece yüzde iki ağırlığını oluşturmasına rağmen çalışması için vücuda giren oksijenin yüzde yirmi beşine ihtiyaç duyar. \n-İnsanlar neredeyse tüm nöron hücrelerine sahip bir şekilde doğmaktadırlar."),
       InfoCardItem("aa_ogrenogretsurec_info_198", "Beynin Yapısı-3", "-Ancak beyin gelişimlerini tamamlamış olarak dünyaya gelmemektedirler. \n-Çünkü bu gelişim için nöronlar arasında bağlantı saplayan dentritler (dallar) ve yeterli düzeyde sinapslar (bağlantı) henüz oluşmamıştır. \n-Beyin iki yarım küreden, dört lob, dış, orta ve alt beyinden oluşmaktadır. \n-Bu bölümlerle ilgili ayrıntılı bilgiler ilerleyen kısımlarda verilmiştir."),
    ]),

       InfoCardModel("Beyne İlişkin Fikir ve Temeller-1 (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_199", "Beyne İlişkin Fikir ve Temeller", "Farklı bilim adamları beynin yapısını ve işleyişini açıklamak için çeşitli çalışmalar yapmışlardır.\n Bu çalışmalar ve bilim insanlarının ileri sürdüğü modeller aşağıda verilmiştir. \n 1-Hücre Topluluğu ve Faz Ardışıklığı\n 2-Beynin Sağ ve Sol Yarım Küreler\n 3-Dört Çeyrek Daireli Beyin Modeli\n 4-Üçlü Beyin Teorisi"),
       InfoCardItem("aa_ogrenogretsurec_info_200", "Hücre Topluluğu ve Faz Ardışıklığı-1", "Donald Hebb öğrenmenin beyinde gerçekleşen fizyolojik bir olay olduğunu savunmuştur. \n-Hebb öğrenme öncesi ve sonrasında beyinde gerçekleşen değişiklikler gözlenirse öğrenmenin nasıl gerçekleştiğinin de açıklanabileceğini iddia etmiştir. \n-Hebb sayesinde öğrenme nörolojik açıdan tanımlanabilmiştir. \n-Hebb’in görüşüne göre bir hücre topluluğu harekete geçtiğinde zihnimizde o hücre topluluğu ile ilgili olay, nesne ya da durumlar canlanmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_201", "Hücre Topluluğu ve Faz Ardışıklığı-2", "-Burada iç ve dış uyaranlar sayesinde nöronların devreye girdiğini dile getirmek istemiştir. \n-Faz ardışıklığı kavramı ile birbiriyle bağlantılı hücre topluluklarını ifade etmektedir. \n-Nöronlar hücre topluluklarından birini harekete geçirdiğinde onla ilişkili olan diğer hücre toplulukları aktif olmaktadır. \n-Bir restoranda duyduğunuz karnıyarık kokusunun size annenizi hatırlatması örnek olarak gösterilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_202", "Beynin Sağ ve Sol Yarım Küreleri-1", "Beynin sol yarımküresinin vücudun sağ tarafını, sağ yarımküresinin ise vücudun sol tarafını yönettiği bilimsel çalışmalar sayesinde biliniyordu. \n-Bu bilgi temel alarak Ornstein beynin birbiri ile uyumlu çalışan iki farklı beyin olduğu düşüncesi üzerine çalışmalar yapmıştır."),
       InfoCardItem("aa_ogrenogretsurec_info_203", "Beynin Sağ ve Sol Yarım Küreleri-2", "-Bu çalışmalar beynin sol yarımküresinin matematik, mantıksal sınama, analitik düşünce, dil ile ilgili fikirlerin işlenmesi, yazma, fikirlerin sınıflandırılması, sayılarla hesaplama yapılması gibi işlevleri yönettiğini ortaya koymuştur. \n-Sağ yarımküre ise görsel şekillerin, imajların (grafik, harita ve çizgiler vb.) ve sezginin kullanılması, müzik, hayal gücü, renk, ritim, belirsizliklerle ilgilenme, rastlantısal ve açık uçlu fikirlerin işlenmesi gibi sözel olmayan işlevleri yönetmektedir."),
       InfoCardItem("aa_ogrenogretsurec_info_204", "Beynin Sağ ve Sol Yarım Küreleri-3", "-İnsanlar fark etmeden beyinlerinin belli bir yarısını daha fazla kullanmaktadır. \n-Bir yarının diğerine oranla daha fazla kullanılması ve baskın olması durumuna beyin başatlığı adı verilmektedir. \n-İnsanların beyinlerinin hangi yarım kürelerini daha baskın kullandıklarını (yarı küresel eğilim) belirlemek amacıyla kağıt-kalem testleri, bilişsel performans testleri ve biyofizyolojik değerlendirmeler gibi farklı yöntemler kullanılmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_204_1", "Beynin Sağ ve Sol Yarım Küreleri-4", "-Yapılan çalışmalar beynin bir yarımküresini daha yoğun kullanan kişilerin kullanmadıkları diğer yarımküre ile ilgili işlerde başarısız olduklarını göstermiştir. \n-Her iki yarımkürenin koordineli olarak kullanılması ile kişilerin genel yeteneklerinde kayda değer artışların ortaya çıktığı görülmüştür."),
    ]),


      InfoCardModel("Beyne İlişkin Fikir ve Temeller-2 (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_205", "Dört Çeyrek Daireli Beyin Modeli-1", "Hermann tarafından geliştirilen bu modelde beyin dört farklı bölgeye ayrılmıştır. \n-Beynin sol üst çeyreğini A, sol alt çeyreğini B, sağ alt çeyreğini C ve sağ üst çeyreğini D harfi ile gösterilmiştir. \n-Bu çeyreklerden beynin üst kısmında olanları (A ve D) kavrama ve zihinsel işlemlere yoğunlaşmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_206", "Dört Çeyrek Daireli Beyin Modeli-2", "-Beynin alt kısmında yer alan diğer iki çeyrek (B ve C) ise daha çok duygusal aktiviteler ve iç organlar ile ilgili işlere yoğunlaşmaktadırlar. \n-Herrmann çalışmaları ile yaklaşık bir milyon kişiye ulaşmış ve bu bireylerin düşünme tercihlerini belirlemiştir."),
       InfoCardItem("aa_ogrenogretsurec_info_207", "A Çeyreği (Teknokrat)", "Teknik, çözümleme ve mantık, eleştirel, analitik, matematiksel\n-A bölgesini baskın kullananların bir otoriteden öğrenmeyi tercih etmektedirler."),
       InfoCardItem("aa_ogrenogretsurec_info_208", "D Çeyreği (Girişimci)", "Bütünsel, sezgisel, yenilikçi, yaratıcı, imgesel\n-D bölgesini baskın kullananlar sezgisel yollarla ve görsel imgelerle öğrenmeyi tercih etmektedirler."),
       InfoCardItem("aa_ogrenogretsurec_info_209", "B Çeyreği (Bürokrat)", "Planlı, ardışık düşünme, organize, ayrıntıcı, kontrollü, örgütlenmiş\n-B bölgesini baskın kullananların deney, uygulama ve pratik yaparak öğrenmeyi tercih etmektedirler."),
       InfoCardItem("aa_ogrenogretsurec_info_210", "C Çeyreği (Hümanist)", "Sosyal, duygusal, müzikle ilgili, ruhani, iletişim kuran\n-C bölgesini baskın kullananların deneyim yaşayarak ve tartışarak öğrenmeyi tercih etmektedirler."),

    ]),

      InfoCardModel("Üçlü Beyin Teorisi (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_211", "Üçlü Beyin Teorisi", "Kafatası içerisinde beynin en dış bölgesinde beynin en yeni yapıları ve beyin kabuğu yer almaktadır. \n-Beyin kabuğu diğer canlılara bulunmasına rağmen en karmaşık hali insanlarda bulunmaktadır. \n-Mclean’a göre daha iç kısımlarda sürüngen ve memelilerinkilere benzer yapılar bulunmaktadır. \n-İlkel Beyin (Sürüngen Beyni) \n-Limbik Sistem \n-Neokorteks (Düşünen Beyin)"),
       InfoCardItem("aa_ogrenogretsurec_info_212", "İlkel Beyin", "İlkel beyin, beden bütünlüğünü sağlayarak hayatı devam ettirmek ile görevlidir. \n-Büyük oranda beynin en içteki parçası olan beyin sapından oluşmaktadır. \n-Mclean’e göre bu bölge insanlardaki en ilkel davranışları kontrol etmektedir. \n-Üst düzey zihinsel çaba gerektirmeyen ilkel beynin kontrol ettiği davranışlar otomatikleşmişlerdir ve değişime karşı direnç gösterirler. \n-Sindirim, dolaşım, solunum, alışkanlıklar bu davranışlara örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_213", "Limbik Sistem", "Limbik sistem beyin sapını çevreleyen kısımdır. \n-Bu sistem hormonlar, kan basıncı, bellek, öğrenme, motivasyon, kişilik özellikleri, açlık, susuzluk, koklama hissi ve kimyasal dengenin temeli ve düzenleyicisidir. \n-Dış ortamda meydana gelen değişikliklere uyum sağlanması Limbik sistem sayesinde olur. \n-Duygulardan davranışlardan, uzun süreli bellekten ve korkudan sorumlu olan sistemin (Amigdala, Hipokampus, Hipotalamus ve Talamus) tümüdür."),
       InfoCardItem("aa_ogrenogretsurec_info_214", "Hipotalamus", "Biyolojik çevre ve fiziksel çevre arasında denge sağlar ve değişikliklere uyum sağlar.\n-Kontrol merkezi olarak vücut sistemlerinin ve işlevlerinin dengeli bir biçimde yürütülmesini sağlar.  \n-Heyecan durumları, vücut ağırlığı ve sıcaklığı, karbonhidrat ve yağ metabolizması kontrolleri hipotalamusta gerçekleşmektedir. "),
       InfoCardItem("aa_ogrenogretsurec_info_215", "Amigdala", "12 -15 ayrı duygu ile ilgili merkezidir ve beynin korkudan sorumlu bölgesidir. \n-Duygusal tepkileri işleme, kaydetme, bağlantı kurma ve çağırma (hatırlamak) görevleri vardır."),
       InfoCardItem("aa_ogrenogretsurec_info_216", "Hipokampus", "Kısa süreli bellekteki bilgileri uzun süreli belleğe aktarmakla görevlidir. \n-Önemli bulduğu yaşantıları hatıra olarak saklamak üzere cerebral kortekse göndermektedir. \n-Limbik sistem incelendiğinde olumlu duygusal izler taşıyan öğrenme durumlarının uzun süreli belleğe kolayca kaydedilebildiği görülmektedir. "),
       InfoCardItem("aa_ogrenogretsurec_info_217", "Talamus", "Talamus beynimize hücum eden uyaranlardan hangisine odaklanılacağını belirler. \n- Bu uyaranları korteksin ilgili alanına transfer eder. \n-Koku alma dışındaki tüm duyusal sinyaller talamustan işlenmektedir. \n-Dışardan gelen uyaranların sınıflandırıldığı (iyi, güzel, çirkin, kötü vb.) yerdir. \n-Bu duyular talamusta değil korkteksin ilgili bölümünde anlamlandırılır."),
       InfoCardItem("aa_ogrenogretsurec_info_218", "Neokorteks (Düşünen Beyin)-1", "Neokorteks beynin alt kısmı da bulunur ve üst düzey zihinsel kapasite gerektiren eylemleri yürütür. \n-Duyusal verilerin işlendiği, bu verilerin bütünleştirilerek bir anlam oluşturduğu, gelecek planlarımızı yaptığımız ve dilin kullanımını (konuşma ve yazma dahil) mümkün kılan kısımdır. \n-Görme, duyma, konuşma, yazma, örüntü oluşturma, kavram yapılandırma, soyut düşünme bu eylemlere örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_219", "Neokorteks (Düşünen Beyin)-2", " Neokorteks toplam dört bölümden oluşur. Bunlar: \n-Ön Lob-Frontal Lob, Şakak Lob-Temporal Lob, Yan Lob-Pariental Lob, Arka Lob-Oksipital Lob"),
       InfoCardItem("aa_ogrenogretsurec_info_220", "Ön Lob-Frontal Lob", "Beynin ön bölgesinde yer alır. \n-Yaratıcılığın, planlamanın, problem çözmenin, karar vermenin ve bilinçli kararların alındığı konuşma merkezidir. \n-Limbik sistemden gelen uyarıları işleyerek sosyal davranışları kontrol eder."),
       InfoCardItem("aa_ogrenogretsurec_info_221", "Şakak Lob-Temporal Lob", "Beynin hem sağ hem de sol bölümünde kulakların üst kısmında yer alır. \n-Dinleme, konuşma, anlam yaratma ve duyma merkezidir. \n-Ayrıca ses, koku ve görüntülerin kaydedildiği bu bölüme kaydedilir. \n-Uzun süreli belleğin bir kısmı temporal lob’da bulunmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_222", "Yan Lob-Pariental Lob", "Beynin arkasına doğru en üst bölgesinde yer alır. \n-Düzenleme, hesaplama, dokunma, dil (tat) ile ilgili olup, duyu ve hareket merkezidir"),
       InfoCardItem("aa_ogrenogretsurec_info_223", "Arka Lob-Oksipital Lob", "Beynin arka bölümünde yer alan görmeyi sağlayan kısımdır. \n-Beyne ulaşan görseller burada analiz edilir. \n-Öğrenmelerin kalıcılığının artması için bilgilerin neokorteksin farklı bölümlerine kaydedilmesi gerekmektedir."),

    ]),

      InfoCardModel("Öğrenmeyi Etkileyen Temel Etmenler (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_224", "Öğrenmeyi Etkileyen Temel Etmenler", "Öğrenmeyi etkileyen temel etmenler şunlardır: \n1-Bellek,\n2-Örüntüleme,\n3-Dikkat,\n4-Çevre,\n5-Duygular"),
       InfoCardItem("aa_ogrenogretsurec_info_225", "Bellek-1", "Bellek bilginin depolanabilme ve geri çağırma ile yeniden kullanılabilme yeteneği olarak tanımlanmaktadır. \n-Nörofizyolojik açıdan ele alındığında bellek nöron demetlerinin ateşlenmesidir. \n-Dinlenirken yüz milisaniyede bir sadece bir defa ateşlenen nöronlar, bir iş ya da düşünce ile meşgul olunurken her yüz milisaniyede defalarca ateşlenmektedir. \n-Bellek bilgiyi işleme ve yorumlamada farklı aşamalar geçirmektedir. \n-Bu aşamalar; duyusal kayıt, kısa süreli bellek (çalışan hafıza) ve uzun süreli bellektir."),
       InfoCardItem("aa_ogrenogretsurec_info_226", "Duyusal Kayıt-1", "Bireyin duyu organlarını kullanarak çevreden gelen uyarıcıların sinir sistemine girmesidir. \n Duyular yoluyla gelen bilgilerin hepsinin saklandığı yerdir. \n Duyusal kayıttaki bilgi uyarıcının birebir bir kopyası şeklindedir. \n Bilgi bu kısımda çok kısa bir süre depolanabilir (0,5 – 4 saniye)."),
       InfoCardItem("aa_ogrenogretsurec_info_227", "Duyusal Kayıt-2", "Duyusal kayıtın kapasitesi sınırsızdır ancak gelen bilgi hızlıca işlenmezse çok kısa bir sürede kaybolur. \n Duyusal kayıta gelen sınırsız uyarıcıdan sadece dikkat edilen sınırlı sayıdaki bilgi, kısa süreli belleğe aktarılır. \n Duygusal kayıt aşaması üç adımda gerçekleşmektedir. Bunlar dikkat, şemalar ve duygu kurallarıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_228", "Duyusal Kayıt Aşamaları", "Dikkat: Kişinin duyu organlarının uyarıcıyı almaya yönlendirilmesidir.\n Şemalar: Bu aşama bir alınan uyarıcılarla anlam yaratma eylemini içerir.\n Duygu Kuralları: Duygu kuralları kişinin nasıl hissetmesi gerektiğinin bir duyusudur"),
       InfoCardItem("aa_ogrenogretsurec_info_229", "Kısa Süreli Bellek", "Kısa süreli belleğin iki temel özelliği varıdır. \n-Bunlar sınırlı miktarda bilginin ancak kısa süreliğine muhafaza edilebilmesidir. \n-Bu bellek türü hızlıca kaybolan bilgileri geçici olarak tutar ya da onları uzun süreli belleğe aktarır. Uzun süreli aktarma için kullanılan yöntemlerden biri bilginin bellekte tutulma süresini uzatan tekrardır. \n-Tekrarsız bilginin uzun süreli belleğe yerleşmesi mümkün değildir."),
       InfoCardItem("aa_ogrenogretsurec_info_230", "Uzun Süreli Bellek", "Uzun süreli bellek iyi öğrenilen bilginin sürekli olarak depolandığı bellektir. \n-Barındırdığı bazı bilgileri ömür boyu depolayabilir.\n-Bir bilgi yeterince uzun bir süre kısa süreli bellekte tutulursa, uzun süreli belleğe aktarılır. \n- Uzun süreli bellek zihinsel tekrar yapıldığı sürece bilgiyi tutar. \n- Uzun süreli bellek sınırlı bir kapasiteye sahipti ve iki bölümden oluşur."),
       InfoCardItem("aa_ogrenogretsurec_info_231", "Anısal ve Anlamsal Bellek", "Anısal Bellek (Episodik Bellek): Bu bölümde kişisel yaşantılar ve hatıralar (otobiyografik) depolanır. \n-Kişiler için önemli olan bilgiler burada saklanır. \n- Zamanla bu bilgilerin hatırlanması zorlaşmaktadır. Anlamsal Bellek (Semantik Bellek): Bu bölümde akademik bilgiler, çevre ile ilgili bilgiler, kurallar, olgular ve kavramlar depolanır."),
       InfoCardItem("aa_ogrenogretsurec_info_232", "Örüntüleme ve Örüntü Tanıma", "Örüntüleme, beynin bilgileri kategoriler halinde düzenleyebilme yeteneğine verilen isimdir. \n-Örüntülemenin yapısı çok karmaşıktır. \n-Örüntülemeyi beynin içinde örümcek ağı şeklinde birbirine bağlı yollar ve patikalar şeklinde betimlemek mümkündür. "),
       InfoCardItem("aa_ogrenogretsurec_info_241", "Örüntüleme ve Örüntü Tanıma", "-Örüntüler içsel ve dışsal uyaranların yorumlanması ile oluşturulur. \n- Daha önce öğrenilen bilgi akılda canlandırılınca o bilgiye ait nöral yollarda yeniden canlanır. \n- Yeni öğrenilen bilgi ile eski bilgiye ait nöral yollar arasında bir bağlantı kurulursa bilgilerin kalıcılığı artar."),
       InfoCardItem("aa_ogrenogretsurec_info_242", "Dikkat", "Öğrenme dikkat süreciyle başlar. \n-Dikkat çevresel bir uyaranın farkına varma ve ona yoğunlaşma olarak tanımlanabilir. \n-Ortamda bulunan tüm uyarıcılara rağmen sadece dikkat edilen ve önemli olan bilgi kodlanır. \n-Dikkat bireyin uyaranlar arasında seçim yapmasını sağlayan kaynaktır. \n-Dikkat değişkendir, seçicidir ve bölünebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_243", "Çevre-1", "Beynin gelişiminde çevrenin önemli bir etkisi vardır. \n-Doğumu takip eden ilk üç yılda beyindeki hücreler arasında çok fazla sayıda bağlantı kurulur. \n-Bu bağlantılar çevreye ve kullanıma bağlı olarak ya kuvvetlenirler ya da kaybedilirler. \n-Beyin hücreleri arasında kurulan bu bağlantılar öğrenmeyi kolaylaştırır ve belleğin güçlenmesini sağlar."),
       InfoCardItem("aa_ogrenogretsurec_info_244", "Çevre-2", "-Öğrenme ortamının rahat ve öğrenmeyi kolaylaştırıcı şekilde düzenlenmesi beyindeki dopamin maddesinde artışa neden olur. \n-Dopamin maddesi belleğin güçlenmesini sağlar. \n-Ayrıca esnek düşünme, problem çözme ve sosyal etkileşim artışına sebep olur. Beynin üzerindeki çevre etkisi yaşam boyu devam eder."),
       InfoCardItem("aa_ogrenogretsurec_info_245", "Çevre-3", "-Yapılan bir çalışma huzurevine yerleştirilen yaşlıların IQ puanlarında altı ay içerisinde ciddi düşüşler meydana geldiğini göstermiştir. \n-Sosyal etkileşimin az olduğu ve eğitim ortamının yetersiz olduğu sınıflarda öğrenim gören öğrenciler içinde benzer sonuçlar olabileceği unutulmamalıdır."),
       InfoCardItem("aa_ogrenogretsurec_info_246", "Duygular-1", "Nörobilim alanında yapılan çalışmalar duyguların öğrenme ve bellekte etkili olduğunu ortaya koymuştur. \n-Olumlu öğrenme ortamı ve koşulları bireylerin beyninde endorfin salgılanmasına neden olur. \n-Endorfin vücutta oluşturduğu rahatlıkla öğrenmenin eğlenceli bir hale dönüşmesine yol açarak nöronlar arasında daha fazla bağ kurulmasına neden olmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_247", "Duygular-2", "-Böylece öğrenme süreci kolaylaşmakta ve bilgilerin kalıcılığı artmaktadır. \n-Stres ve korku dolu bir ortam beyinde olumsuz durumlar yaratmaktadır. \n-Böyle bir ortamda beynine ulaşan veriler neokorteks yerine otomatik hareketlerin meydana geldiği beyin sapına (beyinciğe) gönderilir. \n-Bu durumda kişi üst düzeyde düşünce üretmek yerine, kendi güvenliğini sağlayacak davranışlar sergilemeye yönelir."),
    ]),

      InfoCardModel("Beyin Temelli Öğrenme Süreç ve İlkeleri (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_248", "BTÖ Öğrenmeyi Sağlayan Süreçler", "1-Algılama \n2-Kısa Süreli Belleğe Kaydetme \n3-Kısa Süreli Bellekte Kontrol Edilerek Yorumlanması \n4-Kodlanarak Uzun Süreli Bellekte Saklanması \n5-Farklı Stratejiler Kullanılarak Bilgilerin Geri Getirilmesi"),
       InfoCardItem("aa_ogrenogretsurec_info_249", "Beyin Temelli Öğrenmenin İlkeleri-1", "1-Beyin paralel bir işlemcidir: İnsan beyni aynı anda birçok alandaki farklı işlemi (duygu, düşünce, hayal gücü vb.) yürütebilir. \n2-Öğrenme fizyoloji ile ilgilidir: Öğrenme doğal bir olaydır ve beyin fizyolojisine uygun olarak işler. \n Ancak uyuma, beslenme gibi fizyolojiyi kontrol eden durumlardan da etkilenir. "),
       InfoCardItem("aa_ogrenogretsurec_info_250", "Beyin Temelli Öğrenmenin İlkeleri-2", "3-Anlamı araştırma doğuştandır: İnsanların merak duygusu ve anlam arayışları doğuştan gelir.\n4-Anlamı araştırma örüntüleme yoluyla oluşur: İnsan beyni örüntüler oluştururken, oluşturduğu bu örüntüleri ayırt edip anlamaya çalışır. \n Beyin örüntüleri algılamak ve yaratmak üzerine tasarlanmıştır. \n Anlam içermeyen örüntüleri beyin reddeder.\n Anlamsız örüntüler birey için bir şey ifade etmeyen, ilgilerini çekmeyen unsurlardır."),
       InfoCardItem("aa_ogrenogretsurec_info_251", "Beyin Temelli Öğrenmenin İlkeleri-3", "5-Örüntü oluşturmada duygular önemlidir: Duygular, tutumlar, ilgiler ve bilişsel süreçler birbirinden ayrı düşünülemez. \n Duygular sayesinde yaratılan olumlu duygusal ortam ile öğrenme ve bilgi kalıcılığı arttığı için, duygular çok önemlidir."),
       InfoCardItem("aa_ogrenogretsurec_info_252", "Beyin Temelli Öğrenmenin İlkeleri-4", "6-Beyin parça ve bütünleri eş zamanlı olarak işler: Beynin her iki yarım küresinde farklı işlemler eş zamanlı olarak yürütülür. \n Beynin sol yarım küresinin bütüncül olduğu, sözel, analitik, mantıksal, ayrıntıcı ve matematiksel tarzda çalıştığı saptanmıştır. \n Sağ yarım küresinin ise ayrıntıcı olduğu ve görsel, yaratıcı, hayalci, sezgisel alanlarda etkin olduğu kabul edilir."),
       InfoCardItem("aa_ogrenogretsurec_info_253", "Beyin Temelli Öğrenmenin İlkeleri-5", "Her iki yarım küre birbirinden ayrılamaz derece etkileşim halindedir. \n Bu sebeple her iki yarım küreye hitap eden yöntemlerin öğrenim seviyesinin yükselttiği üzerinde durulmaktadır."),
       InfoCardItem("aa_ogrenogretsurec_info_254", "Beyin Temelli Öğrenmenin İlkeleri-6", "7-Öğrenme hem odaklanmış dikkati hem de çevresel algılamayı içerir: Beyin farkında olduğu ve dikkatini çeken bilginin yanı sıra dikkati dışında kalan uyarıcılara da tepki gösterip o bilgileri de alabilir. \n Yani öğrenme sadece aktif katılımla değil gizil olarak da gerçekleşebilir. \n Bir kişinin hastanenin dikkat çekmeyen duvar renginin farkında olması, gün içinde duyduğu ama dikkat etmediği bir şarkının melodisini söylemesi örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_255", "Beyin Temelli Öğrenmenin İlkeleri-7", "8-Öğrenme bilinçli ve bilinçsiz süreçleri içerir: Bilinç dışı olarak çok şey öğreniriz. \n Çevreden gelen uyarıcıların birçoğu biz farkına varmadan beyne gider ve beyin ile bilinçsiz düzeyde etkileşimde bulunur. \n Bilinçsizce öğrenilmiş olmasına rağmen bu bilgiler karar belirleyici olabilirler."),
       InfoCardItem("aa_ogrenogretsurec_info_256", "Beyin Temelli Öğrenmenin İlkeleri-8", "Uzamsal Bellek Sistemi kodlamaların, örüntülerin ve örgütlemelerin oluşturulduğu yerdir. \n Olgu ve beceriler uzamsal bellekte yer aldığından en iyi şekilde anlaşılır ve hatırlanır. \n Mekanik Bellek (Ezberleyerek Öğrenme Sistemi) ezber öğrenmelerin oluşturulduğu yerdir. \n Tekrar gerektirmez ve yaşanılanların kısa süreli olarak tutulmasını sağlar. \n Dün akşam yemekte ne yediğinizi hatırlamak örnek olarak verilebilir."),
       InfoCardItem("aa_ogrenogretsurec_info_257", "Beyin Temelli Öğrenmenin İlkeleri-9", "10-Öğrenme teşvikle artar ve korkuyla azalır: Beyin korku yaşadığı esnada performansını düşürür ve kısıtlar. \n Teşvik sağlanırsa daha üst düzeyde öğrenir. Bu nedenle özellikle içsel güdülenme önemlidir. \n11-Her beyin tektir: Öğrenme beynin yapısına göre değişir ve her beyin kendine özgüdür. \n Hiçbir beynin diğeri ile aynı olmaması sağladığı çeşitlilik sayesinde her beyni özel kılmaktadır."),

    ]),

      InfoCardModel("BTÖ Uygulamasında Dikkat Edilecek Unsurlar (Yeni)", [
       InfoCardItem("aa_ogrenogretsurec_info_258", "Dikkat Edilecek Unsurlar-1", "Teklik (Biriciklik): Her birey birbirinden farklı ve eşsizdir. Bireysel farklılıklar göz önünde bulundurulmalıdır. \n Değerlendirme: Zamanında dönüt verilmesi, hataları farklı tekniklerle olumlu öğrenme yoluna çevrilmesi önemlidir. \n Duygular: Öğrencilere yönelik olumlu bir dil kullanılmalı, onların duygularını rahatça ifade etmeleri konusunda cesaretlendirilmeleri gerekmektedir."),
       InfoCardItem("aa_ogrenogretsurec_info_259", "Dikkat Edilecek Unsurlar-2", "Anlam: Bütünsel öğrenmeye yer verilmeli, yeterli zaman tanınmalı ve bireysel uygunluk sağlanmalıdır. Çoklu Yol: Sınıf içi uygulamalarda çoklu zeka kuramının kullanımı önemlidir. Beyin-Beden Birlikteliği: Geziler düzenlenmesi, gösteriler yapılması, öğrencilerin hareket etmelerinin ve etkin öğrenmelerinin sağlanması gerekir. "),
       InfoCardItem("aa_ogrenogretsurec_info_260", "Dikkat Edilecek Unsurlar-3", "Bellek: Zengin yaşantılar sağlanmalı ve zihin haritaları kullanılmalıdır. \n Beslenme: Öğrencilerin yararlı besinleri seçmelerinin sağlanması, öğrenme ortamında su bulunması ve beslenme hakkında doğru bilgilere sahip olması gerekir. "),
       InfoCardItem("aa_ogrenogretsurec_info_261", "Dikkat Edilecek Unsurlar-4", "Döngü ve Ritim: Eğitim öğretim sürecinde enerjik etkinliklere yer verilmesidir. \n Korkularla Baş Etme: Öğrenciye yönelik stres yönetimi, içsel motivasyon ve demokratik eğitim ortamının sağlanması gereklidir"),
    ]),
    InfoCardModel("Öğretim Yöntem ve Teknikleri (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_262", "Yöntem ve Teknikler-1", "Öğretim yöntem ve teknikleri aşağıda sıralanmıştır;\n\n-Anlatım Yöntemi – Düz Anlatım\n-Gösterip Yaptırma Yöntemi\n-Soru–Cevap Yöntemi\n-Tartışma Yöntemi \n-Örnek Olay Yöntemi\n-Problem Çözme\n-Beyin Fırtınası Tekniği\n-Altı Şapkalı Düşünme"),
      InfoCardItem("aa_ogrenogretsurec_info_263", "Yöntem ve Teknikler-2", "Öğretim yöntem ve tekniklerin devamı aşağıda sıralanmıştır;\n\n-Gezi – Gözlem \n-Rol Oynama -Dramatizasyon\n-Benzetim (Simülasyon) \n-Mikro Öğretim \n-Bilgisayar Destekli Öğretim \n-Bireyselleştirilmiş Öğretim \n-Ev Ödevi \n-Bireysel Çalışma"),
      InfoCardItem("aa_ogrenogretsurec_info_264", "Anlatım Yöntemi – Düz Anlatım-1", "-Sunuş Yoluyla Öğretim stratejisinde en çok tercih edilen yöntemdir.\n-Öğretmenin ön planda yer alır.\n-Bilgi aktarımı önceliklidir.\n-Kısa, özetleyici ifadelere yer verilmelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_265", "Anlatım Yöntemi – Düz Anlatım-2", "n -Anlatım esnasında örneklerle desteklenmesi fayda sağlar.\n-Öğrenciyi aktif hale getirmek için farklı teknikler ile desteklenmelidir.(Soru-Cevap , Tartışma vb.)\n-Öğretmenin mimikleri , anlatım hızı, telaffuzu yöntemin kullanımında önemlidir."),
      InfoCardItem("aa_ogrenogretsurec_info_266", "Düz Anlatım Artıları", "-Anlatan ve dinleyenlerin genellikle karşılaştığı bir yöntemdir. Kolay algılanır.\n-Kalabalık sınıflarda kullanılması fayda sağlar.\n-Zaman ve ekonomik olarak tasarruflu bir yöntemdir.\n-Kontrol anlatanda olduğu için ortam kontrolünde fazla zorlanma yaşanmaz.\n-Eğitmen sınıfın düzeyine göre anlatımı sadeleştirebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_267", "Düz Anlatım Eksileri", "-Eğitmenin ses tonu anlatımı etkileyebilir.\n-Bazı Öğrencilerin dinleme ile bilgi alış verişi gelişmemiş olabilir.\n-Aktarılan bilgiler ezber yapmayı gerektirdiği için , kalıcılığı zayıftır."),
      InfoCardItem("aa_ogrenogretsurec_info_268", "Gösterip Yaptırma Yöntemi-1", "-Öğretmenin öğrencilere kazandıracağı beceriyi adım adım göstermesi ve aynı şekilde öğrencinin beceriye ulaşana kadar tekrarlayıp kazanması gereken yöntemdir.\n – Psiko-motor beceriler için kullanılan en uygun yöntemdir.\n-Eğitmenin beceriyi aktarma da kusursuz olması beklenir."),
      InfoCardItem("aa_ogrenogretsurec_info_269", "Gösterip Yaptırma Yöntemi-2", "-Bazı durumlarda beceriyi gösterme olayında elektronik ortamlar kullanılabilir.\n-Öğrencinin eksiklikleri , yapmış olduğu hatalar anında kendisine iletilmelidir.\n-Uygulama esnasında öğretimde aşamalılık ilkesi göz önünde bulundurulmalıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_270", "Gösterip Yaptırma Yöntemi Artıları", "-Zihinde kalıcı bir öğrenme sağlar.\n-Etkili bir öğrenme yöntemidir.\n-Birden fazla duyu organına hitap eder.\n-Öğrencinin beceriyi aşamalar halinde eksiksiz yapabildiğini fark etmesi motivasyonu artırır."),
      InfoCardItem("aa_ogrenogretsurec_info_271", "Gösterip Yaptırma Yöntemi Eksileri ", "-Çok zaman alabilir.\n-Hazırlıkların eksiksiz olması gerekir.\n- Öğrencilerin motivasyonunu düşürücü olabilir.\n-Araç gereç yetersizliği\n-Gösterme aşamasında adımların anlaşılmama durumu\n-Kalabalık sınıflarda uygulanması hem maddi hem de motivasyon açısından zor olur."),

    ]),
    InfoCardModel("Soru Cevap-Tartışma Yöntemi (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_272", "Soru–Cevap Yöntemi-1", "-Eğitimin tamamında soru sorulup ardından cevaplanması şeklinde işlenmesi Soru-Cevap yöntemi olarak \n-Anlatım yöntemi ile işlenen derslerde konunun pekişmesi amacıyla soru cevap şeklinde yapılması teknik olarak kabul edilir.\n-Eğitimlerde genellikle teknik olarak karşımıza çıkmaktadır."),
      InfoCardItem("aa_ogrenogretsurec_info_273", "Soru–Cevap Yöntemi-2", "-Derslerin hazır bulunuşluk durumlarını belirlemede kullanılır.\n-Sorulan soruların yanıtları öğrencinin bilgisini kullanmaya yönelik olmalıdır.\n-Soru tüm sınıf ortamına sorulmalıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_274", "Soru–Cevap Yöntemi Artıları", "-Tüm ders öğretim yöntem ve tekniklerinde beraber kullanımında sakınca yoktur.\n-Öğrencilerin soru sormaları o ders için öğrenmeye hazır olduklarını gösterir.\n-Daha kalıcı bir öğrenme sağlar.\n-Ders anında motivasyonu artırır, ders sonunda öğrenme durumlarını tespit etmeyi sağlar.\n-Ders girişlerinde tekrar olanağı sunar."),
      InfoCardItem("aa_ogrenogretsurec_info_275", "Soru–Cevap Yöntemi Eksileri", "-Katılım sağlayan öğrencinin az olması\n-Sorulara doğru yanıt verilme süresinin uzaması\n-uygulama anının tutarsız olması\n-Öğretmenin ön hazırlığının bulunmaması"),
      InfoCardItem("aa_ogrenogretsurec_info_276", "Tartışma Yöntemi-1", "-Öğrenciler aktif olarak katıldıkları bir yöntemdir.\n-Karşısındakilere sözel iletişimde etkili olmaları gerekir.\n-Farklı görüşlerin, bulunan ortamdaki herkese aktarılmasına olanak sağlar.\n-Tartışma konusu ve kullanılacak teknik önceden bilgilendirilmelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_277", "Tartışma Yöntemi-2", " -Tartışma için iyi bir zaman ve planlama gerekmektedir.\n-Öğretmen tartışma ortamını oluşturduğunda izleyici olarak devam eder.\n-Tüm öğrencilerin katılım sağlamasına dikkat eder.\n-Öğrencilerin birbirlerini görebilecekleri ve rahat bir şekilde duyabilecekleri oturma düzeninde yerleştirilmeli ( U veya Çember)"),
      InfoCardItem("aa_ogrenogretsurec_info_278", "Tartışma Yöntemi Artıları", "-Öğrencilerin Hoşgörülü bir kişiliğe bürünmelerini sağlar.\n-Öğrenciler aktif rol alırlar.\n-Öğrenciler olumsuz fikir ve tutumlardan uzaklaşır.\n-Arkadaşlarının düşüncelerinden olumlu çıkarımlar bulabilir.\n-Bulunduğu ortamda saygılı ve düşünceleri hoşgörü ile dinlemeyi sağlar."),
      InfoCardItem("aa_ogrenogretsurec_info_279", "Tartışma Yöntemi Eksileri", "-Duygusal olarak kendilerini baskı altında hissedebilirler. \n-Kalabalık ortamlarda olumsuz durumlar ile karşılaşılabilir.\n-Tartışma konusu dışına çıkılması\n-Birbirlerine kırıcı davranışlar gösterilmesi\n-Katılım olarak belirli öğrencilerin düşüncelerinin baskın olması\n-Uygun ortamın bulunmaması."),
      InfoCardItem("aa_ogrenogretsurec_info_280", "Tartışma Yöntemi Teknikler-1", "-Sempozyum : Akademik olarak bilimsel çalışmaların yapıldığı bir konu hakkında , konu hakkında bilgi sahibi olan izleyicilerin bulunduğu bir ortamda gerçekleşen tartışma tekniğidir.\n-Seminer : Belirlenen bir bilimsel konu hakkında alanında uzman bir kişinin topluluğa anlatım yaptığı tartışma tekniğidir.\n-Panel : Belirlenen alanlarında uzman bir grubunun izleyiciler karşısında güncel olan bir konu hakkında konuştukları tartışma tekniğidir."),
      InfoCardItem("aa_ogrenogretsurec_info_281", "Tartışma Yöntemi Teknikler-2", "-Zıt Panel : Önceden yapılmış bir panel de çözüm bulunamamış bir konu hakkında bu konuyu farklı yönleriyle ele ala iki grubun yaptığı tartışma tekniğidir.\n-Forum : Belirlenen bir konu hakkında uzman bir kişi ve bir moderatör yardımı ile anlatımın yapıldığı , tekniğin sonuna doğru forum dinleyicilerinden soruların alındığı tekniktir.\n-Münazara : Belirlenen bir konunun savunmasını yapmak üzere olumlu ve olumsuz olarak iki zıt grubun oluşturulması ile yapılan tartışma tekniğidir."),
      InfoCardItem("aa_ogrenogretsurec_info_282", "Tartışma Yöntemi Teknikler-3", "-Phillips 66 : Fikir üretme amacı için oluşturulmuş 6 kişiden oluşan 6 dakika içerisinde ortak bir fikri sunan tartışma tekniğidir. \n-Açık Oturum : Bir oturum yöneticisi başkanlığında güncel konuları izleyiciler karşısında , yöneticinin verdiği sıraya göre tartıştıkları tekniktir.\n-Çalıştay : Alanlarında uzman kişilerin bir araya gelerek , belirlenen konu hakkında kısa sürede ortak bir çözüm yolu bulmak için kullandıkları tartışma tekniğidir."),
          ]),
    InfoCardModel("Örnek Olay-Problem Çözme Yöntemi (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_283", "Örnek Olay Yöntemi-1", "-Öğrencilere o anda gerçek ya da hayali bir olayı yaşamaları sağlanır.\n-Gösterilen örnek olay seviyelerine uygun olarak belirlenmelidir.\n-Ortaya atılan durum yazılı veya görsel olarak kendilerine sunulmalıdır.\n-İleriki hayatlarına katkı sağlayacak durumlar göz önüne getirilir."),
      InfoCardItem("aa_ogrenogretsurec_info_284", "Örnek Olay Yöntemi-2", "-Karşılarına çıkacak sorunlara karşı çözüm üretmelerine olanak sağlar\n-Öğretmen ortamı yönetmek ile görevlidir.\n-Öğrenciler o anın içinde yer alırlar.\n"),
      InfoCardItem("aa_ogrenogretsurec_info_285", "Örnek Olay Yöntemi Artıları ", "-Öğrenciler aktif olarak yer alırlar\n-Düşüncelerini ifade etmede becerileri gelişir.\n-olumlu veya Olumsuz bir durum karşısında empati yetenekleri gelişir."),
      InfoCardItem("aa_ogrenogretsurec_info_286", "Örnek Olay Yöntemi Eksileri ", "-Etkili zaman kullanımı sunmaz. \n-Öğrenci sayısının fazla olduğu sınıflarda uygulanması zordur.\n-Öğrencilere verilmesi gereken mesaja uygun örnek olay sunulmamış olabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_287", "Problem Çözme-1", "-Öncelikle Buluş ve Araştırma inceleme yoluyla öğrenme stratejilerinde kullanılan bir yöntem.\n-Bu durum karşısında ya yeni bilgiler elde edilir ya da var olan bilgi ve donanımla problem çözüme ulaşır.\n-Öğrencilere var olan problem ortaya konur.\n-Problem net olarak tanımlanır.\n-Problem çözümünde bilimsel araştırma yöntemlerinden de faydalanılır."),
      InfoCardItem("aa_ogrenogretsurec_info_288", "Problem Çözme-2", "-Birden fazla çözüm yolu ile karşılaşılabilir.\n-Çözüme götüren yollar değerlendirilmeli ve en net bulgulara yer verilmelidir.\n-Problem öğrencilerin bulunduğu sosyo kültürel çevrelerine uygun olarak güncellenmelidir.\n-Her Çözüm üreten öğrenci yanlış yöne doğru gitmesi durumunda bile reddetmeden dinlenmelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_289", "Problem Çözme Artıları", "-Öğrenciler hatalarını görme fırsatı bulurlar.\n-Bir problemin çözüm aşamalarını adım adım inceleme fırsatı bulur.\n-Öğrencilerin çözüme ulaşma ve çözümü ispatlama şansları olduğunu gösterir."),
      InfoCardItem("aa_ogrenogretsurec_info_290", "Problem Çözme Eksileri", "-İstenilen süreden daha fazla sürebilir.\n-Her ders için uygulanma şansı yoktur."),

    ]),
    InfoCardModel("Beyin Fırtınası-Altı Şapkalı Düşünme (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_291", "Beyin Fırtınası Tekniği-1", "-Farklı herkes tarafından söylenmeyen çözümlerin bulunmasını sağlar.\n-Bir den fazla çözüm önerileri karşımıza çıkar.\n-Öğrenciler bu durum içerisinde aktif katılım ve iletişim halinde bulunurlar."),
      InfoCardItem("aa_ogrenogretsurec_info_292", "Beyin Fırtınası Tekniği-2", "-Yanlış , sonuca götürmeyen düşünceler bile öğrenciler tarafından dile getirilebileceği vurgulanır.\n-Kendilerinin bu düşünceleri söylemelerinden dolayı küçük düşme, kınanma gibi durumlarla karşılaşmayacağı vurgulanır."),
      InfoCardItem("aa_ogrenogretsurec_info_293", "Beyin Fırtınası Tekniği Artıları", "-Öğrenciler grupları içerisinde aktif rol oynarlar\n-Herkes tarafından farklı çözümler ve düşünceler ortaya atılır\n-Öğrenciler birlikte hareket etmeyi , birbirlerinden faydalanabileceklerini anlarlar\n-Umulmadık düşünceler ortaya çıkabilir"),
      InfoCardItem("aa_ogrenogretsurec_info_294", "Beyin Fırtınası Tekniği Eksileri", "-Kalabalık sınıf ortamlarında uygulanması zordur.\n-Grup içerisinde aynı kişilerin düşüncelerinin kabulü\n-Diğer öğrencilerin pasif rol oynaması"),
      InfoCardItem("aa_ogrenogretsurec_info_295", "Altı Şapkalı Düşünme-1", "-Edward de Bono ‘nun geliştirmiş olduğu bir tekniktir.\n-Belirlenen bir konuyu 6 farklı yön ile ele alınıp, düşünüp farklı çözümler geliştirmeleri sağlanır.\n-Bu teknikte farklı renkte şapkalar bulunur.\n-Her bir şapkanın anlamı farklıdır.\n-Sonra kartlarda şapkaların anlamlarına göz atalım."),
      InfoCardItem("aa_ogrenogretsurec_info_296", "Altı Şapkalı Düşünme-2", "-Beyaz Şapka : Tarafsız ve açık net bilgileri ifade eder. Açıklamalarında nesnel verileri kullanırlar. Gerçekçidirler. \n-Sarı Şapka : İyimserlik ön plandadır. Ortaya konulan düşüncelerde daima olumlu yönlerini ele alırlar.\n-Yeşil Şapka : Yaratıcılık ön plandadır. Sonuca ulaşmada herkesten farklı fikir ve görüşlerde bulunurlar."),
      InfoCardItem("aa_ogrenogretsurec_info_296", "Altı Şapkalı Düşünme-3", "-Mavi Şapka : Sakin ve Çözüm odaklıdır. Tüm çözümleri irdeler , sakince karar verir ve herkesin bu ortak çözüm ile hareket etmeleri için yönlendirir.\n-Kırmızı Şapka : Duygusal davranışları simgeler. Konuyu ve çözüm durumlarının duyusal yönden etkilerinden bahseder.\n-Siyah Şapka : Sarı Şapkanın aksine Karamsarlık ön plandadır. Konu hakkında çözümlerin olumsuz ve karamsar açıdan değerlendirir."),
      InfoCardItem("aa_ogrenogretsurec_info_297", "Altı Şapkalı Düşünme Artıları", "-Bir konuyu 6 farklı yön ile değerlendirmeyi sağlar -Birbirlerine farklı düşüncelerle ifade etmele"),
      InfoCardItem("aa_ogrenogretsurec_info_298", "Altı Şapkalı Düşünme Eksileri", "-Kalabalık gruplarda uygulanması zordur ve zaman alabilir.\n-Grup içerisinde düşünce aktarımda konu dışına çıkma olabilir.\n-Öğrenci taktığı şapkaya uygun duygu içerisine giremeyebilir."),

    ]),
    InfoCardModel("Gezi Gözlem-Rol Oynama (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_299", "Gezi Gözlem", "-Öğrenciler gözlem yaparak daha kalıcı öğrenmeler gerçekleştirirler.\n-Gezi -Gözlem yapılacak konu da farkındalık arttırmak önemlidir.\n-En önemli aşaması detaylı bir plan oluşturmaktır.\n-Yapılacak olan gezi – gözlem hakkında yeterli bilgilendirme öğrencilere önceden eksiksiz bildirilmelidir"),
      InfoCardItem("aa_ogrenogretsurec_info_300", "Gezi Gözlem Artıları", "-Öğrenciler hayatın içerisinde yer alan konuları görmesi \n-Etkinliğin planlandığı ders ve konuya motivasyon artar.\n-Sınıf ortamında öğrendikleri bilgileri uygulayabildikleri ve gözlemleyebildikleri ortamda "),
      InfoCardItem("aa_ogrenogretsurec_info_301", "Gezi Gözlem Eksileri", "-Ekonomik açıdan maliyetli olabilir. \n-Güvenlik tedbirlerin eksikliği olumsuz durumlar ile karşılaşılabilir.\n-Öğretmen çok fazla sorumluluk yükü altına girer\n-Planlamada amaçlara uygun hareket edilmemesi.\n"),
      InfoCardItem("aa_ogrenogretsurec_info_302", "Rol Oynama -Dramatizasyon-1", "-Öğrencilerin verilen bir durumu kendi dünyalarına yerleştirip canlandırıp oyunlaştırması olayıdır.\n-Öğrencilerin empati kurmaları role daha çok kendilerine kaptırmalarını sağlar.\n-Öğrencilerin ortaya koyması gereken rol için gerekli durum önceden kendilerine aktarılmalıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_303", "Rol Oynama -Dramatizasyon-2", "-Öğrencilerin yaşatacakları rol için uygun roller dağıtılmalıdır.\n-Rolün uygulanacağı ortam durum ile uyumlu olmalıdır."),
      InfoCardItem("aa_ogrenogretsurec_info_304", "Rol Oynama -Dramatizasyon Artıları", "-Öğrencilerin empati yetenekleri gelişir.\n-Öğrencilerin bağımsız olarak bir role sahip olmaları güvenlerini arttırır.\n-Ortaya koydukları durum sayesinde istenilen bilgi kalıcı hale gelir.\n-Kendilerinin sözel anlatım becerileri gelişim gösterir.\n-Kullanıldığı durumlarda öğrencileri motive eder, güdüler"),
      InfoCardItem("aa_ogrenogretsurec_info_305", "Rol Oynama -Dramatizasyon Eksileri", "-Kalabalık sınıf ortamlarında uygulama zorlaşabilir. \n-Sınıftaki tüm öğrencilere görev dağılımı olmayabilir.\n-Aynı öğrencilerin istekli olması diğer öğrencilerin motivasyonunu düşürür."),
      InfoCardItem("aa_ogrenogretsurec_info_306", "Benzetim (Simülasyon)-1", "-Bir eğitim , işe hazırlama, durumdan haberdar olmaları , yaşanabilecek risklerin gösterilmesinde öğrenmeyi desteklemek için kullanılır.\n-Belirlenen konu için bilgisayar ortamında hazırlanmış yazılımlar kullanılabilir.\n-Ayrıca istenilen durum bir oda içerisinde , bir araç şeklinde kullanıcılara sunulabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_307", "Benzetim (Simülasyon) Artıları", "-Öğrenciler durumu direkt olarak yaşadıkları için öğrenme daha kalıcı durumdadır.\n-Durum anında yaşanacakları hissettikleri için güvenlik riskleri en aza indirilir.\n-Bireylerin durum karşısında daha hazır hale getirdiği için hazır bulunuşluk seviyeleri yüksektir.\n-Günlük yaşam becerileri hazırlar ve karşılar."),
      InfoCardItem("aa_ogrenogretsurec_info_308", "Benzetim (Simülasyon) Eksileri", "-Oluşturulan cihaz ve yazılımlardan dolayı maliyetlidir.\n-İstenilen seviyede hazırlanmamış ortamlar eğitimin güvenirliğini düşürür."),

    ]),
    InfoCardModel("Mikro Öğretim -Bilgisayar Destekli Öğretim (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_309", "Mikro Öğretim", "-Eğitim alan bireyin kendisinin karşısındaki kişilere eğitim verdiği, bu eğitim esnasında kayıt altına alınarak, eğitim sonunda olumlu olumsuz dönütlerin gerçekleştiği tekniktir. \n-Bireyin eğitim esnasındaki davranışları tüm sınıf ortamında izlenir ve değerlendirilir.\n-Bu sayede öğrenci kendi eksiklerini de görüp kendini de değerlendirme olanağı verir.\n-Tekniğin uygulanacağı ortam önceden planlanmalı kişi sayısı minimize edilmelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_310", "Mikro Öğretim Artıları","-Kişinin kendi eksikliklerini anında görmesi.\n-Karşısındaki kişilerden uygulama sonrası hemen olumlu veya olumsuz dönütlerin alınması"),
      InfoCardItem("aa_ogrenogretsurec_info_311", "Mikro Öğretim Eksileri", "-Yapılan eleştiriler yıkıcı olabilir.\n-Kendi kayıtlarını izleyen birey motivasyonunu kaybedebilir."),
      InfoCardItem("aa_ogrenogretsurec_info_312", "Bilgisayar Destekli Öğretim-1", "-Günümüzde en çok tercih edilen , kendi kendine öğrenme olayının da gerçekleştiği, Öğrencinin kendinin direk aktif olarak katılım sağladığı yöntemdir.\n-Bilgisayar Destekli Öğrenme de her birey kendi öğrenme seviyesine göre eğitimine devam eder.\n-Yapılan etkinliklerden anında geri bildirim alabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_313", "Bilgisayar Destekli Öğretim-2", "-Öğrenme programı öğrencinin gelişimine göre hazırlanabilir ayrıca tekrardan planlanma olanağı vardır. \n-Öğrenci yönünden çok ilgi çekicidir.\n-Öğretmenin öğrencinin gelişimini takip etme durumu yüksek seviyededir."),
      InfoCardItem("aa_ogrenogretsurec_info_314", "Bilgisayar Destekli Öğretim Artıları", "-Eğitim-Öğretim sürecindeki kalite artar.\n-Dersler ilgi çekici hale gelir.\n-İçerik aktarımı zenginleştirilebilir.\n-Öğrencinin motivasyona ve başarısına olumlu katkılar sağlar.\n-Öğrencinin kendi hızında ilerlemesine izin verir.\n-Aynı anda daha fazla kitleye ulaşılmasını sağlar.\n-Zaman kazandırır.\n-Bireysel öğrenimi destekler"),
      InfoCardItem("aa_ogrenogretsurec_info_315", "Bilgisayar Destekli Öğretim Eksileri", "-Öğrencilerin gerekli yazılım ve donanıma sahip cihazları olmaması.\n-Öğrencilerin ikili ilişkilerine olumsuz etkilemesi.\n-Kullanılacak materyallerin maliyetlerinin pahalı olması.\n-Güncel materyallere ulaşım imkanlarının olmaması."),
          ]),
    InfoCardModel("Bireyselleştirilmiş Öğretim-Ev Ödevi-Bireysel Çalışma (Yeni)", [
      InfoCardItem("aa_ogrenogretsurec_info_316", "Bireyselleştirilmiş Öğretim ", "-Keller tarafından geliştirilmiştir. \n-Verilecek olan eğitim öğretim planları her öğrencinin bireysel farklıları gözetilerek hazırlanması gerektiği savunulur.\n-Ayrıca kullanılacak araç gereç ve materyallerde öğrencilerin düzeylerine uygun olarak seçilmelidir.\n-Öğrenci kendisine uygun bir program içierisinde aktif olarak katılım sağlar."),
      InfoCardItem("aa_ogrenogretsurec_info_317", "Bireyselleştirilmiş Öğretim Artıları", "-Öğrencinin ihtiyaçlarına göre hazırlandığı için öğrenci mutludur.\n-Öğrenci kendi belirlediği hızda eğitimini ilerletir."),
      InfoCardItem("aa_ogrenogretsurec_info_318", "Bireyselleştirilmiş Öğretim Eksileri", "-İkili ilişkilerde ve sosyalleşmede problemler olabilir.\n-Bireysellik ön planda olduğu için maliyetli olabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_319", "Ev Ödevi ", "-Öğrencilerin ders ortamından işlenen konuların tekrarı, ayrıca pekiştirilmesi için okul dışı zamanlarda yapmaları için verilen görevlerdir.\n-Verilen ödevler, okul ortamın da derste verilen düzeyde ve hedeflere uygun olarak verilmelidir.\n-Doğru uygulandığından öğrenciler üzerinde planlı çalışma alışkanlığı gelişir."),
      InfoCardItem("aa_ogrenogretsurec_info_320", "Ev Ödevi ", "-Öğrenmeyi pekiştirir.\n-Öğrenciyi yeni konuya hazırlar.\nAktif örenmeyi sağlar.\n-Öğrenmenin sağlayacağı zihinsel gelişime katkı sağlar.\n-Öğrencinin öğrenme yöntemleri geliştirmesine katkı sağlar."),
      InfoCardItem("aa_ogrenogretsurec_info_321", "Ev Ödevi ", "-Ödevler kontrol edilmediğinde amacı dışına çıkılmış olur.\n-Ödevlerin sayısı öğrencide motivasyon kaybına neden olabilir\n-Ödev için harcanan süre fazla olabilir."),
      InfoCardItem("aa_ogrenogretsurec_info_322", "Bireysel Çalışma", "-Öğrenci sınıf dışında kendisi bireysel çalışmalar ile konuyu öğrenir.\n-Konuyu öğrenirken yaptığı çalışmalar ile araştırma ve inceleme özellikleri gelişir.;\n-Kendisi yaparak yaşayarak öğrenme seviyesine gelir.\n-Kendi öğrenme hızını ayarlayabilir.\n-Eğitmenler bireysel çalışma yapan bireylere kaynaklar açısından yönlendirmeler yapabilir.\n-Eğitmenler bireysel çalışmaların denetimini yapabilmeli ve gelişimini incelemelidir."),
      InfoCardItem("aa_ogrenogretsurec_info_323", "Bireysel Çalışma", "-Kişinin kendi çalışma ortamını oluşturmasını sağlar.\n-Bireysel öğrenme stratejileri geliştirilmesini sağlar.\n-Kendi öğrenme hızını belirlemesine olanak tanır.\n-İlerlemek istediği konularda ilgi düzeyine göre sıralamalar yapmasına olanak tanır.\n-Öğrencinin başarıya ulaşmasında en büyük paya sahiptir."),
      InfoCardItem("aa_ogrenogretsurec_info_324", "Bireysel Çalışma", "-Öğrenci motivasyonu yüksek tutulmaz ise yapılan çalışmalar yüzeysel kalabiliri.\n-Bireysel çalışma süresince sosyal ortam bağları kontrol edilmeli aksi takdir de sosyalleşmesi zayıflayabilir."),
